css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("2ch.hk"), url-prefix('http://2ch.hk/') {

/*ХТМЛ, Боди*/
html, body {
  background-color: white !important;
  background-image: none !important;
  color: #333333 !important;
  font-family: sans-serif !important;
  padding-top: 0 !important;
  font-size: 10pt !important;
}

/*Удоляем банеры и ненужные элементы*/
*.theader, img[style="border: none;"], hr, .doubledash, .logo img, a[href*="liveinternet.ru"], .r36, .rek64, span[style="color: #880080; font-size: 1em; text-align: center;"] { 
  display: none !important;
}

/*Выравниваем по центру*/
table, .postarea, #adminbar, .thread, .logo, div[style="padding:15px 10px;float:right;"], .rmenu[id*="boardNav"], .footer, div[style="font-size:0.9em;"], #posts_form {
  float: none !important;
  margin-left:auto !important;
  margin-right:auto !important;
}

/*Треды и посты*/
.thread {
  width: 880px !important;
  border-top: 16px solid #52769d !important;
  border-left: 1px solid rgba(100, 100, 100, 0.078);
  border-right: 1px solid rgba(100, 100, 100, 0.078);
}

table {
  width: 880px !important;
  border-spacing: 0px !important;
  padding: 0px 15px 0px 30px !important;
}

div[style="font-size:0.9em;"], #posts_form {
  width: 900px !important;
}

#postform {
  background: #F1F1F1 !important;
}

table td {
  border-style: none !important;
}

img {
  border: none !important;
}

.reflink {
  font-size: .8em !important;
  font-weight: normal !important;
}

.filesize {
  padding: 0 !important;
  background:inherit !important;
  border: none  !important;
  text-decoration:none !important;
  color: #666666 !important;
  font-size: .8em !important;
  border-radius: 0 !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
}

.filesize a {
  font-size: 1em !important;
}

.filetitle {
  font-family: sans-serif !important;
  background:inherit;
  font-size:1.2em !important;
  color:#002244 !important;
  font-weight:bold !important;
}

.postername, .postertrip {
  font-family: sans-serif !important;
  color: #2b587a !important;
  font-weight: bold !important;
  font-size: 11px !important;
}

.postertrip:after {
  content: " (питушок)" !important;
}

.omittedposts {
  display: block !important;
  color: #666666 !important;
  font-family: sans-serif !important;
  font-weight: normal !important;
  font-size: 11px !important;
  font-style: normal !important;
}
	
.reply {
  color: #333333 !important;
  font-family: sans-serif !important;
  padding: 0px !important;
  background: white !important;
  border-radius: 0 !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
  border-top: 0 !important;
  border-bottom: solid 1px #dae1e8 !important;
  border-right: 0 !important;
  border-left: 0 !important;
  opacity: 1 !important;
  box-shadow: none !important;
  -moz-box-shadow: none !important;
  -webkit-box-shadow: none !important;
  margin: 1px !important;
}

.reply[id*="reply"] {
  display: inline-table !important;
  width: 100% !important;
}

.reply[id*="preview"] {
  box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
  -moz-box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
  -webkit-box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
}

blockquote {
  background-color: inherit !important;
  color: black !important;
  margin: 0 !important;
  border-radius: 0 !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
}

.reply blockquote {
  background-color: white !important;
  color: black !important;
  margin: 0 !important;
  padding: 5px !important;
  border-radius: 0 !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
}

.replytitle {
  font-family: sans-serif !important;
  font-size: 1.2em !important;
  color:#002244 !important;
  font-weight:bold !important;
}

.highlight {
  width: 860px !important;
  background:#f7f7f7 !important;
  color:#333333 !important;
  border: 2px dashed #2b587a !important;
}

p.spoiler > span.warning {
  color: #FF6600 !important;
  font-weight: bolder !important;
}

span.spoiler {
  background: #BBBBBB !important;
  color: #BBBBBB !important;
}

span.spoiler:hover {
  color: #333333 !important;
}

a {
  font-size: 1em !important;
  color: #2b587a !important;
  text-decoration: none !important;
}

a:hover {
  color: #2b587a !important;
  text-decoration: underline !important;
  text-shadow: none !important;
}

a.highslide img {
  visibility: hidden !important;
  width: 1px !important;
}

a.highslide:before {
  content: url(http://imgur.com/ByaH0.gif) !important;
  visibility: visible !important;
  position: relative !important;
  top: 2px !important;
}

a[href$=sage], a[href$=Sage], a[href$=SAGE] {
  color: #2b587a !important;
  text-shadow: none !important;
}

a[href$=sage]:before, a[href$=Sage]:before, a[href$=SAGE]:before {
  content: "" !important;
}

a:hover[href$=sage], a:hover[href$=Sage], a:hover[href$=SAGE] {
  color: #CC1212 !important;
  text-shadow: none !important;
}

.thumbnailmsg {
  font-size: .6em !important;
  color:#666666 !important;
}

/*Фрейм*/
.menu { 
  border: none !important;
}

dl.menu dt {
  color:#45688e !important;
  font-weight: bold !important;
  border: 1px solid #d3dae0 !important;
  background:#e1e7ed !important;
  border-radius: 0 !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
}

dl.menu dd { 
  font-size: 8pt !important;
  border-left-color: #d3dae0!important;
  border-left-style: solid!important;
}

dl.menu dd a {
  display: block !important;
  padding: 5px!important;
  text-decoration: none !important;
}

dl.menu dd a:hover {
  display: block !important;
  background-color: #e1e7ed !important;
  text-decoration: none !important;
}

h1 {
  display: block !important;
  background:#e1e7ed !important;
  color:#45688e !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
}

/*Формы*/
input, input[type="file"], input[type="submit"], input[type="checkbox"], textarea, select, [name="shampoo"] {
  -moz-appearance: none !important;
  background: none !important;
  background: white !important;
  border: 1px solid #d3dae0 !important;
  color: #333333 !important;
  border-radius: 0  !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
  padding-top: 0 !important;
  font-weight: normal !important;
  box-shadow: none !important;
}

input[type="submit"]:hover, input[type="reset"]:hover, .buttons input[type="button"]:hover { 
  box-shadow: none !important;
}

.form-tbl p { 
  color:#002244 !important;
}

.form-tbl td .but {
  background: none !important;
}

.rules { 
  font-size: 11px !important;
  width: 842px !important;
  background: none !important;
  border: none !important;
  color: black !important;
  opacity: 1 !important;
}

.postarea {
  background-color: #f7f7f7 !important;
  padding: 20px !important;
  margin-top: 0 !important;
  width: 880px !important;
  border-bottom: 1px solid #d3dae0!important;
}

div[style="float: right; margin: 0 0 0.5em 0;"] { 
  text-align: center !important;
  float: none !important;
}

div[style="padding:15px 10px;float:right;"] { 
  width: 900px !important;
  text-align: right !important;
  padding: 0 !important;
}

.postblock {
  background:transparent !important;
  color:#002244 !important;
  font-weight:bold !important;
  border: 0 !important;
  padding: 1px !important;
}

/*Header&Footer*/
.rmenu[id*="boardNav"] {
  display: block !important;
  width: 920px !important;
  margin-top: 0 !important;
  margin-bottom: 0 !important;
  background-image: url(http://imgur.com/QCpUt.jpg) !important;
  background-position: 5px 60% !important;
  background-repeat: no-repeat !important;
  background-color: #52769d !important;
  font-size: 10pt !important;
  font-family: sans-serif !important;
  font-weight: 700 !important;
  padding: 5px !important;
  color: white !important;
  text-align:left !important;
  clear:both !important;
  padding-left: 60px !important;
  border-radius: 0 0 10px 10px !important;
  -moz-border-radius: 0 0 10px 10px !important;
  -webkit-border-radius: 0 0 10px 10px !important;
  text-shadow: none !important;
  border: none !important;
}

#adminbar { 
  margin-bottom: 0 !important;
  width: 920px !important;
  background-color: #52769d !important;
}

#adminbar ul.menu ul li a:hover, .rmenu a:hover { 
  text-shadow: none !important;
}

.footer {
  background:transparent !important;
  font-size: 10px !important;
  font-family: sans-serif !important;
  width: 920px !important;
}

.rmenu a {
  font-weight: normal !important;
  color: white !important;
  text-shadow: none !important;
}

.logo a, #adminbar a {
  text-shadow: none !important;
}

/*Главная (under construction)*/
.main, #mas, .so, .nav, .foot, .box1{
  display: none !important;
}

.txt-box11, .head{
  width: 100% !important;
}

/*Куклота*/
#DESU_panel, #DESU_pform {
  max-width: 945px !important;
}

#DESU_pform{
  margin-left:auto !important;
  margin-right:auto !important;
}

#DESU_select a { 
  font-size: 11px !important;
  padding: 3px !important;
}

#DESU_select a:hover { 
  background: #E1E7ED !important;
  color: #2B587A !important;
  text-decoration: none !important;
}

.de-pview{
  box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
  -moz-box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
  -webkit-box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
}

/*Доброта*/
#newpst_btn {
  display: block !important;
  text-align: center !important;
  margin-bottom: 0 !important;
}

#title { 
  font-size: 11pt !important;
}

#es-sosator{
  width: 500px !important;
}

#es-sosator table{
  width: 450px !important;
}

.reply[id^="es-pop"], #es-sosator{
  box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
  -moz-box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
  -webkit-box-shadow: 0 0 10px rgba(0,0,0,0.5) !important;
}

/*Всякое*/
.abbrev {
  font-size: 8pt !important;
  color:#666666 !important;
}

.logo {
  height: 18px !important;
  font-size: 11pt !important;
  font-weight: normal !important;
  clear:both !important;
  text-align:left !important;
  text-shadow: none !important;
  color: #607387 !important;
  background: #E9EDF1 !important;
  border-style: solid !important;
  border-width: 1px !important;
  border-color: #D9E0E7 !important;
  padding: 3px 10px !important;
  width: 880px !important;
}

.logo2 {
  clear:both !important;
}

.unkfunc {
  background:inherit !important;
  color:#789922 !important;
}
}
