css:
body {
    background-color: #E8E2FF;
    font-family: arial,sans-serif;
    font-size: 12px;
    margin: 0;
    padding: 0 1em;
}
.body {
    -moz-border-radius-bottomleft: 0.9em;
    -moz-border-radius-bottomright: 0.9em;
    background-color: #DFD0FF;
    line-height: 1.5em;
    margin: 0 0 0.3em;
    overflow: hidden;
    padding: 0.9em;
    white-space: normal;
    word-wrap: break-word;
}
.body img {
    border: medium none;
    float: left;
    margin-bottom: 6px;
    margin-right: 7px;
}
.body ul.menu {
    clear: both;
    margin: 0.5em 0 0;
    text-align: right;
}
.body ul.menu li {
    margin: 0 0 0 0.5em;
}
.codebox {
    color: #393939;
    display: block;
    font: 0.9em/1.3em monaco,"andale mono","courier new",courier,mono;
    height: auto;
    margin: 3px 0;
    max-height: 250px;
    overflow: auto;
    padding-top: 5px;
    white-space: normal;
}
span.quote {
    color: #3C6D8A;
}
.highlighted {
    background-color: #B8B4BB;
}
.spoiler {
    background-color: #000000;
}
.spoiler:hover {
    color: #FFFFFF;
}
.border {
    background-color: #D4D2EA;
    border: 3px dashed #A4A0C8;
    margin: 1em;
    padding: 0.5em;
}
.highlight {
    background-color: #FFFF99;
    font-style: normal;
    font-weight: 700;
}
a {
    color: #5000A0;
    text-decoration: none;
}
#notice {
    background-color: #E8E2FF;
    border: 2px solid #ACA6BA;
    padding: 0.5em;
}
h1 {
    display: none;
    font-size: 17px;
    position: relative;
    right: 400px;
    text-align: center;
    top: 1px;
}
h1 a {
    color: #5000A0;
    text-decoration: none;
}
h2 {
    color: #5000A0;
    font-size: 15px;
    left: 375px;
    top: -22px;
    z-index: 2;
}
h2 a:hover {
    text-decoration: underline;
}
h3 {
    -moz-border-radius-topleft: 0.9em;
    -moz-border-radius-topright: 0.9em;
    background-color: #D1C2FB;
    background-image: -moz-linear-gradient(19% 35% 90deg, #BA9DF5, #E8E2FF) !important;
    font-size: 12px;
    font-weight: 400;
    margin: 0;
    padding: 0.5em;
}
.reply_id {
    float: right;
    text-align: right;
    vertical-align: middle;
}
h4.section {
    font-size: 15px;
    margin-bottom: 7px;
}
h4.user {
    font-size: 14px;
    margin: 0 0 0.5em;
}
label.common {
    float: left;
    padding-right: 0.5em;
    text-align: right;
    width: 15em;
}
input, textarea {
    display: block;
}
textarea {
    font-family: monospace;
    width: 100%;
}
input[type="text"]:focus, input[type="password"]:focus, textarea:focus {
    outline: 1px solid #F4F3FE;
}
fieldset {
    margin: 1em 0;
}
ul.body, ul.error_message {
    list-style-type: none;
}
ul.error_message {
    margin: 0;
    padding: 0;
}
ul.error_message li {
    font-style: italic;
    padding: 0.2em;
}
ul.menu {
    font-size: 12px;
    margin: 1em;
    text-align: center;
}
ul.menu li {
    display: inline;
    font-weight: 700;
    margin: 0 1em;
}
ul#main_menu {
    -moz-border-radius: 8px 8px 8px 8px;
    background-color: #BA9DF5;
    background-image: -moz-linear-gradient(19% 75% 90deg, #BA9DF5, #E8E2FF) !important;
    margin: 0 0 1em;
    padding: 1.1em;
}
ul.stuff {
    margin-bottom: 2em;
}
ul.stuff li {
    margin-bottom: 0.5em;
}
table {
    border: 1px solid #ACA6BA;
    border-collapse: collapse;
    margin-bottom: 1em;
    position: relative;
    top: -10px;
    width: 100%;
}
table th {
    background-color: #BA9DF5;
    background-image: -moz-linear-gradient(19% 75% 90deg, #BA9DF5, #E8E2FF) !important;
    color: #5000A0;
    font-size: 13px;
    padding: 0.5em;
    text-align: left;
}
table td {
    background-color: #E1DFF4;
    padding: 0.5em;
    vertical-align: top;
}
table tr.odd td {
    background-color: #DCDAF3;
}
table tr:hover td {
    background-color: #EBE9FE;
}
table .minimal {
    white-space: nowrap;
    width: 1%;
}
.profile_fix {
    overflow: hidden;
    white-space: normal;
    word-wrap: break-word;
}
.reply_body_snippet, .snippet {
    font-style: italic;
}
.topic_headline a:visited {
    font-weight: 400;
}
.topic_info {
    cursor: default;
    float: right;
}
em.marked {
    background-color: #FFFF99;
    font-style: normal;
    font-weight: 700;
}
p.caption {
    font-size: 11px;
    margin-top: 2px;
}
.inline {
    display: inline;
}
#last_seen_marker {
    border-top: 2px solid #A4A0C8;
}
#snapback_link {
    left: 2px;
    position: fixed;
    top: 50%;
}
.video, video, a.video {
    clear: both;
    margin: 10px 0;
}
.standalone, .row {
    margin-bottom: 1em;
}
.body ul.menu li, .body ul.menu li a, .body ul.menu li span, .unimportant, .unimportant * {
    font-size: 12px;
}
a:hover, h2 a {
    text-decoration: underline;
}
a.youtube_alternate, .noscreen {
    display: none;
}
h3#preview, h3#error, h3#replying_to, label, legend, .topic_headline a, .reply_body_snippet a, .snippet a {
    font-weight: 700;
}
.help, .help_cursor {
    cursor: help;
}
