css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.acidcave.net") {
    body {
        background-image: url('http://www.chat.acidcave.net/pink24.jpg') !important;
    }
    .butt {
       -moz-border-radius: 70px !important;
       background-image: url('http://www.chat.acidcave.net/pink24.jpg') !important;
       
       border: none !important;
       color: white !important;
       -moz-box-shadow: 0px 0px 5px #CC77FF;
    }
    .butt:hover {
        -moz-box-shadow: 0px 0px 8px #FF77FF; /* Czerwonawy pÅ‚omyk */
    }
    .userlist {
        border: 1px solid #FF77FF !important;
        -moz-border-radius: 10px;
        background: #DB7093 !important;
        color: white !important;
    }
    .userlist ul a {
        color: black !important;
    }
    .userlist ul li {
        color: #5073BB;
    }
    .userlist .descr {
        color: black !important;
    }
    .chattext {
        background: url('http://www.glitterphoto.net/img/DCglit93.gif') !important;
        border: 0px !important;
        -moz-border-radius: 20px;
        padding: 0px !important;
    }
    .chattext table {
        background: none !important;
        border: none !important;
    }
    .chattext table td {
        background: pink !important;
        border: none !important;
        color: black !important;
        -moz-border-radius: 2px;
        margin: 0px 0px;
    }
    .chattext table td a {
        color: blue !important;
    }
}
