css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

body {
background: #121212 !important;
color: #f1f1f1 !important;
}

* {
font: 10pt "helvetica";
}

a {
color: #83c2d7 !important;
}

hr {
display: block;
border: 1px;
width: 50%;
border-color: #2d2d2d !important;
}

#navtop, #navbot {
background: #262626;
color: #000 !important;
width: 100%;
text-align: center;
height: 12pt;
font-size: 10pt !important;
}

#navtop *, #navbot * {
color: #f1f1f1;
font-size: 10pt;
display: inline;
}

#navtop a:hover, #navbot a:hover {
background: #555;
}

form {
margin: 1em;
}

.inputtext, textarea {
width: 295px;
}

.rules {
display: none;
}

td.postblock {
background: #262626 !important;
border: 1px solid #262626 !important;
color: #aaa !important;
}

td.postblock * {
font-size: 9pt;
}

td.reply, td.replyhl {
background-color:#262626 !important;
border: 2px solid #121212 !important;
color: #f1f1f1 !important;
-moz-border-radius: 7px;
-webkit-border-radius: 7px;
-border-radius: 7px;
}

td.replyhl {
background-color:#262626 !important;
border: 1px solid #6e6e6e !important;
color: #f1f1f1 !important; }

.postername, .filesize, .filesize a, a.linkmail, a.quotejs, .replytitle, .commentpostername, .abbr {
color: #f1f1f1 !important;
font-size: 9pt;}

span.filetitle {
color: #83c2d7 !important;
font-size: 10pt;
font-weight: normal;
}

span.omittedposts {
color: #121212 !important;
font-size: 0pt;
}

span.filetitle, .replytitle {
color: #cc1105 !important;
}

#hd, font[size="1"], font[size="2"], #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo,.logo font b span, .doubledash, img.rotating, input[type="button"], td[align="right"] {
display: none !important;
}

.pages {
background: #262626 !important;
border: 1px solid #2a2a2a !important;
}

.pages td {
color: #444 !important;
font-size: 4pt;
}

.pages td b {
color: #f1f1f1 !important;
font-size: 10pt;
}

.pages td b {
background-color: #353535;
}

.pages input {
display: none !important;
}

.pages td:first-child {
display: none;
}

pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select {
font-size: 9pt ! important;
color: #fff !important;
background: #262626 !important;
border-color: #262626 !important;
}

input[type="submit"] {
background: #262626 !important;
color: #aaa !important;
border: 1px solid #262626;
-moz-appearance: none;
font-size: 7pt;
}

th {
background: #262626 !important;
}

th font {
font-size: 7pt;
}

input[type="file"] {
opacity: 0.12 !important;
}

td.deletebuttons {
font-size: 10pt !important;
color:#f1f1f1 !important;
}
}
