* {margin: 0;padding: 0;}
html, body {height: 100%;margin: 0;padding: 0;}
html, body {background: #171717!important; color: #d3d3d3 !important; text-shadow: 0px 0px 1px #999;}
body * {background:#171717; color: #d3d3d3 !important; text-shadow: 0px 0px 1px #999;}
body div, body span, body h1, body h2, body h3, body table, body tr, body td, body input, body button {
border-color:#262626!important;
background-color:#262626!important;
color: #d3d3d3 !important;
text-shadow: 0px 0px 0px !important;
}
body input, body input *, body button {background: #696969!important; color: #000 !important;}
body img {border: 0;}

body a:link {color:#D6CFC7!important;}      /* unvisited link */
body a:visited {color:#888888!important;}  /* visited link */
body a:hover {color:#efffff!important;}  /* mouse over link */
body a:active {color:#423F3F!important;}  /* selected link */

#lightbox * {background-color:transparent!important;}
