css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('vk24.ordersgame.com'), url-prefix('http://vk24.ordersgame.com') {

/* http://sinmax.ru/blog  */

h2, h3 {margin-bottom:3px !important; margin-top:3px !important;}
#co {margin:1px 10px 2px !important;}
#main_menu {height:30px !important; margin:3px !important;}
#fight_attributes {margin:0 0 !important;}
#result {padding-bottom:0 !important; padding-top:0 !important;}
#fight_result .details {padding-top:0 !important;}
h1.title {font-size:16px !important; margin:0 !important;}
#victim_list .picture, #fight_list .attacker, #fight_list .victim, #alliance_members .picture {height:42px !important;}
#victim_list td {padding:0 !important;}
#special_items {display:none !important;}
#inventory_list td {height:0 !important; padding:0 !important;}
.item_list .picture {height:62px !important;}
.item_list td {padding:0 5px !important;}
#result .payouts .value {line-height:1 !important;}
#result .payouts .payout {padding-bottom:0 !important;}
.profile_pic {height:42px !important;}
#fight_result .item_details {padding-top:0 !important;}
#fight_victim_list {margin-top:-10px !important;}
#inventory {margin-top:-10px !important;}
.profile_inventories .picture {height:77px !important; width:90px !important;}
.profile_inventories .picture img {margin-bottom:0 !important;}
}
