style_setting_options.value:
/* Slimming down the navbar */
#nav-bar-overflow-button, .toolbarbutton-1:not([customizing]){padding-top: 0px !important;padding-bottom: 0px !important;}
#urlbar:not([customizing]){margin-top: 1px !important;margin-bottom: 1px !important;}
#back-button > .toolbarbutton-icon, #forward-button > .toolbarbutton-icon:not([customizing]){padding: 2px 6px !important; border-radius: 0 !important;}
#forward-button > .toolbarbutton-icon:not([customizing]){margin: 1px -4px 1px -4px !important;}
#forward-button[disabled]{display: none !important;}
#urlbar-wrapper:not([customizing]){clip-path: none !important;-moz-margin-start: 0px !important;}
