css:
@namespace url(http://www.w3.org/1999/xhtml);  

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {  

Body{ 
color:#393125 !important; 
font-size:12px !important; 
background-image:none !important }  

a, a:visited{color:#666 !important} 
a:hover{color:#333 !important}  

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag_beta, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies { 

background-image:url('http://i56.tinypic.com/nntoa1.png') !important; 
background-repeat:repeat-x !important; 
background-attachment:fixed !important; 
background-color:#0c081c !important; 
background-position:top }  

#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#main_tag_beta #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { 

background: rgba(12,8,24,1) !important; }  

#dashboard_edit_post{ 
background-color: #0c081c !important }  

#left_column #posts .post .permalink { 
background-color:#04030d !important; }  

#left_column #posts .post .post_avatar, #left_column #posts .post .sub_avatar .sub_avatar_inner { 
background-color: #fbfbfb !important; 
border-bottom-color: #000000 !important; }  

#left_column #posts .post{ 
border-bottom:3px #000000 solid !important }  

#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel { 
background-color:#1d1642 !important; 
background-image:none !important; 
border:0px !important; 
background: rgba(0,0,0,0.3) !important; }  

#nav .nav_item { 
color:#ccc !important; }  

#nav .nav_item:hover { 
color:#fff !important; }  

#nav .nav_item .nav_menu { 
border-bottom:0px !important }  

#nav .nav_item .nav_menu a { 
color:#ccc !important; 
border-top:0px !important; }  

#nav .nav_item .nav_menu a:hover { 
color:#fff !important; 
background: rgba(0,0,0,0.8) !important; }  

#footer{ 
color:#666 !important; 
background: rgba(0,0,0,0.7) !important; 
background-image:none !important; 
text-shadow:none !important }  

.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span { 
color:#ccc !important }  

#right_column .dashboard_nav_item { 
border-bottom:5px solid rgba(39,14,34,0.5) !important; 
background-image:none  !important }                      

#right_column .dashboard_nav_item a,                     
#right_column .dashboard_nav_item ul.dashboard_subpages,                     
#right_column .dashboard_nav_item ul.dashboard_subpages li a {                         
color:#888 !important; }  

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {                         
color:#ccc !important ;}  

#right_column .dashboard_nav_item .dashboard_nav_title{color:#ccc !important}
#right_column .dashboard_nav_item .dashboard_nav_title a{color:#ccc !important}  
#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {                         
background: none !important;                     }  

#show_radar a, #hide_radar a,                         
#hide_recommended_blogs a, #show_recommended_blogs a {
color:#666 !important; background: none !important;                         } 

#dashboard_controls_radar_media{background: none !important;} 
#dashboard_controls_suggested_blogs{background-color: rgba(0,0,0,0.5) !important; border:0px !important} 
#suggested_blog_avatar_1,#suggested_blog_avatar_2,#suggested_blog_avatar_3{border:0px !important} 

.dashboard_controls_suggested_blog{border:0px !important}   

#left_column #posts .notification {                         
background-color:#fbfbfb !important;                         
color:#666 !important;                         
border-bottom-color: #eee !important                     }  

#left_column #posts .notification .block {                             
color:#333 !important;                         }  

#left_column #posts .notification.alt {                         
background-color: #f7f7f7 !important;                     } 

#left_column #posts .notification.last_notification,                     
#left_column #posts .notification.single_notification {                         
border-bottom-color:#2c0d26 !important;                     } 

#left_column #posts .notification a {                         
color:#565656 !important;                         
border-bottom:0px !important;                     } 

#left_column #posts .notification blockquote {                         
border-left-color:#eee !important;                     }  

#left_column #posts .notification .avatar {                         
border-color:transparent !important;                         
background-color:#fbfbfb !important; 
opacity:0.5 !important;                     }  

#left_column #posts .notification .avatar:hover{ 
opacity:1 !important;                     }  

#dashboard_switch_blog_menu{ background-color: rgba(25,7,22,0.9) !important; text-shadow:none !important; border:0px !important } 

#dashboard_switch_blog_menu_header, #dashboard_switch_blog_arrow_and_divider,.dashboard_switch_blog_menu_item,

#dashboard_switch_blog_menu_footer { text-shadow:none !important; border:0px !important }  

.dashboard_switch_blog_menu_item:hover{ 
background-color: rgba(25,7,22,1) !important; } 

#dashboard_switch_blog_menu_footer{ 
background-color: rgba(25,7,22,1) !important; }  

#new_post_notice_container{ 
color:#fff !important; background-image: none !important; } 

#new_post_notice_container a{ color:#fff !important; } 

 #dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current { background-image:none !important } .notification_type_icon{ display:none !important }   

h1,h2,h3,h4,h5,h6{color:#555555 !important}  .sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}  #right_column .dashboard_nav_item ul.dashboard_subpages li a { color:#777 !important; text-shadow: none !important; margin:0px !important; }  #right_column .dashboard_nav_item ul.dashboard_subpages li a:hover{ color:#fff !important; text-shadow: none !important; } }
