@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain(boards.4chan.org), domain(images.4chan.org) {



/*Style Changing*/



/*Background*/

html, body[bgcolor] {

  background-image:url(http://www.meh.ro/original/2010_04/meh.ro4075.jpg) !important;

  background-position:right bottom !important;

  background-repeat:no-repeat !important;

  background-attachment:fixed !important;

  background-color:#FFFFFF !important;

  color:#888888 !important;}



/*Thread Title*/

.filetitle, .replytitle {

  border-width:1px !important;

  border-style:solid !important;

  border-color:#1c1c1c !important;

  background-color:#1c1c1c !important;

  font-style:oblique !important;

  color:#1384d4 !important;}



/*Omited Posts*/

.omittedposts, .abbr {

  color: #888888 !important;}



/* Thread Dividers */

hr {

  opacity:0 !important;}



/*Poster Name & Trip*/

[class*="postername"], [class*="postertrip"] {

  color:#1384D4 !important;}



/*Post Size, Shape & Colors*/

.reply, .replyhl {

  background-color:#1c1c1c !important;

  -moz-border-radius:7px !important;

  border:1px solid #444 !important;

  color:#888888 !important;

  width:450px !important;}



/*Spoiler Text*/

span.spoiler {

  -moz-border-radius:2px !important;

  border:1px solid #283247 !important;}



span.spoiler:hover {

  background:#1384d4 !important;

  color:#000000 !important}



/* Comment and File upload Box.*/

input, .inputtext, input[type="submit"], input[type="button"], input[type="file"] {

  -moz-border-radius-topright:7px !important;

  -moz-border-radius-bottomright:7px !important;

  -moz-appearance:none !important;

  background-color:#000 !important;

  border:1px solid #535353 !important;

  color:#535353 !important;

  margin-right:1px !important;}



div#recaptcha_image {

  opacity:0.4 !important;}



input[type="checkbox"]{

  opacity:0.5 !important;}



.postblock {

  -moz-border-radius-bottomleft:7px !important;

  -moz-border-radius-topleft:7px !important;

  border:1px solid #000 !important;

  background-color: #000000 !important;

  font-size:12px !important;

  color: #535353 !important;}



div.postarea:hover {

  opacity:1 !important;}



div.postarea{

  position:fixed !important;

  width:500px !important;

  height:0px !important;

  opacity:0 !important;

  right:-35px !important;

  bottom:400px !important;}



/*Delete Post*/

.deletebuttons {

  position:fixed !important;

  right:30px !important;

  bottom:40px !important;

  opacity:0.1 !important;}



.deletebuttons:hover {

  opacity:1 !important;}



/* Top Navigation Bar */

#navtop {

  position:fixed !important;

  left: 90px !important;

  background-color: #3D3D3D !important;

  opacity:0.2 !important;

  color: #808080 !important;}



#navtop:hover {

  opacity:1 !important;}



/* LOGO */

.logo > FONT:first-of-type > B > SPAN {

  position: absolute !important;

  right: 15px !important;

  top: 30px !important;

  font: 38px Tekton Pro !important;

  text-transform: lowercase !important;

  color: transparent !important; 

  text-shadow: 0px 0px 1px #EEE, 0px 0px 1px #01609F, 1px 1px 5px #A66 !important;}



.logo > FONT:nth-of-type(2) {

  position: absolute !important;

  right: 15px !important;

  top: 70px !important;

  text-align:right !important;

  width: 400px !important;

  color: #666 !important; 

  text-shadow: 0px 0px 1px #01609F !important;}



/*Sets the color of links*/

a {

  color:#1384d4 !important;}



a:visited {

  opacity:0.5 !important;}



/*Page Selection*/

table.pages {

  color: #808080 !important;

  border-style: solid !important;

  border-color: #444 !important;

  background-color:#1C1C1C !important;

  opacity:0.3 !important;}



.pages td {

  color: #808080 !important;}



table.pages:hover {

  opacity:1 !important;}



table, td {

  border:none !important;}



/*home link on top */

#navtopr {

  background-color: #3D3D3D !important;

  position:fixed !important;

  opacity:0.2 !important;}

#navtopr:hover {

  opacity:1 !important;}





/*Removed Things*/



/*Removes Copyright at the bottom*/

#footer {

  display:none !important}




/*Removes "[Return]"*/

SPAN[style="left: 5px; position: absolute;"] {

  display:none !important;}



/*Removes "Rules" under the upload/comment box*/

.rules {

  display:none !important;}



/*Removes 4Chan Style links (Yotsuba, Yotsuba B, etc)*/

TD[align="right"] {

  display:none !important;}



/*image ads*/

img[src*="http://static.4chan.org/support/"], 

img[src*="http://affiliates.jlist.com/click/583?url=http://www.jlist.com/index.html"], img[style="border:1px solid black;"], .bf {

  display:none !important;}



.logo img {

  opacity:0 !important;} /*Making space for the 4chan's thread Watcher*/



/*Captcha*/

img#recaptcha_logo, img#recaptcha_whatsthis, img#recaptcha_tagline {

  display:none !important;}



img#recaptcha_switch_audio, img#recaptcha_reload {

  opacity:0.5 !important;}

}



