html, body[bgcolor] {

  background-image:url(http://img833.imageshack.us/img833/2049/1273492236297.jpg) !important;

  background-position:right bottom !important;

  background-repeat:no-repeat !important;

  background-attachment:fixed !important;

  background-color:#050B13 !important;

  color: #888888 !important;}



#footer {

  display:none !important}



img[src*="http://static.4chan.org/support/"], 

img[src*="http://affiliates.jlist.com/click/583?url=http://www.jlist.com/index.html"], img[style="border:1px solid black;"], .bf {

  display:none !important;}



hr {

  opacity:0.04  !important;}



#navtop {

  position:fixed !important;

  left: 90px !important;

  background-color: #3D3D3D !important;

  color: #808080 !important;}

#navtop:hover {

  opacity:1 !important;}



a {

  color:#1384d4 !important;}



.logo > FONT:first-of-type > B > SPAN {

  font: 38px Tekton Pro !important;

  text-transform: lowercase !important;

  color: transparent !important; 

  text-shadow: 0px 0px 1px #EEE, 0px 0px 1px #01609F, 1px 1px 5px #A66 !important;}



.logo > FONT:nth-of-type(2) {

  color: #666 !important; 

  text-shadow: 0px 0px 1px #01609F !important;}



}
