css:
@-moz-document domain(twitter.com){
span.tweet-actions
{
display:block;
width:100%;
text-align:right;
margin:-1em 0 0em;
}

.dashboard { min-width:200px !important; }

#page-outer, #front-container, #page-container
{
min-width:800px !important;
}

ul.recommended-followers.user-rec-component { display:none; }
div.user-rec-links { display:none; }
div.trends-inner { display:none; }
div.user-rec-inner { display:none; }
div.user-rec-inner+hr { display:none; }
div.definition { display:none; }
div.definition+hr { display:none; }

div.your-activity { float:none; width:100%; }
div.latest-favorite { width:auto; }

.tweet-content {min-height:6em; }
div.latest-tweet .tweet-row { height:6em; }

.main-content { width:550px !important; }

div.text-area-editor.twttr-editor>textarea{
  width:490px !important;
  padding:4px;
}

.tweet-content{min-height:3em;}
.tweet-full-name{display:none;}



.twttr-dialog.draggable.ui-draggable textarea{
  width:460px !important;
}


.messages-main-content .messages-container .details-pane {
 left:400px;
 min-width:200px;
}
.messages-main-content.main-content {
 width:400px !important;
}


.tweet-meta{
 display:block;
 color:#070;
}
.tweet-corner{display:block;}
.tweet-corner{
 display:block;
 margin:-20px 1em 0 0;
 text-align:right;
 height:20px;
}
.tweet-corner span.icons{
 display:inline-block;
 padding-top:4px;
}

}/*end of domain*/
