@namespace url(http://www.w3.org/1999/xhtml);
/** VERSION 0.3 */
@-moz-document domain("www.sosyal-fobi.net") {
body {
background:url("https://s3.amazonaws.com/twitter_production/a/1292975674/images/bg-clouds.png") repeat-x scroll 0 0 #C0DEED !important;
}

div#top {
padding:0px !important;
background-image:none !important;
border-top:solid 3px #f0f0ff !important;
}
div#login {
background:#00A0D1 !important;
border-width:0px !important;
}
ul.login {
background:#00A0D1 !important;
border:solid #f0f0ff !important;
top:-5px;
border-width:0px 3px 3px 3px !important;
-moz-border-radius-bottomleft:10px !important;
-moz-border-radius-bottomright:10px !important;
}
div#login,ul.login li a {
color:#f0f0ff !important;
}
ul.login li {
background-image:none !important;
}
div.inside,div.outside {
background-image:none !important;
background-color:#fff !important;
-moz-border-radius-topleft:10px !important;
}
div.bgtop, td, th, input {
background-image:none !important;
}
/** Silinen Nesneler */
img[src*='site_logo.png'],img[src*="icon_topic_newest.gif"] {
display:none !important;
}
/** input ogeleri */
input[type="text"],
input[type="password"],
input[type="submit"] {
border-width:0px !important;
background-color:#f0f0ff !important;
}
select {
border-width:0px !important;
background-color:#fff !important;

}
input[type="text"]:hover,
input[type="password"]:hover {
background-color:#ffffff !important;
}
/** Main tables */
table.tablebg, table.tablebg td, th {
border-collapse:collapse;
background-color:transparent !important;
border-width:0px !important;
}
td[bgcolor*="#"] {
background-color:inherit !important;
}
table.tablebg tr {
border-top:solid 1px #eef !important;
}
#pagecontent table.tablebg tr {
background-color: #fff !important;
}
table.tablebg tr:hover {
background-color:#E5F2F7 !important;
cursor:inherit !important;
}
td.menu2 a,td.menu2 a strong {
color:#0084B4 !important;
font-weight:400 !important;
}
tr.row1 {
background-image:none !important;
}
table.tablebg th,td.spacer {display:none !important;}
/** Links */
a.topictitle, a.forumlink {
font-weight:500 !important;
font-size:17px !important;
}
a.topictitle,a.forumlink, p,table.tablebg tr a{
color:#000 !important;
}
p.gensmall {
color:#999 !important;
}
a.topictitle:hover,table.tablebg tr a:hover{
color:#0084B4 !important;
}
a[href*="#unread"]:before {
content:"Â»" !important;
font-size:18px;}
a[href*="#unread"]{
text-decoration:none !important;
}
a.kat{
font-weight:400 !important;
}

/** Buttons (L) */
/*url("../images/fronts/bg-btn-search.png") repeat-x scroll 0 0 #BBBBBB*/
a[href*="posting.php?mode="] img {
display:none;
}
a[href*="posting.php?mode=post"],
a[href*="posting.php?mode=reply"] {
color:#fff !important;
font-weight:bold !important;
padding:7px 10px 7px 10px !important;
background: #3399DD url(http://twitter.com/images/bg-btn-blue.png) repeat-x 0 0;
text-align:center !important;
text-decoration:none !important;
-moz-border-radius:5px;
border:1px solid #3399DD !important;
}
a[href*="posting.php?mode="]:hover {
background-image:none !important;
}
a[href*="posting.php?mode=post"]:before {content:"Yeni Konu" !important;}
a[href*="posting.php?mode=reply"]:before {content:"YanÄ±tla" !important;}

/** Buttons (S) */
a img[src*="icon_post_report"] {
display:none !important;
}
a[href*="report.php"],
a[href*="posting.php?mode=delete"],
a[href*="posting.php?mode=edit"],
a[href*="posting.php?mode=edit"],
a[href*="posting.php?mode=quote"] {
text-decoration:none !important;
color:#2277BB !important;
padding:3px !important;
border:solid 1px transparent;
}
a[href*="report.php"]:hover,
a[href*="posting.php?mode=delete"]:hover,
a[href*="posting.php?mode=edit"]:hover,
a[href*="posting.php?mode=edit"]:hover,
a[href*="posting.php?mode=quote"]:hover {
text-decoration:none !important;
border:solid 1px #2277BB;
-moz-border-radius:5px !important;

}
a[href*="report.php"]:before {content:"Bildir" !important;}
a[href*="posting.php?mode=delete"]:before {content:"Sil" !important;}
