a[href*="posting.php?mode=quote"]:before {content:"AlÄ±ntÄ±" !important;}
td.cat {padding-top:10px !important;}
}

/** Button Widgets */
a[href*="&bookmark=1"],
a[href*="&watch=topic"],
a[href*="&view=print"],
a[href*="?mode=email"],
a.kat[href~="#unread"] {
font-size:0px !important;
padding-top:12px !important;
padding-left:12px !important;
background-repeat: no-repeat !important;
background-image:url(http://a2.twimg.com/a/1294279085/phoenix/img/sprite-icons.png) !important;
}
a.kat[href~="#unread"]:before {background-image:none !important;font-size:0px !important;}
a[href*="&bookmark=1"] {background-position: -63px -1px !important;}
a[href*="&watch=topic"] {background-position: -61px -16px !important;}
a[href*="&view=print"] {background-position: -15px -80px !important;}
a[href*="mode=email"] {background-position: -47px -32px !important;}
a.kat[href~="#unread"] {background-position: -155px -80px !important;}
/** Button Widgets -- Hover */

a[href*="&bookmark=1"]:hover,
a[href*="&watch=topic"]:hover,
a[href*="&view=print"]:hover,
a[href*="?mode=email"]:hover {font-size:8px !important;background-image:none !important;}
a.kat[href~="#unread"]:hover {background-position: -235px -64px !important;}
