css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bloglines.com") {
/* body */
body {
background-color: #626A75!important;
}
a:link {
color: #92CF54 !important;
text-decoration: none !important;
}
a:visited {
color: #7CB93E !important;
background-color: #000 !important;
text-decoration: none !important;
}
span .title {
background-color: #000 !important;
}

/* header */

 /* sidebar */
.header-list {
display: none !important;
}

 /* main window */
#logo {
display: none !important;
}
.header-content {
background: transparent !important;
}
.logbar {
color: #fff !important;
}
.logbar img {
display: none !important;
}

/* tabs */
.tabs {
  background: transparent !important;
  float:left;
  width:100%;
  font-size: 1.0em;
  line-height:normal;
  margin-bottom:10px;
}
.tabs ul {
  margin:0;
  padding:3px 5px 0;
  list-style:none;
  }
.tabs li {
  float:left;
  margin: 0px;
  background: transparent !important;
  padding:0px 0px 0px 6px;
  border-bottom:1px solid #666;
}
.tabs a {
  float:left;
  background: transparent !important;
  display:block;
  width:.1em;
  padding:5px 6px 4px 2px;
  text-decoration:none !important;
  font-weight:bold;
  color:#666;
}

/* content */

#message {
background-color: #C6CED9 !important;
}

.blog-header {
background-color: #89919C !important;
}

.channel_nav {
background-color: #89919C !important;
}

.channel_nav a {
background: transparent !important;
}

.channel {
background-color: #89919C !important;
border-top: 1px dotted #fff !important;
}

.header_nav ul {
background-color: #89919C !important;
border-top: 1px dotted #fff !important;
}

td.article {
padding-left: 1.5em !important;
padding-right: 1.5em !important;
}

td.article a {
color: #368B03 !important;
padding: 3px;
}

td.article a:visited {
color: #92CF54 !important;
}

/* article titles */

h3 a:link {
color: black !important;
}
h3 a:hover {
color: #92CF54 !important;
}

/* subscription page */

.bl_account { /* main subscription options */
background-color: #FBFBFB!important;
padding: 1.5em !important;
}

.bl_account a:link { /* subscription links */
color: #437B0F !important;
}

.bl_account h2 { /* subscription headers */
color: #000 !important;
border-bottom: 1px dotted #9E9E9E!important;
}

.bl_account h3 label { /* feed name */
color: #000 !important;
font-weight: bold !important;
}

/* edit view */

.actionbar,div#bl_instructions { /* options box (top and bottom) and instructions (bottom) */
background-color: #F5F5F5!important;
}

.actionbar a { /* New Folder and Trash links */
color: black !important;
}

li.sub,li.folder,h1.treeRoot { /* folders,feeds,feeds header */
border: none !important;
}

/* options */

div.account td { /* options box,options rows */
background-color: #F5F5F5 !important;
padding: 1.2em !important;
}

div.account th { /* options header */
background-color: #E0E0E0!important;
padding-top: 0 !important;
padding-left: 1em !important;
}

div.account { /* options box */
margin-top: 1.2em !important;
}

#main_window a:visited { /* visited link highlight for feeds/folders */
background: transparent !important;
}

.treeView li.drop, .treeView h1.drop { /* drag/drop highlight */
  background-color: #000 !important;
  border-color: #92CF54 !important;
}

.treeView li.selected, .treeView h1.selected { /* selected feed(s)/folder(s) */
  background-color: #454D58 !important;
}

/* search results */

#topFeeds,#topNews,#rssLinks,
#topFeeds a:link,#topNews a:link{ /* Matching Feeds/News Boxes, Subscribe to search box */
background-color: #454D58 !important;
}

#other-matches h2 { /* other matches headers */
color: black !important;
}

#contain #orientation { /* Search Info */
background-color: #FEFEFE !important;
}

#rssLinks { /* search subscription options */
padding-bottom: .5em !important;
}

div.match { /* search results box */
background-color: #FEFEFE !important;
padding: .3em !important;
}

div.match h3 { /* search result title */
background-color: #EAEAEA!important;
padding: .2em !important;
}

div.match h3 a { /* search results title link text */
font-weight: bold !important;
}

div.match h3 a b,div.summary b { /* search results title link text matching search terms */
color: #FF0080!important;
}

#page-nav a { /* page navigation links */
background-color: #454D58 !important;
border: none !important;
padding: .2em !important;
}

#page-nav a:hover { /* page navigation links hover state */
color: #000 !important;
background-color: #FEFEFE !important;
}

#page-nav .current { /* current page */
color: #FFF !important;
}

}
