css:
body {
    background-color:#f6f1ff;
    color:#1e1e1e;
    font-family:arial, sans-serif;
    font-size:12px;
    padding-top:0;
    padding:0 1em;
    margin:0px;
    }
    .body {
        background-color:#decefe;
        line-height:1.5em;
        margin-bottom:1em;
        margin-top:0em;
        overflow:hidden;
        padding:.5em;
        white-space:normal;
        word-wrap:break-word;
    	box-shadow:0px 1px 3px rgba(0,0,0,0.35);
	-webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
	border-bottom-left-radius:8px;
	border-bottom-right-radius:8px;
    }
    .body img {
        border:none;
        float:left;
        margin-bottom:6px;
        margin-right:7px;
    }
    .body ul.menu {
        clear:both;
        margin:.5em 0 0;
        text-align:right;
    }
        .body ul.menu li {
            margin:0 0 0 .5em;
        }

.codebox {
        overflow: auto;
        display: block;
        height: auto;
        max-height: 250px;
        white-space: normal;
        padding-top: 5px;
        font: 0.9em monaco, "andale mono","courier new", courier, mono;
        line-height: 1.3em;
        color: #393939;
        margin: 3px 0;
}

a[href^="http://minichan.org/new_reply"] {text-align:center !important;}
a[href^="http://minichan.org/report_"] {color:#db0000 !important;}
div[class^="bar"] {display:none !important;}
th[class^="minimal percentage"] {padding-left:2px !important; padding-right:4px !important;}
th[class^="minimal graph"] {color:transparent;font-size:1px; text-align:right !important;}

span.quote {
    color:#9287ff;
}

.highlighted {
    background-color:#b8b4bb;
}

.spoiler {
    background-color:#000;
}
    .spoiler:hover {
        color:#fff;
    }

.border {
    background-color:#ead6ff;
    border:1px solid #897de6;
    margin:1em;
    padding:.5em;
    border-radius:8px;
    box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
}

.highlight {
    background-color:#ffff99;
    font-style:normal;
    font-weight:700;
}

a {
    color:#000;
}

#notice {
    background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#c5b8ff), to(#d0c5fe)) !important;
    background-image:-moz-linear-gradient(19% 75% 90deg,#c5b8ff, #d0c5fe);
    box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    padding:.5em;
    margin-top:1em;
    margin-bottom:0.8em;
    border-radius:8px;
    -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
}

h1 {
    font-size:17px;
    text-align:center;
}
    h1 a {
        color:#1e1e1e;
        text-decoration:none;
	display:none;
    }

h2 {
    font-size:16px;
	padding-top:5px;
	color:#333333;
}
    h2 a:hover {
        text-decoration:underline;
    }

h3 {
    background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#ae9aff), to(#c5b8ff)) !important;
    background-image:-moz-linear-gradient(19% 75% 90deg,#ae9aff, #c5b8ff);
    -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    -moz-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    font-size:12px;
    color:#1e1e1e;
    font-weight:400;
    margin:0;
    padding:.5em;
    -webkit-border-top-left-radius:8px;
    -webkit-border-top-right-radius:8px;
    border-top-left-radius:8px;
    border-top-right-radius:8px;
}

h3 a[title^="Gold member"] {
    color:transparent !important; 
    background: no-repeat url(http://media.ef.com/_imgs/shared/flags/flag_UK.gif);
}


.reply_id {
    float:right;
    text-align:right;
    vertical-align:middle;
}

h4.section {
    font-size:15px;
    margin-bottom:7px;
}

h4.user {
    font-size:14px;
    margin:0 0 .5em;
}

label.common {
    float:left;
    padding-right:.5em;
    text-align:right;
    width:15em;
}

input,textarea {
    display:block;
    -webkit-border-radius:0px;
}

textarea {
    font-family:monospace;
}

fieldset {
    margin:1em 0;
}

ul.body,ul.error_message {
    list-style-type:none;
}

ul.error_message {
    margin:0;
    padding:0;
}
    ul.error_message li {
        font-style:italic;
        padding:.2em;
    }

@-moz-document domain(minichan.org)  
#logo { 
	display:none!important;
}

@-moz-document domain(minichan.org)  
body {
    padding:3.2em 1em!important;
}

ul.menu {
    margin:1em;
    text-align:center;
}
    ul.menu li {
        display:inline;
	font-weight:700;
        margin:0 1em;
    }
	
ul#main_menu {
    background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#ae9aff), to(#c5b8ff)) !important;
    background-image:-moz-linear-gradient(19% 75% 90deg,#ae9aff, #c5b8ff) !important;
    -webkit-border-bottom-left-radius:8px;
    -webkit-border-bottom-right-radius:8px;
    border-bottom-left-radius:8px;
    border-bottom-right-radius:8px;
    -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    -moz-border-radius:8px;
    margin-top:-15px;
    padding-top:1.2em;
    padding-left:0.3em;
    padding-right:0.3em;
    padding-bottom:11px;
    margin-left:.1em;
    margin-right:.1em;
}

    ul#main_menu a {
	    font-weight:bolder;
	    margin-left:-8px;
	    margin-right:-8px;
	    font-size:14px;
	    color:#333333;
	    text-shadow: 0 1px rgba(255,255,255,0.4);
    background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#ae9aff), to(#c5b8ff)) !important;
    background-image:-moz-linear-gradient(19% 75% 90deg,#ae9aff, #c5b8ff) !important;
	    text-decoration:none;
	    padding-top:1.2em;
	    padding-left:1em;
	    padding-right:1em;
	    padding-bottom:0.8em;
    }
    ul#main_menu a:hover {
	    font-weight:bolder;
	    font-weight:bolder;
color:#fff;
text-shadow: #111 1px 1px 1px;
	        background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#6c6c6c), to(#909090)) !important;
    background-image:-moz-linear-gradient(19% 75% 90deg,#6c6c6c, #909090) !important;
	    text-decoration:none;
	    padding-top:1.2em;
	    padding-left:1em;
	    padding-right:1em;
	    padding-bottom:0.8em;
    }
    ul#main_menu a:active {
	    font-weight:bolder;
    background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#464646), to(#323232)) !important;
    background-image:-moz-linear-gradient(19% 75% 90deg,#464646, #323232) !important;
	    text-decoration:none;
	    padding-top:1.2em;
	    padding-left:1em;
	    padding-right:1em;
	    padding-bottom:0.8em;
    }

#main_menu li.replies {display:none!important;}
#main_menu li.hot_topics {display:none!important;}
#main_menu li.events {display:none!important;}

ul.stuff {
    margin-bottom:2em;
}
    ul.stuff li {
        margin-bottom:.5em;
    }

table {
    background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#ae9aff), to(#c5b8ff)) !important;
    background-image:-moz-linear-gradient(19% 75% 90deg,#ae9aff, #c5b8ff) !important;
    -webkit-border-radius:8px;
    border-radius:8px;
    -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    -moz-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    -moz-border-radius:8px;
    border-collapse:collapse;
    margin-bottom:1em;
    margin-left:0em;
    color:#1e1e1e;
    margin-right:0em;
    width:100%;
}
    table a {
	color:#1e1e1e;
    }
    table th {
    	background-image:-webkit-gradient(linear, 0% 100%, 0% 0%, from(#ae9aff), to(#c5b8ff)) !important;
	background-image:-moz-linear-gradient(19% 75% 90deg,#ae9aff, #c5b8ff) !important;
        padding:.5em;
	color:#1e1e1e;
        text-align:left;
	-webkit-border-top-left-radius:8px;
	-webkit-border-top-right-radius:8px;
	border-top-left-radius:8px;
	border-top-right-radius:8px;
    }
    table td {
    	background-color:#e1dff5;
        padding:.5em;
	color:#1e1e1e;
        vertical-align:top;
    }
    table tr.odd td {
    	background-color:#dbd9f4;
	color:#1e1e1e;
    }
    table tr:hover td {
        background-color:#c4b6fc !important;
	color:#1e1e1e;
    }
    table .minimal {
        white-space:nowrap;
        width:1%;
}

.profile_fix {
    overflow:hidden;
    white-space:normal;
    word-wrap: break-word;
}

.reply_body_snippet,.snippet {
    font-style:italic;
}

.topic_headline a:visited {
    font-weight:400;
}

.topic_info {
    float: right;
    cursor: default;
}

em.marked {
    background-color:#ff9;
    font-style:normal;
    font-weight:700;
}

p.caption {
    font-size:11px;
    margin-top:2px;
}

.inline {
    display:inline;
}

#last_seen_marker {
    border-top:2px solid #a49bff;
}

#snapback_link {
    left:2px;
    position:fixed;
    top:50%;
}

.video,video,a.video {
    margin-top:0em;
    width:500px;
    margin-left:auto;
    margin-right:auto;
    margin-bottom:1em;
}

.standalone,.row {
    margin-bottom:1em;
}

.body ul.menu li,.body ul.menu li a,.body ul.menu li span,.unimportant,.unimportant * {
    font-size:10px;
    color:#333333;
}

a:hover,h2 a,#snapback_link a {
    text-decoration:none;
}

a.youtube_alternate,.noscreen {
    display:none;
}

h3#preview,h3#error,h3#replying_to,label,legend,.topic_headline a,.reply_body_snippet a,.snippet a {
    font-weight:700;
}

.help,.help_cursor {
    cursor:help;
}

hr {
    background-color: transparent !important;
    margin-bottom:-9999px;
}
