css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(youtube.com)
{
span
{
	color: grey !important;
}
#masthead-sections,
/* Indivudual videos */
#watch-main-container,
#watch-video-count,
.subscription-container,
#alerts,
#footer,
#watch-channel-brand-div,
#playnav-video-details
{
	display: none !important;
}
#watch-headline,
#watch-video
{
	width: 640px !important;
}
#watch-headline-title,
#watch-headline-user-info
{
	display: inline !important;
}
#content-container,
#content
{
	background: none !important;
}

/* User content */
#masthead-utility,
#user_banner,
#main-channel-content,
.scrollbox-header,
.pinned
{
	display: none !important;
}
#masthead
{
	width: auto !important;
}
#playnav-body,
.scrollbox-body
{
	height: 390px !important;
}
#channel-body
{
	background: none !important;
	height: auto !important;
}
#page,
#masthead-container,
#footer-container
{
	border: none !important;
}
/* User channel colors */
#user_playlist_navigator
{
	color: black !important;
	background-color: grey !important;
}
#playnav-channel-header,
#playnav-play-all-scrollbox,
#playnav-play-uploads-scrollbox,
.navbar-tab
{
	color: black !important;
	background-color: white !important;
}
#playnav-channel-name,
#channel_base_title,
.navbar-tab-selected
{
	color: white !important;
	background-color: black !important;
}
#playnav-chevron
{
	border-left-color: black !important;
}
.contents .a
{
	background-color: black !important;
	border-bottom-color: black !important;
	border-top-color: black !important;
}
.playnav-item-selected .selector
{
	background-color: #ccc !important;
}
}
