css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gyazo.com")
{
    body
    {
      background-image:none !important;
      background-color: #EEEEEE !important;
      padding-top: 20px !important;
    }
    
    .image-screen {
        background-color: #EEEEEE !important;
    }
    
    #room, body, #chat, #left_block, #container, #page, #wrapper, #tinychat, #tinychat_sub {padding: 0px!important; margin: 0px!important; height:100%!important; }
    
    #left_block, #chat { width: 100% !important;}

    #header,
    .navbar,
    .ads,
    .ads_square,
    .ads_bar,
    .image-toolbar,
    .share-buttons-container,
    .image-footer,
    .notify_banner_box,
    .dl_promo_tips,
    .comments-container,
    #right_block
    {
        display:none !important;
    }
    
    #gyazo_img
    {
        border:0 !important;
    }
}
