css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikia.com"),
domain("fandom.com")
{
.wikia-logo__subtitle,
.hubs-links,
#exploreWikiaEntryPoint,
#TOP_RIGHT_BOXAD,
.GamingCalendarModule,
#WikiaSpotlightsModule,
.likes,
.toolbar,
#WikiaArticleBottomAd,
#WikiaFooter,
#meebo-canvas-6,
#WikiaAdInContentPlaceHolder/* Floating 'Trending Articles' */
{
  display: none !important;
}
/* Making the clickable Search box wider */
#globalNavigation
{
    flex-wrap: nowrap !important;
}
.wds-global-navigation__link
{
  margin-left: 10px !important;
}
@media screen and (min-width: 1000px) {
.wds-global-navigation__search-toggle-text
    {
        width: 300px !important;
    }
}
@media screen and (min-width: 1200px) {
.wds-global-navigation__search-toggle-text
    {
        width: 500px !important;
    }
}
@media screen and (min-width: 1400px) {
.wds-global-navigation__search-toggle-text
    {
        width: 600px !important;
    }
}

.CorporateFooter
{
  clear: both !important;
}
}
