css:
@-moz-document domain('reddit.com'){
body {
background:#D4EAF5!important;
font-family:Helvetica, sans-serif!important;
font-size:.75em!important;
font-weight:400!important;
letter-spacing:0!important;
margin:0 1em!important;
width:auto!important
}

#header {
background:none!important;
border-bottom:0!important;
height:6.5em!important
}

#header-img {
max-height: 55px;
}

.score {
color:red!important
}

.comment.expand {
float:left
}

#sr-header-area {
background:#fff!important;
border:0!important;
border-radius:0 0 0 6px !important;
font-size:1em!important;
height:.6em!important;
padding:.3em 0 1em .5em !important;
text-transform:capitalize!important
}

.dropdown.srdrop .selected {
color:#FF4500!important;
font-weight:700!important
}

.drop-choices {
border:0!important;
border-bottom:1px solid #eee!important;
border-left:1px solid #eee!important;
border-radius:0 0 5px 5px !important;
padding:10px!important
}

#sr-more-link {
background:#fff!important;
border-radius:0 0 6px 6px !important;
color:#FF4500!important;
padding:.3em 1em!important;
top:1.9em!important
}

.tabmenu {
bottom:1em;
left:10em;
position:absolute
}

.tabmenu li {
margin:0!important
}

.tabmenu li a {
background:none!important;
border:0!important;
color:#494949!important;
font-size:1em!important;
padding:.5em .9em!important
}

.tabmenu li.selected a {
background:#fff!important;
border-radius:6px!important;
color:#FF4500!important
}

#downarrow {
border-left:10px solid #f6f6f6!important;
border-right:10px solid #f6f6f6!important;
border-top:20px solid #77c!important;
font-size:0!important;
line-height:0!important;
position:relative!important;
width:0!important
}

#header-img-a {
position:relative;
top:0
}

.redditname,.pagename {
background:none!important;
color:#494949!important;
font-size:1em!important;
font-variant:normal!important;
margin:0!important;
position:relative!important;
right:25em;
top:3.5em
}

.searchpane.raisedbox {
border-radius:6px
}

.hover.redditname {
right:2.5em!important;
text-align:center!important;
top:0!important
}

.redditname a {
color:#494949!important;
font-size:1em!important;
font-variant:normal!important;
margin:0!important
}

#header-bottom-right {
background:none!important;
bottom:.5em!important;
color:#000!important;
font-weight:700!important
}

.separator {
font-weight:400!important
}

.content {
background:#fff!important;
border-radius:6px;
margin:0 310px 0 0 !important;
padding:.75em!important;
width:auto!important
}

.content .content {
background:none!important;
box-shadow:none!important;
position:relative;
width:auto!important
}

ul.tabmenu.formtab {
bottom:15px;
position:absolute;
right:125px
}

.side {
background:none!important;
margin:0!important;
padding:0!important
}

.side .spacer {
background:#fff!important;
border-radius:6px!important;
margin:0 0 1.2em!important
}

.side .spacer #search input {
border:1px solid #494949!important;
font-size:1.4em!important;
font-weight:400!important;
margin:.6em!important;
width:270px!important
}

.sidecontentbox {
margin:0!important;
padding:.5em .2em .1em .6em !important
}

.sidecontentbox .content {
border:0!important;
box-shadow:none!important;
clear:both;
margin:0!important;
padding:.8em .2em .1em .4em !important;
width:auto!important
}

.fancy-toggle-button a {
border:0!important;
border-radius:5px!important;
color:#fff!important;
font-size:.7em!important;
font-weight:700!important;
top:1em!important
}

.fancy-toggle-button .remove {
background:#d54a61!important;
padding:2px 8px!important;
position:relative!important;
top:-.2em!important
}

.fancy-toggle-button .add {
background:#54d54a!important;
padding:2px 8px!important;
position:relative!important;
top:-.2em!important
}

.titlebox .hover.redditname.hover {
padding:0 0 0 10em !important
}

.titlebox h2,.titlebox h3,.titlebox p {
position:relative!important;
top:.4em!important
}

.titlebox .number,.titlebox .word {
color:#90d3f5!important;
font-size:1em!important;
font-weight:700!important;
left:2em!important
}

.number {
display:block
}

.word {
display:none
}

.number::after {
content:" readers"!important
}

div.titlebox span.number::before {
content:none!important
}

.titlerow .title {
color:#494949!important;
font-size:1.5em!important;
font-weight:700!important
}

.titlebox .bottom {
border-top:1px solid #eee!important;
padding:10px 0 0!important
}

.morelink {
background:#90d3f5!important;
border:0!important;
border-radius:6px!important;
font-size:1.2em!important;
letter-spacing:0!important;
padding:0!important
}

.hover.pagename.redditname.hover {
opacity:0!important
}

.morelink:hover {
background:#FF4500!important
}

.sidebox {
letter-spacing:0!important
}

.sidebox.submit {
font-size:1.2em!important
}

.sidebox.create .spacer,.sidebox.submit .spacer,.sidebox.gold .spacer {
box-shadow:none!important;
font-size:.8em!important;
margin:0!important;
min-height:0!important;
padding:1em 0 0!important
}

.sponsorshipbox.thing {
border:0!important;
padding:0!important
}

.linkinfo {
background:none!important;
border:0!important;
padding:1em!important
}

.linkinfo .downvotes {
color:#90d3f5!important
}

.thing {
border-bottom:1px solid #eee;
margin:0!important;
padding:.4em 2em 0 0 !important
}

.organic-listing {
border:0!important;
border-radius:6px!important
}

.link.promotedlink.promoted {
background:#F5F9FB!important
}

.link .score {
color:#43a1cf!important;
font-size:1.2em!important;
font-weight:700!important;
margin:0 0 0 -1px !important
}

.midcol {
margin:0 1em!important
}

.thumbnail {
margin:0 1em 0 0 !important;
padding:0!important
}

.thing .title.loggedin:visited {
color:#888!important
}

.thing .title.loggedin {
font-weight:700!important;
padding:0 5px 0 0 !important
}

.domain,.domain a {
color:#888!important;
font-size:.85em!important
}

.tagline {
font-size:.9em!important
}

.domain {
position:relative
}

.arrow {
background:url(data:image/gif;base64,)!important;
height:15px!important
}

.arrow.up {
background-position:0 0!important
}

.arrow.down {
background-position:0 -15px!important
}

.arrow.upmod {
background-position:0 -30px!important
}

.arrow.downmod {
background-position:0 -45px!important
}

.menuarea .drop-choices {
border-bottom:1px solid #eee!important;
border-left:1px solid #eee!important
}

.comment {
border-bottom:0 solid gray!important;
padding:0!important
}

.comment .child {
border-left:1px solid #eee!important
}

.footer-parent {
background:#fff!important;
border-radius:6px;
margin:20px 310px 0 0 !important;
padding:.75em!important;
text-align:left!important;
width:auto!important
}

.footer {
border:0!important;
margin:0!important;
padding:0!important
}

.footer .col {
border-left:1px solid #c1dbf5!important;
margin:0 0 20px!important;
padding:10px 20px!important
}

.footer .col:first-of-type {
border-left:0!important;
padding:10px 20px 10px 0 !important
}

#header-bottom-right a,.thing .title.loggedin {
color:#494949!important
}

#siteTable_organic.organic-listing div.thing div.entry p.title {
padding:0 1em 0 0 !important
}

.entry .buttons li a,.tagline a,.nextprev a,.footer .col a {
color:#43a1cf
}

.titlebox,.submit,.create,.gold {
font-size:1.3em!important;
padding:.3em!important
}

.morelink .nub {
display:none!important
}

.link .rank {
color:#888!important;
font-size:1em!important;
margin:0!important;
position:relative!important;
top:1.7em!important
}

.flat-list {
padding:1.2em 0 .4em!important
}

.error-log .exception.interesting {
border:2px solid #000!important
}

.link.last-clicked {
border:1px solid!important
}

.message.message-reply .entry,.message.message-parent .entry {
border-left:2px solid!important
}

.error-log .exception.new {
border:solid #f60 2px!important
}

#img-preview-container {
border-style:dashed!important
}

.cup-info-box {
border:solid!important
}

.bidding-history .linefield {
border-left:1px #DDD solid!important
}

.calendar div.grid + div.grid {
border-left:1px solid!important
}

.gold-accent.comment-visits-box {
border-radius:6px!important;
padding:7px 10px 7px 7px !important
}

.panestack-title a.title-button.gold {
border-radius:6px!important;
bottom:.5em!important;
padding:4px 8px!important;
position:relative!important
}

.sponsored-tagline {
right:1em!important
}

.link.promotedlink {
border-radius:6px!important;
padding:5px 0 5px .5em !important;
position:relative!important
}

.link.promotedlink p {
margin:0 5em 0 0 !important
}

.help a.open {
right:.1em!important
}

.min-body .content {
width:98%!important
}

.button-body {
background:none!important;
margin-left:0!important;
margin-top:-4px!important
}

.side .spacer:first-of-type,.infobar {
border-radius:6px!important
}

.panestack-title,.commentarea h1 {
border-bottom:1px solid #eee!important
}

#preview #previewbox,#preview-table > table {
border-style:solid!important
}

span.RESshortcut,.styleToggle {
display:inline;
float:right;
font-size:12px;
padding:2px 5px
}

.styleToggle {
padding:0!important
}

.linkinfo .upvotes,.noncollapsed .author.submitter,.sr-bar .separator,.morelink a,.bottommenu,.bottommenu a {
color:#fff!important
}

#srLeftContainer,#RESStaticShortcuts,#RESShortcuts,#srDropdown,#RESShortcutsEditContainer,#RESShortcutsTrash,#RESShortcutsRight,#RESShortcutsLeft,#RESShortcutsAdd {
background:#fff!important
}

.titlebox h1 {
float:right;
margin:0!important;
padding:0!important
}

#header-bottom-left {
background:transparent
}
}
