css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kwality.polip.com") {
body,html {
background-color:#FFF;
color:#000
}

pre,.serializer {
background-color:#F3F3F3;
border:gray 1px;
color:#000;
display:block;
margin:1em 2em;
padding:1em 1em 1em 6em;
position:relative
}

table {
border-collapse:collapse;
margin:1em auto 1em 15%;
width:70%
}

th,td {
border:#a1a1a1 solid 1px;
padding:0.3em 1em
}

th {
background:#e3e3e3;
color:#000
}

td.counter {
text-align:right
}

table.deprec {
width:auto
}

.serializer {
font-family:monospace;
overflow:visible
}

code {
background-color:#F0F0F0;
font-weight:700;
padding-left:0.5ex;
padding-right:0.5ex;
white-space:nowrap
}

.tag {
color:#040
}

.attrname {
color:#800
}

.attr {
color:green
}

.link {
color:blue;
text-decoration:underline
}

.serializer a,.pre a {
left:1em;
position:absolute
}

#err {
border:red inset 2px;
margin:1em 15% 3em;
padding:1em
}

#err h2 {
color:red;
text-align:center
}

.bodywords {
background-color:#F3F3F3;
border:gray inset 1px;
color:#000;
display:block;
margin:1em 2em;
padding:1em
}

.kword {
color:red;
font-weight:700
}

body {
font-family:"Trebuchet MS", Arial, sans-serif;
font-size:90%
}

br+h2 {
background:#c00;
color:#FFF;
float:right;
padding:0.5em 2em;
position:absolute;
top:0
}

div,form,h1 {
display:none
}

h1+h2 {
margin-top:4em
}

pre {
border:none
}
}
