css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@-moz-document url("chrome://browser/content/preferences/preferences.xul"),
               url("chrome://browser/content/pageinfo/pageInfo.xul") {

#BrowserPreferences,
#main-window {
  background-color: #ccd9ea !important;
  background-image: -moz-linear-gradient(top, rgb(231,237,246) 3px, rgba(231,237,246,0) 156px),
                    -moz-linear-gradient(rgba(241,245,251,0) 25%, rgba(241,245,251,.75)),
                    -moz-linear-gradient(left, rgba(255,255,255,.3), rgba(255,255,255,0) 20%, rgba(255,255,255,0) 80%, rgba(255,255,255,0.3)), 
                    -moz-linear-gradient(30deg, rgba(255,255,255,0), rgba(255,255,255,0) 87%, rgba(255,255,255,.25)),
                    -moz-linear-gradient(-30deg, rgba(255,255,255,.25) 13%, rgba(255,255,255,0), rgba(255,255,255,0)),
                    -moz-repeating-linear-gradient(30deg, rgba(255,255,255,0), rgba(255,255,255,0) 30%, rgba(255,255,255,.05) 30.5%, rgba(255,255,255,.05) 38%, rgba(255,255,255,0) 38.5%),
                    -moz-repeating-linear-gradient(30deg, rgba(255,255,255,0), rgba(255,255,255,0) 27%, rgba(255,255,255,.05) 27.5%, rgba(255,255,255,.05) 35%, rgba(255,255,255,0) 35.5%),
                    url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAM0lEQVQI12M4dOGJ1IFzj6T+//8v9ev3fykGEAdFAMQA4S2HrkmBVDOAREEYxDl9860UAAjiLPR0/dzCAAAAAElFTkSuQmCC);
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat,
                     no-repeat,
                     no-repeat,
                     no-repeat,
                     no-repeat,
                     repeat !important;
  min-width: 53em !important;
  min-height: 40em !important;
  padding: 0 !important;
  margin: 0 !important;
}

/* Dutch */
#BrowserPreferences[style="width: 48em; min-height: 37.5em;"] {
  min-height: 42em !important;
}

#topBar {
  border: 0 !important;
}

.paneSelector,
#viewGroup {
  position: fixed !important;
  left: 0 !important;
  top: 0 !important;
  -moz-binding: url("chrome://mozapps/content/extensions/extensions.xml#categories-list") !important;
  margin: 0 !important;
  border: 0 !important;
  -moz-margin-start: 8px !important;
  background: transparent !important;
  width: 60px !important;
  padding: 2px 0 !important;
}

.paneSelector > radio > label,
radio[oncommand*="showTab"] > label  {
  display: none !important;
}

.paneDeckContainer {
  margin: -.3em 0 0 0  !important;
  padding: 0 !important;
  -moz-margin-start: 60px !important;
}

.prefWindow-dlgbuttons {
  padding: 0 !important;
  margin: -12px 10px 10px 0 !important;
}

#mainDeck {
  -moz-margin-start: 60px !important;
}

radio[pane],
radio[oncommand*="showTab"] {
  -moz-binding: url("chrome://mozapps/content/extensions/extensions.xml#category") !important;
  -moz-appearance: none !important;
  background: transparent !important;
  padding: 5px 2px !important;
  margin: 6px 0 !important;
  border-radius: 2px !important;
  border: 1px solid transparent !important;
  list-style-image: url(data:image/png;base64,) !important;
}

radio[oncommand*="showTab"] {
  list-style-image: url(data:image/png;base64,) !important;
}

radio[pane=paneMain] {
  -moz-image-region: rect(0, 32px,  32px, 0) !important;
}

radio[pane=paneTabs] {
  -moz-image-region: rect(0, 64px, 32px, 32px) !important;
}

radio[pane=paneContent] {
  -moz-image-region: rect(0, 96px,  32px, 64px) !important;
}

radio[pane=paneApplications] {
  -moz-image-region: rect(0, 128px,  32px, 96px) !important;
}

radio[pane=panePrivacy] {
  -moz-image-region: rect(0, 160px,  32px, 128px) !important;
}

radio[pane=paneSecurity] {
  -moz-image-region: rect(0, 192px,  32px, 160px) !important;
}

radio[pane=paneAdvanced] {
  -moz-image-region: rect(0, 224px, 32px, 192px) !important;
}

radio[pane=paneSync] {
  -moz-image-region: rect(0, 256px, 32px, 224px) !important;
}

radio[pane=paneMain][selected="true"] {
  -moz-image-region: rect(32px, 32px,  64px, 0) !important;
}

radio[pane=paneTabs][selected="true"] {
  -moz-image-region: rect(32px, 64px, 64px, 32px) !important;
}

radio[pane=paneContent][selected="true"] {
  -moz-image-region: rect(32px, 96px,  64px, 64px) !important;
}

radio[pane=paneApplications][selected="true"] {
  -moz-image-region: rect(32px, 128px,  64px, 96px) !important;
}

radio[pane=panePrivacy][selected="true"] {
  -moz-image-region: rect(32px, 160px,  64px, 128px) !important;
}

radio[pane=paneSecurity][selected="true"] {
  -moz-image-region: rect(32px, 192px,  64px, 160px) !important;
}

radio[pane=paneAdvanced][selected="true"] {
  -moz-image-region: rect(32px, 224px, 64px, 192px) !important;
}

radio[pane=paneSync][selected="true"] {
  -moz-image-region: rect(32px, 256px, 64px, 224px) !important;
}

#generalTab {
  -moz-image-region: rect(0px, 32px, 32px, 0px)!important;
}

#mediaTab {
  -moz-image-region: rect(0px, 64px, 32px, 32px)!important;
}

#feedTab {
  -moz-image-region: rect(0px, 96px, 32px, 64px)!important;
}

#permTab {
  -moz-image-region: rect(0px, 128px, 32px, 96px) !important;
}

#securityTab {
  -moz-image-region: rect(0px, 160px, 32px, 128px) !important;
}

#generalTab {
  -moz-image-region: rect(0px, 32px, 32px, 0px) !important;
}

#generalTab[selected="true"] {
  -moz-image-region: rect(32px, 32px, 64px, 0px) !important;
}

#mediaTab[selected="true"] {
  -moz-image-region: rect(32px, 64px, 64px, 32px) !important;
}

#feedTab[selected="true"] {
  -moz-image-region: rect(32px, 96px, 64px, 64px) !important;
}

#permTab[selected="true"] {
  -moz-image-region: rect(32px, 128px, 64px, 96px) !important;
}

#securityTab[selected="true"] {
  -moz-image-region: rect(32px, 160px, 64px, 128px) !important;
}

radio[pane][selected],
radio[oncommand*="showTab"][selected] {
  border-color: rgba(36,52,71,.5) !important;
  background-image: -moz-linear-gradient(rgba(61,76,92,.2), rgba(61,76,92,.2)),
                    -moz-linear-gradient(rgba(250,250,250,.25), rgba(166,184,206,.125) 50%,
                                         rgba(45,78,115,.06) 50%, rgba(31,55,84,.025)) !important;
  box-shadow: 0 0 3px rgba(51,74,98,.2) inset,
              0 1px 3px rgba(44,71,103,.2) inset !important;
}

groupbox {
  -moz-appearance: none !important;
  padding: 8px !important;
  -moz-margin-start: 15px !important;
  border: 0 !important;
}

.groupbox-body {
  border: 1px solid hsla(218,38%,64%,.35) !important;
  border-radius: 5px !important;
  background-image: -moz-linear-gradient(hsla(0,0%,100%,0), hsla(0,0%,100%,.2)) !important;
  background-color: hsla(0,0%,100%,.35) !important;
  background-repeat: no-repeat !important;
  background-clip: padding-box !important;
  box-shadow: 0 0 3px hsla(213,44%,40%,.10) inset,
              0 1px 0 hsla(0,0%,100%,.5) !important;
}

.caption-text,
.text-link,
#currentAccount,
#accountName {
  font-family: 'Segoe UI Semibold','Segoe UI',sans-serif !important;
  color: rgba(37,47,59,.95) !important;
  text-shadow: 0 1px 0 rgba(255,255,255,.1) !important;
  font-weight: 600 !important;
}

.groupbox-title {
  font-size: 120% !important;
  margin-bottom: 24px !important;
  -moz-margin-start: -16px !important;
}

caption {
  -moz-appearance: none !important;
  background: none !important;
  margin-bottom: -18px !important;
}

/* Buttons */

button,
menulist {
  -moz-appearance: none !important;
  -moz-transition: .24s background-color ease-in !important;
  border: 1px solid rgba(26,58,96,.38) !important;
  padding: 0 4px !important;
  margin: 3px 4px !important;
  background-color: rgba(223,232,242,.5) !important;
  background-image: -moz-linear-gradient(rgba(250,250,250,.5), rgba(166,184,206,.25) 50%,
                                         rgba(45,78,115,.12) 50%, rgba(31,55,84,.05)) !important;
  background-clip: padding-box;
  box-shadow: 0 0 0 1px rgba(255,255,255,.25) inset,
              0 0 3px rgba(255,255,255,.25) inset !important;
  border-radius: 3px !important;
  font-family: 'Segoe UI Semibold','Segoe UI',sans-serif !important;
  color: rgba(37,47,59,.95) !important;
  text-shadow: 0 1px 0 rgba(255,255,255,.1) !important;
  font-weight: 600 !important;
  font-size: 12px !important;
}

.menulist-dropmarker {
  list-style-image: url(data:image/png;base64,) !important;
}

#manageAccountExpander {
  padding: 1px 0 2px 0 !important;
  list-style-image: url(data:image/png;base64,) !important;
  -moz-image-region: rect(0,11px,11px,0) !important;
}

#manageAccountExpander .button-icon {
  padding: 0 !important;
  margin: 3px 5px 0 !important;
}

#manageAccountExpander.expander-up,
#manageAccountExpander:hover:active {
  -moz-image-region: rect(0,22px,11px,11px) !important;
}

menulist {
  padding: 1px 0 !important;
  -moz-padding-end: 2px !important;
}

button:not(:active):not([open]):not([disabled="true"]):not(.expander-up):hover,
menulist:not([open]):not([disabled="true"]):hover {
  background-color: hsl(212,42%,95%) !important;
}

button:not([disabled="true"])[open],
button:not([disabled="true"]):hover:active,
menulist:not([disabled])[open],
.expander-up {
  border-color: rgba(36,52,71,.5) !important;
  background-image: -moz-linear-gradient(rgba(61,76,92,.2), rgba(61,76,92,.2)),
                    -moz-linear-gradient(rgba(250,250,250,.25), rgba(166,184,206,.125) 50%,
                                         rgba(45,78,115,.06) 50%, rgba(31,55,84,.025)) !important;
  box-shadow: 0 0 3px rgba(51,74,98,.2) inset,
              0 1px 3px rgba(44,71,103,.2) inset !important;
  color: rgba(255,255,255,.95) !important;
  text-shadow: 0 1px 0 rgba(37,47,59,.4) !important;
}

button[disabled="true"] {
  opacity: .8 !important;
  background-image: none !important;
}

.menulist-label-box {
  border: 1px solid transparent !important;
  background: none !important;
}

.menulist-label-box > .menulist-label {
  color: rgba(37,47,59,.95) !important;
  text-shadow: 0 1px 0 rgba(255,255,255,.1) !important;
  font-weight: 600 !important;
}

menulist[open] .menulist-label-box > .menulist-label {
  color: rgba(255,255,255,.95) !important;
  text-shadow: 0 1px 0 rgba(37,47,59,.4) !important;
}

.menulist-dropmarker {
  -moz-appearance: none !important;
  border: 0 !important;
  background: none !important;
  padding: 0 !important;
}

menulist[open] .menulist-dropmarker {
  list-style-image: url(chrome://browser/skin/appmenu-dropmarker.png) !important;
}

/* checkbox */

checkbox {
  margin: 0 !important;
  padding: 2px 0 !important;
  -moz-padding-start: 1px !important;
}

checkbox .checkbox-check {
  -moz-appearance: none !important;
  -moz-transition: .2s opacity !important;
  border: 0 !important;
  background: transparent url(data:image/png;base64,) no-repeat right center !important;
  height: 11px !important;
  width: 13px !important;
  opacity: .5 !important;
}

checkbox:not([checked]):hover .checkbox-check {
  opacity: 1 !important;
}

checkbox[checked] .checkbox-check {
  background: transparent url(data:image/png;base64,) no-repeat left center !important;
  opacity: 1 !important;
}

/* Radio */

radio:not([pane]),
.radio-check-box1,
.radio-label-box {
  -moz-appearance: none !important;
}

radio:not([pane]) .radio-check-box1 {
  -moz-transition: .2s background-color ease-in !important;
  border: 1px solid rgba(36,52,71,.5) !important;
  border-radius: 100% !important;
  background-color: rgba(223,232,242,.5) !important;
  background-image: -moz-linear-gradient(rgba(61,76,92,.2), rgba(61,76,92,.2)),
                    -moz-linear-gradient(rgba(250,250,250,.25), rgba(166,184,206,.125) 50%,
                                         rgba(45,78,115,.06) 50%, rgba(31,55,84,.025)) !important;
  background-clip: padding-box !important;
  box-shadow: 0 0 1px rgba(51,74,98,.2) inset,
              0 1px 2px rgba(44,71,103,.2) inset!important;
  min-height: 14px !important;
  min-width: 14px !important;
}

radio:not([pane]) .radio-check-box2 {
  border: 0 !important;
  opacity: 0 !important;
  -moz-transition: .2s opacity !important;
}

radio:not([pane])[selected] .radio-check-box2 {
  opacity: 1 !important;
  margin: 2px !important;
  border: 1px solid rgba(26,58,96,.38) !important;
  border-radius: 100% !important;
  background-color: hsl(212,42%,95%) !important;
  background-image: -moz-linear-gradient(rgba(250,250,250,.5), rgba(166,184,206,.25) 50%,
                                         rgba(45,78,115,.12) 50%, rgba(31,55,84,.05)) !important;
  background-clip: padding-box !important;
  box-shadow: 0 0 0 1px rgba(255,255,255,.25) inset,
              0 0 3px rgba(255,255,255,.25) inset !important;
}

radio:not([pane]) .radio-check {
  display: none !important;
}

.radio-label-box {
  border: 1px solid transparent !important;
}

/* Textboxes */

filefield {
  -moz-appearance: none !important;
  border: 0 !important;
  background: none !important;
  margin: 3px 4px !important;
}

filefield .fileFieldContentBox,
textbox:not(.fileFieldLabel) {
  -moz-appearance: none !important;
  border-radius: 2px !important;
  border: 1px solid rgba(36,52,71,.5) !important; 
  background-color: rgba(255,255,255,.75) !important;
}

textbox[readonly="true"] {
  border: 1px solid transparent !important;
  background: none !important;
}

/*Richlistbox/item */

#handlersView richlistitem {
  max-height: 35px !important;
  min-height: 35px !important;
}

#handlersView richlistitem .actionsMenu {
  -moz-margin-end:15px !important;
}

#handlersView .box-inherit.scrollbox-innerbox {
  padding-bottom: 2px !important;
}

/* Tabs */

#tabsElement tab {
  -moz-appearance: none !important;
  -moz-transition: none !important;
  height: 22px !important;
  max-height: 22px !important;
  background: -moz-linear-gradient(rgba(255,255,255,0),rgba(255,255,255,.5)) !important;
  background-clip: padding-box !important;
  background-color: transparent !important;
  padding: 0 8px 2px 8px !important;
  margin: 0 1px !important;
  border-radius: 2.5px 2.5px 0 0 !important;
  border: 2px solid !important;
  border-bottom: 0 !important;
  -moz-border-left-colors: #BDCAE2 rgba(255,255,255,.45) !important;
  -moz-border-top-colors: #BDCAE2 rgba(255,255,255,.45) !important;
  -moz-border-right-colors: #BDCAE2 rgba(255,255,255,.45) !important;
  font-family: 'Segoe UI Semibold','Segoe UI',sans-serif !important;
  color: rgba(37,47,59,.95) !important;
  text-shadow: 0 1px 0 rgba(255,255,255,.1) !important;
  font-weight: 600 !important;
}

#tabsElement tab[selected="true"]{
  -moz-transition: none !important;
  height: 25px !important;
  max-height: 25px !important;
  box-shadow: none !important;
  background: none !important;
  border: 0 !important;
  border-bottom: 0 !important;
  margin: 0 0 -2px 0 !important;
  padding: 0 7px 0 7px !important;
  -moz-border-image: url(data:image/png;base64,) 2 4 3 4 / 2px 4px 3px 4px !important;
  opacity: .99 !important;
}

#tabsElement {
  -moz-appearance: none !important;
  border-bottom: 1px solid #BDCAE2 !important;
  background: none !important;
  padding: 0 3px !important;
}

tabpanels {
  -moz-appearance: none !important;
  border: 1px solid #BDCAE2 !important;
  border-top: 0 !important;
  background: #DAE5F4 !important;
  border-radius: 0 0 2.5px 2.5px !important;
  box-shadow: 0 0 0 1px rgba(255,255,255,.15) inset !important;
}

.tabs-right {
  border: 0 !important;
}

/* Tree cols */
treecol,
.treecol-image {
  -moz-appearance: none !important;
  -moz-transition: .24s background-color ease-in !important;
  border: 0 !important;
  padding: 0 4px !important;
  -moz-border-start: 1px solid rgba(26,58,96,.38) !important;
  border-bottom: 1px solid rgba(26,58,96,.38) !important;
  background-color: rgba(223,232,242,.5) !important;
  background-image: -moz-linear-gradient(rgba(250,250,250,.5), rgba(166,184,206,.25) 50%,
                                         rgba(45,78,115,.12) 50%, rgba(31,55,84,.05)) !important;
  background-clip: padding-box !important;
  box-shadow: 0 0 0 1px rgba(255,255,255,.25) inset,
              0 0 3px rgba(255,255,255,.25) inset !important;
  font-family: 'Segoe UI Semibold','Segoe UI',sans-serif !important;
  color: rgba(37,47,59,.95) !important;
  text-shadow: 0 1px 0 rgba(255,255,255,.1) !important;
  font-weight: 600 !important;
  font-size: 12px !important;
}

treecol:not([sortDirection]):hover {
  background-color: hsl(212,42%,95%) !important;
}

treecol[sortDirection="descending"] {
  border-color: rgba(36,52,71,.5) !important;
  background-image: -moz-linear-gradient(rgba(61,76,92,.2), rgba(61,76,92,.2)),
                    -moz-linear-gradient(rgba(250,250,250,.25), rgba(166,184,206,.125) 50%,
                                         rgba(45,78,115,.06) 50%, rgba(31,55,84,.025)) !important;
  box-shadow: 0 0 3px rgba(51,74,98,.2) inset,
              0 1px 3px rgba(44,71,103,.2) inset !important;
  color: rgba(255,255,255,.95) !important;
  text-shadow: 0 1px 0 rgba(37,47,59,.4) !important;
}

treecol:first-Child {  
  -moz-border-start: 0 !important;  
}

.tree-columnpicker-icon {
  list-style-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAAKCAYAAABi8KSDAAAAnklEQVQY02P4//+/DxA3IGFsfAYQBhENrsFx/x39Iv/beoX8B/FNnXz+G9l7/jewcfsP1cAEU0yayZGZJf9DkvP++8dl/sdmE7LJDSkl9f/j86v+gzSB+CBNXhHJ/0GaMBTn1Xf9z6xq/Q/ShOYEFOeAFZd1TPlf1NL/H6QJ3SZk5+H0IMwmkCZkkxmgboJhuAEwm2AhAlOMDfugBx0AbJdFBkXonpcAAAAASUVORK5CYII=) !important;
}

treecol:not([hideheader="true"]) > .treecol-sortdirection[sortDirection="ascending"] {
  list-style-image: url("data:image/png;base64,") !important;
}

treecol:not([hideheader="true"]) > .treecol-sortdirection[sortDirection="descending"] {  
  list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAHCAYAAADam2dgAAAAS0lEQVQY02P4//8/AyGMIfDr169neBX9+fPnE5D+//fv3x9YFf38+fP2fyQA1YBQ9Pv37/tA3X/Q8ffv34/CFanqW6fhwlgdjg0DAJN4vLqylPaYAAAAAElFTkSuQmCC") !important;  
  -moz-padding-end: 2px !important;   
}

.inset.iframe,
#permList {
  border: 1px solid rgba(36,52,71,.5) !important; 
  background-color: rgba(255,255,255,.75) !important;
}

/* fixes for new sync pane */
#syncEnginesList {
  border: 0 !important;
  background: none !important;
  -moz-appearance: none !important;
}

#syncEnginesList richlistitem {
  border-color: transparent !important;
  background: none !important;
  box-shadow: none !important;
}
}
