css:
@-moz-document url-prefix("http://www.reddit.com/r/"), url-prefix("http://www.reddit.com/message/moderator/"), url-prefix("http://www.reddit.com/user/") {
.banned-user {
   opacity: 0.65 !important}
.reported.comment>.entry,.reported.link {
   border-bottom: 1px solid #f5e9b8;
   border-left: 5px solid #f6e69f;
   border-right: 5px solid #f6e69f}
.reported.comment>.entry,.reported.link,.spam.comment>.entry,.spam.link,.spam.message,.spam.message>.entry {
   -moz-border-radius: 5px;
   -webkit-border-radius: 5px;
   border-radius: 5px;
   overflow: hidden}
.reported.comment>.child,.spam.comment>.child,.spam.message>.child {
   margin-left: 15px;
   padding-left: 0px}
.reported.comment>.entry,.spam.comment>.entry {
   margin-left: 10px;
   padding-bottom: 1px;
   padding-left: 5px;
   padding-right: 5px}
.spam.comment,.spam.link,.spam.message {
   background: none !important}
.spam.comment>.entry,.spam.link,.spam.message>.entry {
   border-bottom: 1px solid #ffddd9;
   border-left: 5px solid #fa8072;
   border-right: 5px solid #fa8072}
.spam.message {
   border: none;
   border-left: 5px solid #fa8072 !important}
}
