css:
@-moz-document domain("facebook.com")
{
/***
* HIDING ADS
***/

.UIStandardFrame_SidebarAds, #pagelet_netego_ads, #pagelet_adbox {
    display: none;
}

/***
* GENERAL
***/

.fbx #globalContainer {
   width: 100% !important;
   min-width: 981px !important;
}

.uiProfilePhotoLarge {
}

.fbxWelcomeBox img {
}

.UIImageBlock_MED_Image img {
   width: 70px !important;
   height: 70px !important;
}

.UIImageBlock_MED_Image:hover img {
   width: 90px !important;
   height: 90px !important;
}

body {
   font-family: "lucida grande",tahoma,verdana,arial,sans-serif !important;
   font-size: 12px !important;
}

.fbProfilePhotoStrip, .fbProfilePhotoBar {
   width: auto !important;
}

a.fbProfilePhotoThumb i {
    background-position: 0 0 !important;
    -moz-background-size: 200px !important;
    -webkit-background-size: 200px !important;
    background-size: 200px !important;
    width: 200px !important;
    height: 170px !important;
}

.profile-picture {
   width: 176px !important;
   z-index: 2;
}

.profile-picture:hover {
   width: 244px !important;
}

.profile-picture img {
   width: 100% !important;
   max-width: 100% !important;
}

.uiStream .uiStreamMessage {
    font-size: 12px !important;
}

#fbDockChatBuddylistNub .fbNubFlyout {
    /* margin-bottom: 66px !important; */
}

.profileFriendsText {
    max-height: 48px !important;
}

.profileFriendsNetwork {
    white-space: normal !important;
}

.uiProfilePhotoMedium {
    height: 50px !important;
    width: 50px !important;
}

.uiProfilePhotoMedium:hover {
    height: 70px !important;
    width: 70px !important;
}

.contact_importer_widget .ci_login, .contact_importer_widget .ci_password {
    width: 156px !important;
}
/***
* HEADER 
***/

#headNav {
    background-image: -moz-linear-gradient(-45deg, #4B72C1, #3B5998);
    background-image: -webkit-gradient(linear, center top, center bottom, from(#4B72C1), to(#3B5998) );
}

.fbx #pageHead {
    margin: 0 0 0 10px !important;
    width: auto;
}

#pageHead {
    margin: 0 auto;
    width: 980px;
}

.ptm {
    padding-top: 10px;
}

/***
* FIRST COLUMN 
***/

.hasLeftCol #leftCol {
    display: block;
}

.home #leftCol {
    min-height: 0px !important;
}

#leftCol {
    display: none;
    float: left !important;
    padding: 10px 0 10px 10px !important;
    width: 176px !important;
    word-wrap: break-word;
    margin-bottom: -999999px;
}

/***
* CONTENT PLUS SECOND COLUMN CONTAINTER 
***/

.hasLeftCol #contentCol {
    border-left: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
}

#contentCol {
    background-color: #FFFFFF;
    padding-top: 0 !important;
}

/***
* SECOND COLUMN
***/

.hasLeftCol #rightCol {
    padding-right: 10px !important;
}

.hasRightCol #rightCol {
    margin-top: 0;
    padding-right: 10px !important;
}

#rightCol {
    float: right;
    margin-top: -10000px;
    padding-top: 10px !important;
    width: 176px !important;
    word-wrap: break-word;
    margin-bottom: -999999px;
}

#pagelet_eventbox input.event_name {
    width: 156px !important;
}

.ego_column {
    width: 176px !important;
}

/***
* CONTENT ITSELF 
***/

.hasLeftCol #headerArea {
    float: none !important;
    margin: 0 186px !important;
    width: auto !important;
}

#headerArea {
    padding: 5px 10px !important;
}

.hasLeftCol .hasRightCol #contentArea {
    padding: 10px 10px 0 10px !important;
    margin: 0 186px 0 186px !important;
    width: auto !important;
}

.hasRightCol #contentArea {
    width: auto !important;
    padding: 10px 10px 0 10px !important;
    margin-right: 186px !important;
}

.hasLeftCol #contentArea {
    padding: 10px 10px 0 10px !important;
    width: auto !important;
    margin-left: 186px !important;
}

.hasLeftCol #contentArea, .hasRightCol #contentArea {
    float: none !important;
    margin-right: 0;
}

#contentArea {
    /*margin-right: -1px;
    padding-left: 1px;*/
    word-wrap: break-word;
}

/***
* FOOTER 
***/

.hasLeftCol #pageFooter {
    background-color: #FFFFFF;
    clear: both;
}

.fbx #pageFooter {
    margin: auto;
    width: auto !important;
}

#pageFooter {
    color: #737373;
    font-size: 11px;
    margin: 15px auto !important;
    width: auto !important;
    margin-left: 186px !important;
    margin-right: 186px !important;
}

.hasLeftCol #contentCurve {
    border-color: -moz-use-text-color #B3B3B3 #B3B3B3;
    border-style: none solid solid;
    border-width: medium 1px 1px;
    position: relative;
}

#contentCurve {
    display: none !important;
    background-color: #FFFFFF;
    border-bottom: 1px solid #B3B3B3;
    font-size: 1px;
    height: 8px;
}
}
