css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  
 {body {background: url('http://i56.tinypic.com/332winr.jpg')bottom left #070707 no-repeat fixed !important;}

ol#posts li blockquote,

body.mceContentBody blockquote {margin-left: 0 !important;margin-right: 0 !important; padding-left: 10px !important; border-width: 4px !important; border-color: #070707 !important;}

ol#posts li.notification.single_notification {border-bottom: 1px solid rgba(0,0,0,0);}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(52,51,51,0) !important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(52,51,52,0) !important; -webkit-border-radius: 20px; -moz-border-radius: 20px; border-radius: 20px; margin: auto; padding: 40px auto;}

h1 {color: #3E4245; text-shadow: 0 0 0px #f8f8f0;}

.no_posts_found, h1.dashboard_header {color: #666666;}

img#content_top, img#content_bottom {display: none;}

#right_column a
#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {background: none;}
#right_column .dashboard_nav_item {background: none; background-attachment: initial; background-position-x: 50%; background-position-y: 100%; background-origin: initial; background-clip: initial; background-color: initial;}
#right_column .dashboard_nav_item #hide_radar {background-image: none; background-repeat-x: no-repeat; background-repeat-y: repeat; background-attachment: initial; background-position-x: 0%; background-position-y: 0%; background-origin: initial; background-clip: initial; background-color: transparent;}
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {background: rgba(0,0,0,0.5);}
#nav .nav_item.active {background: rgba(102,18,18,0) !important;}
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {background: rgba(52,51,51,0) !important;}

ol#posts li.notification.alt {background-color: rgba(0,0,0,0);}

ol#posts li.notification.first_notification {background-color: rgba(0,0,0,0);}

ol#posts li.notification {background-color: rgba(0,0,0,0); border-bottom: 1px solid rgba(0,0,0,0);}

ol#posts li.notification.last_notification {background-color: rgba(0,0,0,0); border-bottom: 1px solid rgba(0,0,0,0);}

ol#posts li.post .permalink {background: rgba(52,51,51,0);}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {background: rgba(0,0,0,0); border: solid #f8f8f0;}

ol#posts li.post .post_avatar{border-bottom: 0px solid #070707;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {border-top: 1px solid #070707; border-bottom: 1px solid #070707;}

#right_column a#dashboard_switch_blog_menu_current:hover {background: none;}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {background: none;}

form.dashboard_options_form {background-color: rgba(0,0,0,0.7); border-bottom: 1px solid #070707; border-bottom-left-radius: 10px 10px; border-bottom-left-radius: 10px 10px; border-bottom-right-radius: 10px 10px; border-bottom-right-radius: 10px 10px; border-top-left-radius: 10px 10px; border-top-left-radius: 10px 10px; border-top-right-radius: 10px 10px; border-top-right-radius: 10px 10px; color: #070707; display: block; font-size: 13px; margin-bottom: 25px; padding: 15px 22px;}

form.dashboard_options_form .option_container {border-top: 1px solid #070707;}

.no_posts_found, h1.dashboard_header {color: #3E4245;}

.answer_container {background-color: #070707; border-bottom: 0px solid #070707;}

#nav .nav_item .nav_menu {background-color: #070707; border-bottom: 2px solid #f8f8f0;}

#nav .nav_item .nav_menu a {border-top: 1px solid #f8f8f0;}

#auto_pagination_loader {background: none;border-top: 0px solid #f8f8f0;}}

@-moz-document url-prefix('http://www.tumblr.com/reblog/'), url-prefix('https://www.tumblr.com/reblog/')   {#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}}

@-moz-document url-prefix('http://www.tumblr.com/directory/'), url-prefix('https://www.tumblr.com/directory/')   {#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}}

@-moz-document url-prefix('http://www.tumblr.com/reblog/'), url-prefix('https://www.tumblr.com/reblog/')   {#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}}

@-moz-document url-prefix('http://www.tumblr.com/new/'), url-prefix('https://www.tumblr.com/new/')   {#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}}

@-moz-document url('http://www.tumblr.com/goodies') {#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}}

@-moz-document url-prefix('http://www.tumblr.com/edit/'), url-prefix('https://www.tumblr.com/edit/')   {#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}}
