/* First created: 25 Nov 2005 UTC
 * Last modified: Wed 05 Dec 2007 @ 13:14 UTC
 * Author: Soren Andersen (somian)
 * Note on application: the main font used depends on non-MSWindwoes
 * users having the 'mstcorefonts' or equivalent installed, for the
 * TTF font 'Georgia'. Change according to your tastes, of course.
 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  url-prefix(https://sourceforge.net/mailarchive/),
                url-prefix("http://sourceforge.net/mailarchive/")
{
   body , body>div#hd>div#topuser
     { background-color : rgb(48,48,48) !Important;  }
   body>div#hd>h1>a { background-color : #FFFFFF !Important; }

   div.topnav h2 *, h1, h3 {
       color : #FF602C !Important;
   }
   h3>a, h3>a:visited {
       color : #31A853 !Important
   }

  /* SourceForge.net does not *work well enough* for it to be
     required of us to have to suffer the stupid G*d-damn ads! */

   div#adbatch {  display: none !Important; }
   div#fad1p   {  display: none !Important; }
   div#fad1    {  display: none !Important; }

   table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"]
      tbody  {
                 font-family : georgia, serif  !Important;
                 font-size   : 125%            !Important;
                 background-color : rgb(2,2,2) !Important;
                            color : #B1B8A3    !Important;
      }
   table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"]
      tbody>a  {
                 font-family : georgia, serif !Important;
      }
 
   table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"]
      tbody>tr>td>table[width="100%"][border="0"]>tbody>tr>td>div.forum
             {
                 font-family : georgia, serif !Important;
                   color     : #272D42 !Important;

   }

   table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"]
      tbody>tr>td>table[width="100%"][border="0"]>tbody>tr>td[bgcolor="#DDDDDD"]
             {
                background-color : rgb(0,16,69)  !Important;
   }
   table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"]
      tbody>tr>td>table[width="100%"][border="0"]>tbody>tr>td>div.forum>div>b
             {
                background-color : rgb(0,16,69)  !Important;
                         padding : 2pt 26pt !Important;
                margin-top    : 3pt !Important;
	       	margin-bottom : 3pt !Important;
   }
   table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"]
      tbody>tr>td>table[width="100%"][border="0"]>tbody>tr>td>div.forum>div>b>a
             {
                           color : #2E9E92 !Important;
   }

   table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"]
      tbody>tr>td>table[width="100%"][border="0"]>tbody>tr>td>div.forum>small
             {
                           color : rgb(242,10,146) !Important;
   }

 /* customization that works with Greasemonkey */
   code.somian {
         display   : inline !Important;
         border    : none   !Important;
         font-family : "Deja Vu Sans Mono", monospace !Important;
          font-size  : 91% !Important;
         margin-left : 0px !Important;
         background-color : inherit;
                    color : #A1A8DD !Important;
   }

}
