css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://d.hatena.ne.jp/Gaius_Petronius/"){

html body div.hatena-body div.sidebar{
	margin-top:-50px!important;
}
h1{
	padding:10px 0px !important;
	font-family:"メイリオ","Meiryo",sans-serif !important;
}
.hatena-moduletitle{
	font-family:"メイリオ","Meiryo",sans-serif !important;
}
/*文字サイズ*/
p
,.sectionheader a
,.refererlist ul li a{
	font-size:15px!important;
	line-height:1.5 !important;
}
/*広告削除*/
.ad
,#google_afc_user{
	display:none!important;
}

/*リンク*/
/*一番上のメニュー*/
table#banner tbody tr td div center table tbody tr td a:hover
,table#banner tbody tr td form font input:hover{
	opacity:0.6!important;
}
h1 a:link{
	text-decoration:none!important;
	font-size:20px!important;
}
h1 a:visited{
	color:#BFBFBF!important;
	text-decoration:none!important;
}
h1 a:hover{
	color:#AFAFAF!important;
	text-decoration:none!important;
}
h2  a:link{
	font-size:16px!important;
}
h2  a:link
,ul.menu a:link{
	/*color:#FFFFFF!important;*/
}
h2  a:hover
,ul.menu a:hover{
	color:#DFDFDF!important;
}
a:link{
	/*color:#2F8EFF!important;
	color:#66A100!important;*/
}
a:visited{
	color:#9F9F9F !important;
}
a:hover{
	color:#FF9F2F!important;
}
div.sidebar div.hatena-module div.hatena-modulebody ul.hatena-sectioncategory li a
,html body div.hatena-body div.sidebar div.hatena-module a{
	/*color:#D6E2CE!important*/
}
div.sidebar div.hatena-module div.hatena-modulebody ul.hatena-sectioncategory li a:visited
,html body div.hatena-body div.sidebar div.hatena-module a:visited{
	color:#B5CBA1 !important;
	color:#9F9F9F !important;
}
div.sidebar div.hatena-module div.hatena-modulebody ul.hatena-sectioncategory li a:hover
,html body div.hatena-body div.sidebar div.hatena-module a:hover{
	/*color:#FFFFFF !important;*/
}
html body div.hatena-body div.sidebar div#hatena-bookmark-widget0.hatena-bookmark div.hatena-bookmark-widget-title a{
	color:#EFEFEF!important
}
a.prev:hover,
a.next:hover,
.body a:hover,
span.commentmessage a:hover{
	color:#FF9F2F!important;
	text-decoration:none!important;
}
.refererlist ul li a:hover,
.hatena-modulebody ul li a:hover{
	color:#FF9F2F!important;
	text-decoration:underline!important;
}

/*画像を真ん中に*/
p.sectionheader+p{
	/*text-align:center!important;*/
}
.hatena-modulebody .hatena-recentcomment{
	font-size:9px!important;
	overflow:hidden!important;
}
}
