@-moz-document url-prefix('http://nyheter24.se/filmtipset') {

#contentWrapper > TABLE > TBODY > TR:first-child > TD > TABLE > TBODY > TR:last-child > TD:first-child > TABLE > TBODY > TR:first-child > TD > SCRIPT:first-child + TABLE {
  margin-top: 2em !important;
  }

#contentWrapper > TABLE > TBODY > TR:first-child > TD > TABLE > TBODY > TR:last-child > TD:first-child > TABLE > TBODY > TR:first-child > TD > TABLE:last-child > TBODY > TR:first-child + TR + TR + TR + TR + TR + TR + TR + TR + TR + TR + TR {
  position: absolute !important;
  top: 60px !important;
  }
#contentWrapper > TABLE > TBODY > TR:first-child > TD > TABLE > TBODY > TR:last-child > TD:first-child > TABLE > TBODY > TR:first-child > TD > TABLE:last-child > TBODY > TR:first-child + TR + TR + TR + TR + TR + TR + TR + TR + TR + TR + TR > TD:first-child > H2 {
  margin-right: 5em !important;
 }

#contentWrapper > TABLE > TBODY > TR:first-child > TD > TABLE > TBODY > TR:last-child > TD:first-child > TABLE > TBODY > TR:first-child > TD > SCRIPT:first-child + TABLE > TBODY > TR:first-child + TR > TD:last-child > A,
#contentWrapper > TABLE > TBODY > TR:first-child > TD > TABLE > TBODY > TR:last-child > TD:first-child > TABLE > TBODY > TR:first-child > TD > SCRIPT:first-child + TABLE > TBODY > TR:first-child + TR > TD:last-child > IMG {
  position: absolute !important;
  top: 80px !important;
  left: 440px !important;
  border: 1px solid #666 !important;
 }

}
