css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://wiki.massgate.net/") {
/* Pretty printing styles. Used with prettify.js. */

.str { color: #fc3; }
.kwd { color: #c70; font-weight: bold; }
.com { color: #a96; font-style: italic; font-family: comic-sans; }
.typ { color: #fa0; }
.lit { color: #fa0; font-weight: bold; }
.pun { color: #fc3; }
.pln { color: #fa0; }
.tag { color: #080; }
.atn { color: #fa0; }
.atv { color: #fa0; }
.dec { color: #fa0; }
pre.prettyprint { padding: 0.5em; border: 1px dashed #850; overflow: auto; }

@media print {
  .str { color: #060; }
  .kwd { color: #006; font-weight: bold; }
  .com { color: #600; font-style: italic; }
  .typ { color: #404; font-weight: bold; }
  .lit { color: #044; }
  .pun { color: #440; }
  .pln { color: #000; }
  .tag { color: #006; font-weight: bold; }
  .atn { color: #404; }
  .atv { color: #060; }
}
}
