css:
@-moz-document domain("furaffinity.net") {
/*
	FurAffinity - Light and Shiny
	By SkippOtter
	http://userstyles.org/styles/42695
*/



body {
	background: url('http://img529.imageshack.us/img529/8756/gradientstripesinv.png') !important;
}
a {
color: #000 !important;
}
a:visited{
color: #444 !important;
}
.maintable {
background-color: #a7a7a7 !important;
}
.logo, td.logo > table > tbody > tr > td.alt1, .innertable, body>div>table.maintable{
      background: none !important;
}

.alt1, .alt1 A:LINK, .alt1 A:VISITED{
 color: #000 !important;
}

.alt1 A:HOVER{
	color: #790000 !important;
	text-decoration: underline;
}

ul.dropdown li a {
    background: url('http://img801.imageshack.us/img801/9414/gradientup.png');
    border-radius: 10px;
    -moz-border-radius: 10px;
    color: #000 !important;
}
ul.dropdown li a:hover {
    background: url('http://img152.imageshack.us/img152/9310/gradientdown.png');
}
ul.dropdown ul {
    background: none !important;
    filter: none !important;
    opacity: 1 !important;
}
ul.dropdown li {
    margin: 0 1px 0 1px !important;
}
ul.dropdown ul li {
    margin: 3px 0 0 0 !important;
}
ul.dropdown ul a:hover {
    background: url('http://img152.imageshack.us/img152/9310/gradientdown.png');
}

.tab{
	margin-left: 5px;
	margin-bottom: 3px !important;
}
.tab u, .tab a {
border-top-right-radius: 9px;
border-top-left-radius: 3px;
-moz-border-top-right-radius: 9px;
-moz-border-top-left-radius: 3px;
}
.tab u{
	background-color: #a7a7a7 !important;
	border-top: 1px solid #a7a7a7 !important;
	border-left: 1px solid #a7a7a7 !important;
	border-right: 1px solid #a7a7a7 !important;
	color: #fff !important;
}

.tab a{
	background-color: #cecece !important;
	border-top: 1px solid #a7a7a7 !important;
	border-left: 1px solid #a7a7a7 !important;
	border-right: 1px solid #a7a7a7 !important;
}

.cat{
	background: #a7a7a7 url('http://img703.imageshack.us/img703/1019/gradientacross.png') repeat-y !important;
	background-size: 100% !important;
	border: 2px solid #fff !important;
	color: #000 !important;
}

.container-comment td.cat{
	height: 0px !important;
	line-height: 0px !important;
	padding: 0;
	border: 0;
	background: none !important;
	background-image: none !important;
	border: none !important;
}
.lead{
	border-top: 2px solid #fff !important;
	border-bottom: 2px solid #fff !important;
}
.alt1 {
background-color: #fff !important;
}
.ldot{
	border-left: 1px dotted #777 !important;
}
.rdot{
	border-right: 1px dotted #777 !important;
}
.bdot{
	border-bottom: 1px dotted #777 !important;
}
.tdot{
	border-top: 1px dotted #777 !important;
}

.alt2{
	background-color: #777 !important;
}
.alt2 A:LINK, .alt2 A:VISITED{
	color: #000 !important;
}
.alt2 A:HOVER{
	color: #790000;
	text-decoration: underline;
}
#JSForm {
        background-color: #fff;
}
#JSMessage {
           background-color: #383838;
           border: 2px solid #a7a7a7;
           color: #000;
}
.footer,.footer A:LINK,.footer A:VISITED{
	color: #777 !important;
}
.footer A:HOVER{
	color: #790000;
	text-decoration: none;
}

.button{
	color: #000 !important;
	background-color: #a7a7a7 !important;
	border: 1px solid #777;
	margin: 1px;
}
#frontpage>table>tbody>tr>td.alt1, div#frontpage>table>tbody>tr>td.alt2{
	background: none !important;
}
#frontpage .cat{
color: #000 !important;
}
#frontpage .maintable {
margin-bottom: 20px;
	background: rgba(255,255,255,0.55) !important;
}

#frontpage .alt2{
   background: none !important;
}

#frontpage .alt1{
   background: none !important;
}
#frontpage .alt2 .maintable {
margin-bottom: 5px !important;
}
#frontpage .alt2 br {
display: none;
}


.container-comment{
	border: 0px !important;
	background-color: #fff;
}
.container-comment TD,
.container-comment TH{
	color: #000 !important;
}

.container-comment TH{
	background-color: #a7a7a7 !important;
}

.container-comment TD.cat A{
	background-color: #888 !important;
}

.textarea, .textbox {
      background-color: #cecece !important;
           border: 2px solid #a7a7a7 !important;
           color: #000 !important;

}
.textarea[id='JSMessage'], .textbox[name='to'], .textbox[name='subject'] {
       width: 98% !important;
}
 .textbox[type='checkbox']{
 width: 13px !important;
 }
div#messagecenter-submissions, div#messagecenter-other {
	color: #000 !important;
  background: rgba(255, 255, 255, 0.5) !important;
  padding-bottom: 5px !important;
  padding-top: 5px !important;
  border-radius: 10px;
  -moz-border-radius: 10px;
}
div#messagecenter-submissions>form#messages-form>ul.messages-stream>li>table.checked {
	background-color: #bbb !important;
}

div#messagecenter-submissions>form#messages-form>ul.messages-stream>li{
 background: rgba(255,255,255,1);
 border-radius: 12px;
 -moz-border-radius: 12px;
 box-shadow: inset 0px 0px 7px 2px #bbb;
 -moz-box-shadow: inset 0px 0px 7px 2px #bbb;
 padding: 5px !important;
}
div#messagecenter-submissions h3.date-divider{
	border-bottom: 1px solid #a7a7a7 !important;
}

div#messagecenter-submissions div.navigation a{
	color: #313131 !important;
	border: 1px solid #a7a7a7 !important;
}
div#messagecenter-submissions div.actions .remove-checked{
	background-color: #ADADFF !important;
	color: #313131 !important;
}
div#messagecenter-submissions div.actions .remove-nuke{
	background-color: #FFAFAF !important;
	color: #313131 !important;
}
div#messagecenter-other a {
      color: #000 !important;
}
div#messagecenter-other fieldset h3 input{
	background-color: #FFAFAF !important;
	color: #cecece !important;
}
div#messagecenter-other ul.message-stream table.checked{
	background-color: #ddd !important;
}
div#messagecenter-other ul.message-stream li.checked{
	background-color: rgba(194,194,194, 0.5) !important;
}
div#messagecenter-other ul.message-stream li.hover{
	background-color: rgba(119,119,119, 0.5) !important;
}
.checkbox {
    background: none;
}
div.checkbox:hover {
  background-color: #bbb !important;
}
div#messagecenter-other ul.message-stream li em {
border-bottom-color: #777;
}

.popup_date {
border-bottom-color: #a7a7a7 !important;
}

.menu li ul li{
  float: none;
	text-align: center !important;
	border: none !important;
  background: url('http://img801.imageshack.us/img801/9414/gradientup.png') !important;
	cursor: pointer;
	padding: 3px !important;
	width: 110px !important;
	margin-top: 3px !important;
	border-radius: 8px;
	-moz-border-radius: 8px;

}
.menu li ul li:hover{
	float: none;
	text-align: left;
	border: 1px dotted #919bad;
 background: url('http://img152.imageshack.us/img152/9310/gradientdown.png') !important;
	color: #fff;
	padding: 3px;
	cursor: pointer;
}

.menu li ul li A:LINK{
	text-decoration: none;
	color: #000 !important;
}
.menu li ul li A:VISITED{
	text-decoration: none;
	color: #000 !important;
}
.menu li ul li A:HOVER{
	color: #790000;
	text-decoration: none;
}

.bbcode_quote{
	border: 1px dashed #777 !important;
	background-color: #eee !important;
}

#pms-form>table {
background: rgba(255,255,255,0.5) !important;
color: #000 !important;
}
#pms-form>table[cellpadding='10']{
padding: 5px !important;

}

#pms-form>table>tbody>tr>td.alt1[rowspan] {
background-color: #777 !important;
border-radius: 12px;
-moz-border-radius: 12px;
box-shadow: inset 0 0px 30px 2px #eee;
-moz-box-shadow: inset 0 0px 30px 2px #eee;
}
form#pms-form>table.maintable.container {
border-collapse: collapse !important;
}

form#pms-form>table.maintable.container .alt1 {
border-bottom: 1px solid #000 !important;
}

.personal-messages  a.high_prio,
.viewmessage a.high_prio{
    color: #B38C95 !important;
}
.personal-messages  .priority_high,
.viewmessage .priority_high{
    background-color:#EFBAC6  !important;
    color: #000 !important;
}
.personal-messages a.medium_prio,
.viewmessage a.medium_prio{
    color: #759EB9 !important;
}
.personal-messages .priority_medium,
.viewmessage .priority_medium{
    background-color: #9CD7F7 !important;
        color: #000 !important;
}
.personal-messages a.low_prio,
.viewmessage a.low_prio{
    color: #87A769 !important;
}
.personal-messages .priority_low,
.viewmessage .priority_low{
    background-color: #B5DF8C !important;
        color: #000 !important;
}

    #ucp-site-settings table td,
    #ucp-site-settings table th{
	border-bottom: 1px solid #777 !important;
    }

 div#messagecenter-submissions div.info small a{
	margin-top: 2px;
	font-size: 10px !important;
	display: block;
	color: #777 !important;
	font-style: italic;
}

div#messagecenter-other ul.message-stream li.section-controls,
div#messagecenter-other #messages-watches ul.message-stream li.section-controls{
	border-bottom: 1px solid #a7a7a7 !important;
}
   #latest-submissions dd a.active img,
#latest-favorites   dd a.active img{
	border: 1px dotted #a7a7a7 !important;
}

#sessions-form table.container tr.hover td,
#activity-periods-list table.container tr.hover td,
.personal-messages tr.hover td{
	background-color: #888 !important;
}
#sessions-form table.container tr.checked td,
#activity-periods-list table.container tr.checked td,
.personal-messages tr.checked td{
	background-color: #aaa !important;
}

#description_popup{
	border: 1px solid #777 !important;
	background: rgba(255,255,255,0.85)  !important;
	padding: 5px !important;
 border-radius: 12px;
 -moz-border-radius: 12px;
	box-shadow: 3px 3px 7px 0px black;
	-mozbox-shadow: 3px 3px 7px 0px black;
}
#description_popup h5{
	background-color: #cecece  !important;
	color:  #444 !important;
  border-top-right-radius: 5px;
  border-top-left-radius: 5px;
  -moz-border-top-right-radius: 5px;
  -moz-border-top-left-radius: 5px;
}

#description_popup span{
	border-top: 1px solid #fff  !important;
	padding: 5px;
	color: #000  !important;
}


#gallery .alt1,
#gallery .maintable,
#gallery .cat,
#scraps .alt1,
#scraps .maintable,
#scraps .cat,
#favorites .alt1,
#favorites .maintable,
#favorites .cat
 {
  background: none !important;
}

div#gallery>table.maintable,
div#scraps>table.maintable,
div#favorites>table.maintable
 {
background: rgba(255,255,255,0.5) !important;
}



#browse .alt1,
#search .alt1 {
  background: none !important;
}
#browse .maintable,
#search .maintable   {
    background: rgba(255,255,255,0.5) !important;
}
#browse .button {
background: #000 !important;
color: #fff !important;
}

#browse .cat[width="50%"]{
background: none !important;
border: none !important;
}


#search-form>fieldset, #section-help fieldset, #section-extended fieldset {
background: #fff !important;
}

div#stats-submission table.submissions td.preview,
div#stats-submission table.submissions td.info,
div#stats-submission table.submissions td.desc{
	border-bottom: 1px solid #a7a7a7 !important;
}

div#stats-submission table.submissions td.desc{
	border-left: 1px solid #a7a7a7 !important;

}

table#usercp-menu .alt1, table#usercp-menu{
        background: none !important;
        border: none !important;
        border-collapse: collapse;

}

table#usercp-menu {
margin-bottom: 10px !important;
margin-top: 5px !important;
}

table#usercp-menu>tbody>tr:last-child {
      visibility: collapse;
      position: absolute !important;
      z-index: 597 !important;
      background: none !important;
      border: none !important;
      width: 946px;

}

    table#usercp-menu .cat{
    border: none !important;
    }

table#usercp-menu:hover>tbody>tr:last-child {
      visibility: visible !important;
       position: absolute !important;
      z-index: 599 !important;
      width: 946px;
      background: rgba(255,255,255,0.85) !important;
      border-bottom-right-radius: 12px;
      border-bottom-left-radius: 12px;
      -moz-border-bottom-right-radius: 12px;
      -moz-border-bottom-left-radius: 12px;
      border-bottom: 2px solid #a7a7a7 !important;
      border-left: 2px solid #a7a7a7 !important;
      border-right: 2px solid #a7a7a7 !important;

}

table#usercp-menu:hover .alt1 {
      background: none !important;
      padding-right: 11px !important;
      padding-left: 11px !important;
}


table#usercp-menu ~ br {
display: none;
}

    #submission .prev,
#submission .next{
 border-radius: 8px;
 -moz-border-radius: 8px;
 padding: 4px 7px 5px 7px !important;
 border: none !important;
  background: url('http://img801.imageshack.us/img801/9414/gradientup.png') !important;
}

#submission .prev:hover,
#submission .next:hover{
 background: url('http://img152.imageshack.us/img152/9310/gradientdown.png') !important;
 color: #000 !important;
}


img#submissionImg {
max-width: /*[[maxwidth]]*/;
/*
-1 !
1024 979px
1152 1107px
1280 1235px
1366 1321px
1440 1395px
1600 1565px
1680 1635px
1920 1875px
*/
}
}
