@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("ogame.com.es")

{

/* --------- COMPACTADOR DE BATALLAS script --------- */

/*Fecha*/
#combatreport font[style="font-size: 8pt;"]
{color: #777777 !important; font-size: 11px !important;}

/* Cuerpo */
#combatreport #codHTML
{color: #aaaaaa !important; font-size: 13px !important; line-height: 115%;}

/*Atacantes-Defensores Titulos*/
#combatreport font[style="font-size: 18pt;"]
{color: #AAAAAA !important; font-size: 0px !important;}

/* Nicks jugadores (A/D) */
#combatreport font[color="#00ff40"] font[style="font-size: 14pt;"]
{color: #00FF00 !important; font-size: 18px !important; line-height: 150%;}
#combatreport font[color="#ed7010"] font[style="font-size: 14pt;"]
{color: #AAAAFF !important; font-size: 18px !important; line-height: 150%;}

#combatreport font[color="#00ff40"] font[style="font-size: 14pt;"]:before 
{content: "â—„ " !important;}
#combatreport font[color="#00ff40"] font[style="font-size: 14pt;"]:after 
{content: "â–º" !important;}
#combatreport font[color="#ed7010"] font[style="font-size: 14pt;"]:before 
{content: "â—„ " !important;}
#combatreport font[color="#ed7010"] font[style="font-size: 14pt;"]:after 
{content: "â–º" !important;}

/* Total naves */
#combatreport font[color="#00ff40"] {color: #00DD00 !important;  font-weight: bold !important;}
#combatreport font[color="#ed7010"] {color: #8888DD !important;  font-weight: bold !important;}

/* Naves perdidas */
#combatreport font[color="#00dddd"] {color: #aa4444 !important; font-size: 12px !important;}

#combatreport font[color="#00dddd"]:before 
{content: "(" !important;}
#combatreport font[color="#00dddd"]:after 
{content: ")" !important;}

/* Perdidas */
#combatreport font[color="#ffcc66"] {color: #FF0000 !important; font-weight: bold !important; font-size: 16px !important;}

/* Escombros, renta parcial */
#combatreport font[color="#9e7625"] {color: #c39977 !important; font-weight: bold !important; font-size: 14px !important;}

/* Mensaje ganador */
#combatreport font[style="font-size: 14pt;"] font[color="#aa96ed"] {color: #cccccc !important; font-size: 12px !important;}


/* Robo/Escombros/Reciclas */
#combatreport font[color="#f0ec64"] font[style="font-size: 14pt;"]
{color: #33ff33 !important; font-weight: bold !important; font-size: 13px !important;}
#combatreport font[color="#e4123c"] font[style="font-size: 14pt;"]
{color: #fdc608 !important; font-weight: bold !important; font-size: 13px !important;}
#combatreport font[color="#e4123c"]
{color: #ffffff !important; font-weight: bold !important; font-size: 13px !important;}

/* Perdidas parciales y Rentabilidad (titulos) */
#combatreport font[color="#aa96ed"]
{color: #FFFFff !important; font-weight: normal !important; font-size: 13px !important;}

#combatreport font b font[color="#e4123c"]
{color: #EE3333 !important; font-size: 18px !important;}

}
