css:
@-moz-document url-prefix('http://www.giveawayoftheday.com/forums/') {
body {background-color:#EFEDFC;}
#wrapper {background: none; background-color:#EFEDFC;}
#header {background: none; height:75px;}
#front-page #hottags {display:none;}
#front-page #discussions {margin-left: 0px; width: 760px;}
#header h1 {background-color:#ccc; position:relative; right:0px; top:0px; text-align:left; z-index:1;}
#header div.search {-moz-border-radius-bottomleft: 0px; -khtml-border-bottom-left-radius: 0px; -webkit-border-bottom-left-radius: 0px; border-bottom-left-radius: 0px;}
#header div.search input {width:200px;}
#header div.search input.submit {width:70px;}
.login {color:#555; padding:5px; top:36px; background-color:#aaa; width:463px; height:19px; -moz-border-radius-bottomleft: 4px; -khtml-border-bottom-left-radius: 4px; -webkit-border-bottom-left-radius: 4px; border-bottom-left-radius: 4px;}
.login a {color:#fff;}
form.login div {position:absolute; top:0px; right:0px; margin-top:-36px; margin-right:-278px; z-index:999;}
div.remember label {position:absolute; top:37px; left:-550px;}
ul#profile-menu {position:absolute; top:0px; left:0px; margin-top:-7px; margin-left:-3px;}
div#discussions {margin-top:-25px; font-size:125%;}
div#footer {font-size:125%;}
tr.sticky td {visibility:hidden;}
tr.sticky big {visibility:visible; float:left; padding:5px; font-weight:bold; font-size; 24px;}
tr.super-sticky big {visibility:visible; float:left; margin-top:-15px; padding:5px; font-weight:bold; font-size; 24px;}
tr.sticky td.num {visibility:visible;}
div.admin {visibility:hidden;}
div.admin a {visibility:visible;}
div.admin form#topic-move {visibility:visible; display:inline-block;}
div.infobox {visibility:hidden;}
h2.topictitle {visibility:visible; position:absolute; top:0px; left:0px; font-size:16px; font-weight:bold;}
ol#thread {margin-top:-25px;}
p#post-form-tags-container {display:none;}
.threadpost {background-color: #ddd;}
div.search-results ol {background-color: #ddd;}
ul.topicmeta li {display:none;}
ul.topicmeta li#favorite-toggle {visibility:visible; display:block; position:absolute; top:-75px; right:5px; z-index:997; color:#fff; font-size:16px;}
ul.topicmeta li#favorite-toggle a {color:#fff; font-weight:bold;}
span#topic_labels {display:none;}
span#topic_posts {display:none;}
span#topic_voices {display:none;}
div#topic-tags {display:none;}
div#viewdiv {display:none;}
div.members_online {display:none;}
}

@-moz-document url-prefix('http://www.giveawayoftheday.com/forums/topic/') {
div.nav {position:absolute; top:-5px; right:0px;}
div.nav span {font-size:9px; font-weight:normal;}
div.nav a {font-size:9px; font-weight:normal;}
span.dots {color:#000;}
a.next {color:#000;}
a.prev {color:#000;}
}

@-moz-document url-prefix('http://www.giveawayoftheday.com/forums/forum/') {
div.nav {position:absolute; top:-5px; right:0px;}
div.nav span {font-size:9px; font-weight:normal;}
div.nav a {font-size:9px; font-weight:normal;}
span.dots {color:#000;}
a.next {color:#000;}
a.prev {color:#000;}
tr.super-sticky big {visibility:visible; float:left; padding:5px; margin-top:0px; font-weight:bold; font-size; 24px;}
}

@-moz-document url-prefix('http://www.giveawayoftheday.com/forums/profile/') {
div.nav {position:absolute; top:-5px; right:0px;}
div.nav span {font-size:9px; font-weight:normal;}
div.nav a {font-size:9px; font-weight:normal;}
span.dots {color:#000;}
a.next {color:#000;}
a.prev {color:#000;}
}

@-moz-document url('http://www.giveawayoftheday.com/forums/') {
div.nav {position:absolute; top:15px; left:175px;}
div.nav span {font-size:12px; font-weight:bold;}
div.nav a {font-size:12px; font-weight:bold;}
span.dots {color:#000;}
a.next {color:#000;}
a.prev {color:#000;}
}

@-moz-document url-prefix('http://www.giveawayoftheday.com/forums/page/') {
