css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {
	* {
		background: none !important;
	}

	body {
		color: #303030 !important;
		background-color: #ececec !important;
		font-size: 10pt !important;
		font-family: sans-serif !important;
	}

	a, s {
		color: #144495 !important;
	}

	b, i, pre, code {
		color: #303030 !important;
	}

	option {
		color: #303030 !important;
		background-color: #ececec !important;
	}

	/* wiki */

	div#nav-menu, div#nav-search {
		background-color: #c5c5ca !important;
	}
	div#nav-search input {
		color: #303030 !important;
		background-color: #ececec !important;
	}
	div.tab-item {
		background-color: #c5c5ca !important;
	}
	div#contents {
		background-color: #e5e5ea !important;
		color: #303030 !important;
	}
	div#contents h1, div#contents h2, div#contents h3 {
		color: #000000 !important;
        }

	/* forum */

	table.nav td {
		border: none !important;
	}
        table.nav a {
                text-decoration: none !important;
        }
	div.nav {
		padding: 0 5px !important;
	}
	div.nav p {
		margin: 0.5em 0 !important;
	}
	table.message-table {
		background-color: #c5c5ca !important;
	}
	table.message-table thead th, table.message-table tfoot td {
		background-color: #c5c5ca !important;
	}
	table.message-table tbody td {
		background-color: #e5e5ea !important;
		padding: 1px 5px !important;
	}
	table.message-table tbody tr:hover td {
		background-color: #ffffff !important;
	}
	table.message-table tbody td a:visited {
		color: #606060 !important;
	}

	/* thread */

	div#dw {
		display: none !important;
	}
	div.msg {
		background-color: #e5e5ea !important;
		border: 1px solid #282828 !important;
		padding: 0 !important;
		-moz-border-radius: 5px !important;
	}
	div.msg:target {
		border: 2px solid #c8c8c8 !important;
	}
	div.msg div.title {
		background-color: #c5c5ca !important;
		padding: 0 10px 1px 10px !important;
		margin: 0 !important;
	}
	div.msg div.userpic {
		padding: 0 !important;
		margin-left: 5px !important;
	}
	div.msg div.msg_body {
		margin-left: 170px !important;
		margin-right: 10px !important;
		padding: 0 0 2em 0 !important;
	}
	div.msg div.msg_body h1, div.msg div.msg_body h2 {
		font-size: larger !important;
		margin: 0.5em 0 !important;
	}
	div.msg div.msg_body p {
		margin: 0.5em 0 !important;
	}
	div.msg div.msg_body div.sign {
		float: left;
		margin: 0.5em 0 !important;
	}
	div.msg div.msg_body div.reply {
		float: right;
		margin: 1em 0 0.5em 0 !important;
	}

	/* post */

	div#recaptcha_area {
		background-color: #000000 !important;
	}

        #commentForm {
		font-size: 10pt !important;
        }

	/* main page */

	div.boxlet, marquee {
		background-color: #e5e5ea !important;
		-moz-border-radius: 5px !important;
	}
	div.boxlet h2 {
		background-color: #c5c5ca !important;
	}
	div.news div {
		background-color: #ececec !important;
		border: none !important;
	}
}

@-moz-document url-prefix("http://www.linux.org.ru/dw.jsp") {
	body {
		background-color: #e5e5ea !important;
	}
}
