css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org"), domain("secure.wikimedia.org") {

/*navigation rebuild*/

div.portal {
 background-color:#FFF !important;
 background-image: -moz-repeating-linear-gradient(top, #CCC , #AAA 22px , rgba(0,0,0,0) 22px, rgba(0,0,0,0) 100%),  -moz-linear-gradient(top, #CCC, #AAA) !important;
 -moz-border-radius: 8px;
 margin: -4px 9px 10px 11px !important;
 -moz-box-shadow:  1px 1px 6px rgba(0,0,0,0.5), inset 0.5px 1px 1px #fff, inset -1px -1px 1px rgba(0,0,0,0.5);

}

#p-navigation {


 background-image:  -moz-linear-gradient(top, #CCC, #AAA) !important;
 -moz-border-radius: 8px;

}
div.portal a:hover {
 text-shadow: none;

}
div.collapsed h5 {
 color: black !important;

}
div.expanded h5{

 
}
#simpleSearch {
 /*-moz-box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5) inset;*/
 /*border: 1px solid #777 !important;*/
 border: none !important;
 z-index: 1100 !important;
 -moz-border-radius: 6px;
}
#simpleSearch button#searchButton {
 padding-top: 4px !important;
}
#ca-watch.icon a, #ca-unwatch.icon a{
  background-image: none !important;
 padding-top: 12px !important;
}
#ca-watch.icon a:before {
 content: "\2606";
 color:#666;
 font-size:15pt;

}
#ca-unwatch.icon a:before {
 content: "\2605";
 color:#666;
 font-size:18pt;
}

.suggestions{
 -moz-box-shadow: 0 0 5px rgba(0,0,0,1);
 background: #333 !important;
 border: 2px solid #FFF !important;
 top: 43px !important;
 right: 3px !important;
 min-width: 226.5px !important;
 -moz-border-radius:  10px;

}
.suggestions-results{
 background: transparent !important;
 margin-top: 35px !important;
 border-right: none !important;
 border-left: none !important;
 border-bottom: none !important;
 color: white !important;
 border-top: none !important;

}
.suggestions-result{
 color: white !important;
 padding: 2px 5px 0 5px !important;
}
.suggestions-result-current{
 background: -moz-linear-gradient(top, rgba(0,0,255,0), rgba(0,0,255,1), rgba(0,0,255,0)) !important;
 background: none !important;
 color: white !important;
 -moz-box-shadow: 0 0 15px #0080FF inset;
}
.suggestions-special{
 background: transparent !important;
 -moz-border-radius: 0px 0px 8px 8px;
 color: white !important;
 border: none !important;
 padding: 5px !important;
}
.suggestions-result-current .special-label, .suggestions-result-current .special-query{
 color: white !important;
}
.special-query{
 color: white !important;
}


/*linen background*/

body {

 background:  -moz-radial-gradient( 70px 60px , circle closest-corner, rgba(250,250,250,0.7) 0%, rgba(250,250,250,0) 100% ) no-repeat , -moz-linear-gradient(top, rgba(150,150,150,0.5), rgba(40,40,40,0.8)) fixed, url(http://i56.tinypic.com/2003cwl.jpg) !important;


}
#content, #pt-login, #pt-userpage {
 background-image: none !important;

}

#pt-prefswitch-link-on, #pt-prefswitch-link-anon {
 display: none !important;
}
div.vectorTabs, div.vectorTabs ul, div.vectorTabs li:not(#ca-watch) a, div.vectorMenu h5 a{
 background-image: none !important;
}
#mw-head, #mw-head-base, #mw-panel, #mw-page-base {
 background: none !important;
 background-color: transparent !important;
}

#page-base, #head-base, #footer {
 background-color: transparent !important;
 background-image: none !important;
}
div.vectorTabs li.selected {
 background-image:none !important;
 background: transparent !important;
 text-shadow: 0 0 5px #0080FF;
}
div.vectorTabs ul li {
 background: none !important;
 background-image: none !important;
 color: black !important;
}
div.vectorTabs ul li span {
 color: black !important;
}

/*rounded Corners*/

#hauptseite .inhalt  {
 -moz-border-radius-bottomright: 12px;
 -moz-border-radius-bottomleft:12px;
}
#hauptseite h2 {
 -moz-border-radius-topleft: 12px;
 -moz-border-radius-topright: 12px;

}
#toc, #mw-revisiontag, .toccolours, #catlinks, .catlinks {
 -moz-border-radius: 12px !important;
}
#content table {
 -moz-border-radius: 5px;
}
#content{
 -moz-border-radius: 12px 0 0 12px;
}
.catlinks {
 padding: 5px 15px !important;
}
table.taxobox th, table.palaeobox th {

}
table.taxobox, table.palaeobox {

}

/*toccolours box shade*/
.toccolours {
 -moz-box-shadow: 1px 1px 4px rgba(0,0,0,0.5)
}

/*thumb box shade*/

div.thumbinner {
 background: none !important;
 border: none !important;
 padding: 10px !important;
}
.thumbimage {
 -moz-box-shadow: 1px 1px 6px rgba(0,0,0,0.5);
 border: 3px solid white !important;
 -moz-border-radius: 3px;
}
div.thumb {
 background-color: #FFF !important;
}

/*shows links if hover paragraph*/

a {
 color: black !important;
}
a { outline: 0 !important; }

#content p:hover a, #content ul:hover a, #content ol:hover a, #content tbody:hover a, p:hover a.new{
 text-shadow: 0 0 4px violet;
 text-decoration: none !important;
}
a:hover {
  text-shadow: 0 0 4px #0080FF !important;
  text-decoration: none !important;
}
a.new:hover {
 text-shadow: 0 0 5px red;
 text-decoration: none !important;
}

/*floating references*/

 ol.references > li:target {
    color: #FFF !important;
    position: fixed !important;
    bottom: 5px !important;
    margin-left: -20px !important;
    padding: 2px 10px 2px 10px !important;
    list-style-position: inside !important;

    max-height: 49% !important;
    overflow: hidden !important;
    background: rgba(0,0,0,0.8) !important;
    border: 2px solid #FFF;
    -moz-border-radius: 10px;
    -moz-box-shadow: 2px 2px 10px rgba(0,0,0,0.5);
  }
   ol.references > li:target a {
    color: #FFF !important;
  }

}
