css:
body
  {background-color: #222 !important
  ;font-family: 'Segoe UI' !important
  ;color: #AAA !important}
table
  {background-color: #222 !important}
tr
  {border-bottom-color: #222 !important;}
p,
.profile h3
  {color: #CCC !important}
#header_categories ul li a.active
  {background-color: #222 !important;
  color: #CCC !important}


div > [id="header_icons"],
div > [class="twitter-share-button twitter-count-none"],
.FBConnectButton,
.fb_button_text
  {display: none !important}
div > [id="header"]
  {background-color: white !important}
div > [class="drigg-order"],
div > [class="storydata"],
.tagging-curtags-wrapper,
.preview .node
  {background-color: #222 !important}
div > [class="comment even"]
  {background-color: #2a2a2a !important;
  border-color: #333 !important}
div > [class="comment odd"],
div > [class="vud-widget vud-widget-hdvcomment"],
div > [class="picture"]
  {border-color: #333 !important}
div > [class="content"]
  {color: #CCC !important}
div > [class="node ntype-drigg odd clearfix"],
div > [class="node ntype-drigg even clearfix"],
div > [class="primary drigg-article-bottom-menu"]
  {border-bottom-color: #333 !important}
div > [class="pager"]
  {color: #CCC !important}


ul.tabs.secondary li {
  border-right-color: #333 !important}
tr.even,
thead.tableHeader-processed th {
  background-color: #333 !important}
ul.primary.drigg-article-bottom-menu li.active a.active,
ul.tabs.primary li.active a.active {
  background-color: #333 !important;
  border-color: #333 !important}
ul.primary.drigg-article-bottom-menu li  a,
ul.tabs.primary li  a {
  background-color: #2a2a2a !important;
  border-color: #333 !important}
.block ul li,
.block ol li,
.tabs.primary,
.profile h3,
ul.tabs.secondary li.active a.active,
ul.tabs.secondary {
  border-bottom-color: #333 !important}
fieldset {
  border-color: #333 !important}
a.active {
  color: #CCC !important;}
.karma_score,
li.promoted span.score {
  color: #888 !important;
  background-image: none !important;
  background: #202730 !important;
  background: -moz-linear-gradient(top, #2a2a2a, #202730) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#2a2a2a), to(#202730)) !important;
  text-shadow: 0 2px 0 #333 !important;
  border-width: 1px !important;
  border-color: #2a2a2a !important;
  border-style: solid !important}
.teaser_links {
  background-image: none !important}
#content_center div.pager strong {
  border: 1px solid #333;
  padding: 0.1em 0.5em;
  margin: 0 1px;
  color: #CCC;
  background-color: #333;}
#content_center div.pager a {
  border: 1px solid #333;
  padding: 0.1em 0.5em;
  margin: 0 1px;
  color: #CCC;}
