css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://us.f506.mail.yahoo.com/ym/"), url-prefix("http://us.mc506.mail.yahoo.com/mc/") {
/* Author: somian
 * First created: Fri Nov 30 10:39:45 UTC 2007
 * Last modified: Mon Nov 24 11:04:25 UTC 2008
 */
    #northbanner>iframe , div#northbanner , #nwad , #swads
       {  display: none !Important; }

    #ygmabot
       {  display: none !Important; }

    body { background-color: #664C16 !Important;
	              color: #9C6F14 !Important; }

    /* Main table */
    table#datatable>tbody>tr , table.tbldata>tbody>tr
       {  color: #FFFFFF !Important }

    table#datatable>tbody>tr.msgnew , table.tbldata>tbody>tr.msgnew
       {  background-color: #69AD69 !Important; }

    table#datatable>tbody>tr.msgold , table.tbldata>tbody>tr.msgold
       {  background-color: #7DAD69 !Important; }


    /* Reading messages */
    td.label~td
       {             color: #68CC6A !Important;
	         font-size: large   !Important;
       }
    td.label~td>a
       {
	         font-size: smaller !Important;
       }


    /* Navigation strips at top and bottom */
    div.contentnav
       {
	  border: #858585 solid 1px  !Important;
	  font-weight: bolder        !Important;
	  font-style:  oblique       !Important;
	  padding: 2px 6px 4px 6px   !Important;
	  background-color: #000000  !Important;
	             color: #4ECFCC  !Important;
       }
    div.contentnav
       {  font-size: 10pt !Important; }

    div.contentnav>span>a , div.contentnav>span>span>a
       {              color: #FF8B41 !Important;
           border: 1px solid #FF8B41 !Important;
       }
     /* Possible todo ... span.last */

    table[cellpadding="0"][cellspacing="0"][border="0"][width="100%"]
       {  background-color: #99855D !Important; }

    /* Sidebar navigation content */
    div#customfolders , div#defaultfolders , div#folderlist , div#folderlist>*>*>li
       {  background-color: #99855D !Important;
	             color: #EE3B3B !Important; }

}
