css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.ota-suke.jp/"),
	url("http://www.ota-suke.jp/") {

.header_ad
,.news_container>div[style="margin-bottom: 7px;"]
,#sidebar_news>div[style="margin-bottom: 10px;"]

/*特選フィギュア（左カラム）*/
,.side_ads

/*売れ筋アイテム（右カラム）*/
,.news_rightbar_contents .rightbar:nth-of-type(3)

,#content>br
,#content>div[align="center"]
,#sidebar>div[style="margin-bottom: 10px;"]
,.tags_box_main>div+div
,.report_list_box+.line+div[align="center"]
,.banner_left
,.banner_right
,.product_review+div[style="margin-left: 20px;"]
,.event_report>div[style="margin-bottom: 5px;"]
,.event_report+div[style="margin-left: 20px;"]
,.photo_navi+div

,div#page div#content_news div.news_container div#sidebar_news div.leftbar
,div#page div#content_news div.news_rightbar_contents div.rightbar

,html body div#page div#content_news div.news_container>div:first-child[style="margin-bottom:7px;"]
,html body div#page div#content_news div.news_rightbar_contents a[href="http://ac3.i2i.jp/bin/getslink.php?00207203&&&http://acc.i2i.jp/?aci"]

,html body div#page div#content_news div.news_container div.news_main_contents div.news_box_main div.news_info_link+div[style="margin:20px 0px 20px 0px; text-align:center;"]
{
	display:none !important;
}

/*ロゴ移動*/
.header_logo{
	position:absolute!important;
	top:38px!important;
	margin-left:-5px!important;
	width:160px!important;
	height:70px!important;
}
.header_logo img{
	width:160px!important;
	height:70px!important;
}

#sidebar_news{
	margin-top:80px!important;
}

*{
	font-family:
	"ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro",
	"メイリオ", "Meiryo",
	"Osaka",
	"ＭＳ Ｐゴシック","ＭＳ ＰGothic",
	sans-serif !important;
}

.news_note1{
	font-size:14px !important;
}
.news_relative b {
	font-weight:normal!important;
}

/*ナビ部分*/
.header_navi{
	background-color:#FFCFF0!important;
	border: solid 1px #FF7FD8!important;
	margin:0!important;
}
.header_navi ul li a{
	color:#FF5FCF!important;
	margin:0!important;
	padding:5px 5px 4px 5px!important;
}
.header_navi ul li a:hover{
	background-color:#FF9FE2!important;
	color:#FFFFFF!important;
}
.header_navi_mainmenu{
	margin:0!important;
}

/*リンク*/
a:link{
	color:#2F7BFF!important;
}
a:visited{
	color:#FF8D4F!important;
	color:#FF7FCC!important;
}
a:hover{
	color:#FF7FCC!important;
	color:#FF8D4F!important;

	text-decoration:underline!important;
	text-decoration:none!important;
}
a img:hover{
	opacity:0.7!important;
}
.header_navi a:hover{
	text-decoration:none!important;
}
/*ニュース記事ランキング*/
.rightbar a{
	text-decoration:none!important;
}
/*記事タイトル*/
.news_title{
	background-color:#FFDFF4!important;
	border-top: solid 2px #FF7FD8!important;
	border-bottom: solid 2px #FF7FD8!important;
}
.news_title h1{
	color:#FF7FD8!important;
}
h2
,.rss_info+div b{
	color:#5F5F5F!important;
}
.news_box_main{
	border-top: none!important;
}

/*トップページ*/
.info_top_pickup_title{
	background-color:#FFDFF4!important;
	border-top: solid 1px #FF7FD8!important;
	border-bottom: solid 1px #FF7FD8!important;
	border-left: solid 1px #FF7FD8!important;
	border-right: solid 1px #FF7FD8!important;
}
.info_top_pickup_box{
	border: solid 1px #FF7FD8!important;
	border-top: none!important;

}
.info_report_list_right{
	color:#5F5F5F!important;
}
}

@-moz-document url("http://www.ota-suke.jp/"),
	url-prefix("http://www.ota-suke.jp/special/"),

	url-prefix("http://www.ota-suke.jp/list/event/report/"),
	url-prefix("http://www.ota-suke.jp/event/review/"),

	url-prefix("http://www.ota-suke.jp/list/product/review/"),
	url-prefix("http://www.ota-suke.jp/product/review/"),

	url-prefix("http://www.ota-suke.jp/list/game/report/"),
	url-prefix("http://www.ota-suke.jp//game/review/"),

	url-prefix("http://www.ota-suke.jp/list/figure/report/"),
	url-prefix("http://www.ota-suke.jp/figure/review/"),

	url-prefix("http://www.ota-suke.jp/list/cos/report/"),
	url-prefix("http://www.ota-suke.jp/cos/review/"),

	url-prefix("http://www.ota-suke.jp/list/idol/report/"),
	url-prefix("http://www.ota-suke.jp/idol/review/"),

	url-prefix("http://www.ota-suke.jp/list/A-POP/report/"),
	url-prefix("http://www.ota-suke.jp/A-POP/review/"),

	url-prefix("http://www.ota-suke.jp/list/comic/report/"),
	url-prefix("http://www.ota-suke.jp/comic/review/"),
	url-prefix("http://www.ota-suke.jp/photo/"),

	url-prefix("http://www.ota-suke.jp/list/product/newrelease/")
{

/*ロゴ移動（右カラムへ）*/
.header_logo{
	position:absolute!important;
	top:38px!important;
	margin-left:795px!important;
	width:160px!important;
	height:70px!important;
}
.header_logo img{
	width:160px!important;
	height:70px!important;
}
#sidebar{
	margin-top:80px!important;
}
}

@-moz-document url-prefix("http://www.ota-suke.jp/list/news/"),
	url-prefix("http://www.ota-suke.jp/special/mov"){
/*ニュース一覧*/
/*ロゴ移動*/
.header_logo{
	position:absolute!important;
	top:38px!important;
	margin-left:-5px!important;
	width:160px!important;
	height:70px!important;
}
.header_logo img{
	width:160px!important;
	height:70px!important;
}
.header_navi{
	/*width:750px!important;*/
}
.pager{
	overflow:hidden!important;

}
.news_rightbar_contents .rightbar
,div#page div#content_news div.news_container>div:nth-of-type(1){
	display:none !important;
}
}

@-moz-document url-prefix("http://www.ota-suke.jp/annex/"){
/*ナビ部分*/
.header_navi{
	background-color:#FFFBCF!important;
	border: solid 1px #FFBF00!important;
	margin:0!important;
}
.header_navi ul li a{
	color:#FFA200!important;
	margin:0!important;
	padding:5px 5px 4px 5px!important;
}
.header_navi ul li a:hover{
	background-color:#FFC30F!important;
	color:#FFFFFF!important;
}
.header_navi_mainmenu{
	margin:0!important;
}
/*記事タイトル*/
.news_title{
	background-color:#FFFBCF!important;
	border-top: solid 2px #FFBF00!important;
	border-bottom: solid 2px #FFBF00!important;
	border-left: solid 6px #FFBF00!important;
}
.news_title h1{
	color:#FFA200!important;
	/*padding-left:6px!important;*/
}
html body div#page div#content_news div.news_container div.news_main_contents div.news_main_contents div.news_title2
{
	background-color:#FFFBCF!important;
	border-top: solid 2px #FFBF00!important;
	border-bottom: solid 2px #FFBF00!important;
}
html body div#page div#content_news div.news_container div.news_main_contents div.news_main_contents div.news_title2 h1{
	color:#FFA200!important;
}

div#page div#content_news div.news_container div.news_main_contents div.news_main_contents div.news_title2{
	color:#5F5F5F!important;
	background-color:#FFED2F!important;
	border-left: solid 0px #FFC30F!important;
}
div#page div#content_news div.news_container div.news_main_contents div.news_main_contents div.news_box_main h2.datehead{
	color:#5F5F5F!important;
	font-size:16px !important;
}
div#page div#content_news div.news_container div.news_main_contents div.news_main_contents div.news_box_main div.news_list2 ul li{
	font-size:14px !important;
}
}
