css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("h728.serverid.net") {

ul, form, td, p { margin: 0px !important; padding: 0px !important; }
a { text-decoration: none !important; color: #888888 !important; }
a:hover{color:#000000 !important;}
a img { border: 0 !important; }
ul { list-style: none !important; }
body { font-family: "Arial" !important; font-size: 16px !important; background-color:#D6DFEF !important;}
h1 { font-size: 16px !important; margin-bottom: 5px !important; }
table { clear: both !important; width: 420px !important; }
small { font-size: 12px !important; }
small img { display: none !important; }
td { height: 20px !important; background-color:#FFFFFF !important; }
input[type="submit"] { float: left !important; margin-right: 5px !important; background-color:#FFFFFF !important; color:#333333 !important; border: 1px solid #333333 !important;}
p { margin-bottom: 0.5em !important; }

.xxl table { border: 10px solid #CCC !important; margin-top: 1em !important; }
.xxl td, .xxl th { font-size: 11px !important; padding: 0.5em !important; text-align: center !important; vertical-align: middle !important; border-bottom: 1px solid #CCC !important; border-right: 2px solid #CCC !important; }
.xxl th	{ padding: 1em !important; line-height: 1em !important; }
.xxl th span { font-size: 12px !important; color: fuchsia !important; }
.xxl .side { font-size: 12px !important; text-align: right !important; }	

.button-to { float: left !important; margin-right: 5px !important; background-color:#FFFFFF !important; color:#333333 !important; border: 1px solid #333333 !important; }

#container { width: 500px !important; margin: 0 auto !important; padding: 15px 0 !important; text-align: left !important; position: relative !important; }
#navigation {  position: absolute !important; top: 32px !important; right: 0 !important; left:0 !important;width:585px !important; text-align:center !important; background-color:#0000FF !important; color:#FFFFFF !important;  font-size: 12px !important; border: 1px solid #333333 !important; }
#navigation a {color:#FFFFFF !important;}
#navigation a:hover{color:#000099 !important;}
#logout { font-size: 12px !important; position: absolute !important; right: 0px !important; top: 0px !important; padding: 10px !important; }
#content { background-color:#FFFFFF !important; width: 555px !important; margin-top: 50px !important;padding-bottom: 15px !important;border: 5px solid #0000FF !important; padding:10px !important;}

#controls { position: relative !important; margin-top: 10px !important; width: 46px !important; height: 46px !important; float: left !important; }

#canvas { border-bottom: 1px solid silver !important; border-right: 1px solid silver !important; width: 144px !important; height: 144px !important; float: left !important; margin-right: 3px !important; margin-bottom: 3px !important; clear: both !important; }
#canvas .cell { width:15px !important; height:15px !important; float:left !important; border-top: 1px solid silver !important; border-left: 1px solid silver !important; font-size:0px !important; }
	
#map { position: relative !important; width: 480px !important; height: 480px !important; float: left !important; border: 2px solid silver !important; margin-right: 10px !important; margin-top: 10px !important; overflow: hidden !important; background-color: #00BBFF !important; }
#map img{opacity:0.4 !important;}
#move-to input, strong { float: left !important; margin-right: 5px !important; }

#errors, #flash { padding: 10px !important; border: 2px solid silver !important; margin: 10px !important; width: 60% !important; clear: both !important; }

.orderable .name { font-weight: bold !important; clear: both !important; }
.orderable .text { margin-top: 0.5em !important; }
.orderable .attributes { margin-top: 0.5em !important; }
.orderable .costs { margin-top: 0.5em !important; margin-bottom: 0.5em !important; }
.resources { margin-top: 0.25em !important; }
.orderable img, .resources img, #navigation img, .units img { width: 18px !important; height: 18px !important; vertical-align: middle !important; margin-right: 5px !important; }
.logo { vertical-align: middle !important; margin-right: 5px !important; }

.units li { padding-top: 2px !important; }

.orders { padding: 10px !important; background-color: silver !important; margin: 10px !important; width: 60% !important; }
.orders .current { float: left !important; }
.orders .clock { text-align: right !important; }
#order_number { float:left !important; margin-right: 5px !important; }

.active a{color:#333333 !important;}
.active a:hover{color:#888888 !important;}

.tabs { margin-top: 0.25em !important; padding-bottom: 1em !important; }
.tabs li { display: inline !important; margin-right: 10px !important; }

#landscape { position: relative !important; margin-top: 2em !important; margin-bottom: 2em !important; }
#landscape #fleet { position: absolute !important; left: 414px !important; top: 38px !important; width: 18px !important; height: 18px !important; }
