css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tivocommunity.com") {


/* for colors http://www.easyrgb.com/index.php?X=HARM type in 8195E6 */


/* remove top Tivo image in masthead*/
body > table > tbody > tr:nth-child(1) > td > a > img {
  display: none !important;
}

/* remove masthead advertisement */
#google_ads_div_TCF-Bug {
  display: none;
}

/* remove tab background image */
body > table  {
  background-image: none !important;
}
/* change navigation to a bar with no images */
#topNav:first-child {
  text-align:center !important;
  padding: 0px 0 !important;
  background-color: #6DA760 !important;
  width: 100% !important;
  height: 25px !important;
}
#topNav li {
  display: inline !important;
  padding: 0 !important;  
  background-image: none !important;
  background-color: #6DA760 !important;
  height: 25px !important;
}
#topNav li a {
  color: #DEDDC7 !important;
  font: normal 12px/2 Verdana !important;
  height: 25px !important;
}
#topNav li a:hover{
  color: #008855 !important;
  background-image: none !important;
  background-color: #DEDDC7 !important;
}


/* threads table */
/* search results bar */
.tcat{
  background-image: none !important;
  background-color: #DB8260 !important;
  color: #444 !important;
}
.tcat a:link, .tcat_alink {
  color: #444 !important;
}

.tborder{
  background-color: #FFC6AF !important;
}

.thead{
  background-image: none !important;
  background-color: #DEDDC7 !important;
  color: #444 !important;
  font: normal 10px Verdana !important;
}
.tfoot{
  background-image: none !important;
  background-color: #DEDDC7 !important;
  color: #444 !important;
}

form > table:nth-child(2) {
  display: none !important;
}

/* remove network bar at bottom */
.network-bar {
  display: none !important;
}

/* remove wide advertisement "join the TC Club" box */
body > div > div.page:first-child > div:first-child > table:nth-child(4) tr > td  {
   display: none !important;
}

/* remove orange line under wide advertisement box */
body > div > div.page:first-child > div >  table:nth-of-type(3) {
  display: none !important;
}


/* removes right column advertisement on post details screen */
table.page > tbody > tr:first-child > td + td{
  display: none;
}

/* remove faint rounded box around breadcrumbs */
body > div > div.page:first-child > div > table {
  background: none !important;
}
body > div > div.page:first-child > div > table > tbody > tr > td > img {
  display: none !important;
}
body > div > div.page:first-child > div > table  td {
  background-image: none !important;
  font: normal 12px Verdana !important;
}

/* change styling of pagenav "page 1 of"*/
.vbmenu_control  {
  background: #DB8260 !important;
  color: #DEDDC7 !important;
  font: normal 11px Verdana !important;
}
.pagenav .alt2, .pagenav .alt1 {
  background: #DEDDC7 !important;
}
.pagenav .alt2 a, .pagenav .alt1 a {
  color: #008855 !important;
}
.pagenav .alt2 strong {
  color: #444 !important;
}

/* fix font on breadcrumbs */
.navbar{
  font: normal 10px Verdana !important;
}
.navbar strong{
  font: bold 10px Verdana !important;
}
.style388 {
  font: normal 10px Verdana !important;
}

/* threads */
#threadslist td{
  font-family : Verdana !important;
}

#posts div.page:first-child div{
  padding: 0 !important;
}

}
