css:
@-moz-document domain('bluekaffee.com'), url-prefix('http://www.bluekaffee.com/') {

/* links */
a:link,
a:active,
a:visited { color: #FFB273 !important; text-decoration: none !important}
a:hover { color: #FF9640 !important; text-decoration: underline !important}

a.dropdown:link,
a.dropdown:active,
a.dropdown:visited { color: #6B80B0 !important}
a.dropdown:hover { color: #9EB3E3 !important}

a.menulink:link,
a.menulink:active,
a.menulink:visited,
a.menulink:hover { color: #6B80B0 !important; text-decoration: none !important}

a.submenu:link,
a.submenu:active,
a.submenu:visited { color: #6B80B0 !important; text-decoration: none !important}
a.submenu:hover { color: #6B80B0 !important; text-decoration: underline !important}

a.stats:link,
a.stats:active,
a.stats:visited { color: #6B80B0 !important}
a.stats:hover { color: #9EB3E3 !important}

.fakelink { color: #FFB273 !important}

body {
  font-size: 100% !important;
  background-color: #112 !important;
  color: #DDD !important
}

td#dd1, td#dd2, td#dd3, td#dd4,
td#dd5, td#dd6, td#dd7, td#dd8, {
  display: none !important;
  background-color: #0E1420 !important;
  color: #6B80B0 !important;
  text-decoration: none !important
}

hr {
  border: 1px solid #1D2638 !important;
  height: 0px !important
}

img[alt="bk cup"] {
  display: none !important
}

#dropmenudiv {
  background-color: #111620 !important
}

td.menu {
  background-color: #111620 !important
}

td[height="44"] {
  display: none !important
}

table[bgcolor="#100C6B"], td[bgcolor="#100C6B"] {
  background-color: #1D2638 !important
}

td[bgcolor="#0D0A54"] {
  background-color: #111620 !important
}

.content {
  background-color: #111620 !important
}

.laccent {
  background-color: #0E1420 !important
}

.accent {
  background-color: #1D2638 !important
}

.divaccent {
  background-color: #1D2638 !important;
  margin: 0 auto !important;
  padding:8px 8px 8px 5px !important;
  width: auto !important
}

/* forum breadcrumb */
div.spec_accent {
  background-color: #1D2638 !important;
  border: none !important;
  margin: 0 auto !important;
  width: 100% !important
}

select.compact {
  background-color: #111620 !important;
  width: 200px !important
}

table.flattable {
  border: none !important;
  border-collapse: collapse !important
}

table.flattable td {
  border: 1px solid #1D2638 !important;
  padding: 4px 2px !important
}

.bbButton {
  color: #DDD;
  background-color: #1D2638 !important;
  border: #000 1px solid !important;
  margin: 2px 1px !important
}

input.bbButton[value="â™¥"],
input.bbButton[value="[sup]"],
input.bbButton[value="[sub]"] {
  display: none !important
}

}
