css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.rememberthemilk.com") {

/* ==== Items to be completely hidden ==== */

/* The cow */
#appheaderlogo {
 /* display: none; */
}

/* Footer links */
.appfooter {
  display: none; 
}

/* Help tips box */
#onlinehelpwrap {
  display: none; 
}

/* Snaking line connecting tasks to info box */
.snake-line, .snake-left-link, .snake-right-link {
  display: none; 
}

.orange_rbbot, .orange_rbtop {
  display: none !important;
}

/* Tag cloud */
#taskcloud_copy, #taskcloudcontent_copy {
  display: none;
}


/* ==== Color and font settings ==== */

#listsearchfor {
  font-style: italic !important; 
  color: silver !important; 
}

.orange_rbroundbox {
  border-color: black !important;
}

#detailsbox {
  opacity: 0.9 !important;
  background: white !important;
}

#listdetailsbox > * {
  font-weight: bold !important; 
}

#taskdetails > * > SPAN {
  font-weight: bold !important;
}

#listtabs { 
  background: white !important; 
  opacity: 1.0 !important;
}

#listtabs > UL >  * { 
  background: none !important; 
  text-align: left !important;
  font: 9pt/15pt Calibri !important;
}

#listtabs > UL > LI > A { 
  background: #EDEFEF !important;
  border-color: black !important;
  color: #1D1D1D !important;
}

#listtabs > UL > .xtab_smartlist > A {
  background: #DEDEEF !important;
}

#listtabs > UL >.xtab_selected > A {
  border-color: maroon !important; 
  background: white !important;
}

#notes {
  background: white !important; 
  border-color: black !important; 
  opacity: 0.9 !important;
}

#searchbox {
  background: white !important;
}

#searchboxwrap {
  background: none;
}

#searchoptions {
  border-color: black !important;
  background: white !important;
  opacity: 0.9 !important;
}


/* ==== Position and size settings ==== */

/* Everything, basically */
#content {
/*
  position: absolute !important; 
  left: 115px !important;
  width: 88.8% !important;
  height: 100% !important;
*/
}

/* Cow, top navigation, date, search */
#appheader {
  position: absolute !important;
  left: 0px !important;
  top: 0px !important;
  width: 100% !important;
  height: 100% !important;
}

/* Status messages */
#statusbox {
/*
  margin:20px 0 0 0 !important;
  padding:0 0 0 6px !important; 
*/
}

/* Advanced search dialog */
#searchoptions {
  margin: 0 !important;
  padding: 0 !important;
  position: fixed !important;
  top: 70px !important;
  border: 2px solid !important;
  -moz-border-radius: 5px !important;
  z-index: 50 !important;
  width: 79% !important;
  left: 115px !important;
}

/* Used in Locations for showing map */
#mapview {
}

/* Main area of Overview page */
#overview {
}

/* Everything else */
#appview {
}


/* All of the tabs I want on the left in #appview */
#listtabs, #contacttabs, #settingstabs { 
  padding-top: 56px !important; 
  position: fixed !important; 
  left: 0% !important; 
  top: 0px !important;
  width: 114px !important;
  height: 100% !important; 
}

#listtabs > UL > *, #contacttabs > UL > *, #settingstabs > UL > * { 
  height: 25px !important;
  border: none !important;
  margin: 0 !important;
}

#listtabs > UL > LI > A, #contacttabs > UL > LI > A, #settingstabs > UL > LI > A { 
  margin-left: -16px !important;
  width: 96px !important;
  -moz-border-radius : 6px 0 0 6px !important;
  border: 1px solid !important;
}

#listtabs > UL > .xtab_selected > A, #contacttabs > UL > .xtab_selected > A, #settingstabs > UL > .xtab_selected > A {
  border: 1px solid !important; 
  border-right: none !important; 
  width: 97px !important;  
}


/* Entire list subsection, including tools and add */
/* Goes in the middle */
/* Divide this up later? */
#list {
  position: absolute !important; 
  top: 147px !important; 
  width: 100% !important; 
} 

.xtable > TBODY > .xtr:nth-child(odd) {
  background: #fafafa !important;
}

.xtable > TBODY > .xtr:nth-child(even)  {
  background: #FFF !important;
}

/* Proper divisions */
/*
#tools {
}

#midcontent { 
}
or
#add-box {
}

This could be made the sole scrolling component
#tasks {
}

Some other internal groupings exist
*/


/* Status popups, a subsegment of #statusbox */
/*.orange_rbroundbox {
  position: fixed !important; 
  top: 45% !important; 
  width: 50% !important; 
  left: 30% !important; 
  z-index: 2 !important; 
  padding: 6px !important; 
  border: solid 1px !important; 
  -moz-border-radius: 10px !important;
  opacity: 0.7 !important;
}*/

/* header information */
#detailscontent {
  margin: 0 !important; 
  padding: 0 !important;
}

#detailstitle {
  margin-left: 15px !important; 
  margin-top: -10px !important; 
  padding-bottom: 5px !important;
  font-size: 1.2em !important;
}

#appheader {
  position: fixed !important; 
  right: 2% !important; 
  top: 36px !important;
  width: 77.8% !important; 
  z-index: 30 !important;
  font-size:0.8em !important;
}

#detailsbox {
  position: fixed !important; 
  padding-top:31px !important; 
  top: 0px !important;
  width: 88.6% !important; 
  left: 115px !important; 
}

#listdetails, #details, #detailswrap {
  width: 100% !important;
}

#details, #detailswrap, #detailsbox {
  height: 114px !important;
}

#taskdetails > * {
  min-height: 21px !important; 
  float: left !important; 
  margin-left: 10px !important; 
  min-width: 22% !important;
}

#listdetailsbox > * {
  margin-left: 10px !important; 
  min-height: 21px !important; 
  float: left !important;  
  min-width: 20% !important;
}

#publishform > INPUT[type="submit"] {
  position: absolute !important; 
  top: 80px !important; 
  right: 20px !important; 
}

#publishform, #listsharedetails {
  margin-left: 10px !important;
}

/* selected details */
#detailsstatuswrap {
  position: fixed !important; 
  top: 90% !important; 
  width: 30% !important; 
  left: 35% !important; 
} 



#notes {
  position: fixed !important; 
  top: 12% !important; 
  left: 18% !important; 
  width: 70% !important; 
  border: solid 1px !important; 
  -moz-border-radius: 5px !important; 
  z-index: 30 !important;
}

.notexedit, .notewidth {
  margin-left: 3px !important;
  width: 99% !important;
}



/*
#taskcloud_copy > * > * ,#taskcloud_copy > * > * > *  {
  overflow: hidden !important; 
  height: 0 !important; 
  margin: 0 !important;
}

#taskcloudcontent_copy {
  position: fixed !important; 
  top: 90% !important; 
  width: 90% !important;
  opacity : 0.8 !important; 
  left: 5% !important;
  border: 1px black solid !important;
  -moz-border-radius: 5px !important;
  background: white !important;
}

.tasktag,.locationtag  { }

.level1 {font-size: 0.9em !important;}
.level2 {font-size: 1.1em !important;}
.level3 {font-size: 1.3em !important;}
.level4 {font-size: 1.5em !important;}
.level5 {font-size: 1.7em !important;}
*/



#notetoolbox, #listtoolbox {
  margin-top: 12px !important;
  text-align: right !important;
  margin-right: -6px !important;
} 

#searchbox {
  position:absolute !important;
  margin: 0 !important;
  padding: 0 !important;
  right: 0 !important;
  width: 100% !important;
  top: -30px !important;
  z-index: 3 !important;
}

#searchboxwrap > TABLE { 
  width: 100% !important; 
}
#searchtogglewrap {
  margin-top: -16px !important;
  width: inherit !important; 
}
#searchboxwrap {
  width: 79% !important; 
}


#sorting {
  position: fixed !important;
  top: 13px !important;
  left: 10px !important;
  z-index: 300 !important;
  font-size: 0.6em !important; 
}

}
