css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://osatomi.jugem.jp/") {

#gEntryAdContainer
,.jugem_blog_ad_by
,#jgSideAdContainer

/*コピーライト部分*/
,.entry+br
,ul.navi+br
/*忍者ツール*/
,#NnVD
,.entry+div+br
,.entry+div+br+br{
	display:none!important;
}

*:not(input){
	font-family:
	"ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro",
	"メイリオ", "Meiryo",
	"Osaka",
	"ＭＳ Ｐゴシック","ＭＳ ＰGothic",
	sans-serif !important;
}
body{
	background-position:5% -1%!important;
	margin:0px 0% 0px 0%!important;
	width:1100px!important;
}
#all{	
	width:800px!important;
}
#all:after{
	content:""!important;
	display:block!important;
	height:0!important;
	clear:both!important;
	visibility:visible!important;
}
#box{
	width:100%!important;
	position:relative!important;

	/*overflow:hidden!important;*/
}
#box:after{
	content:""!important;
	display:block!important;
	height:0!important;
	clear:both!important;
	visibility:visible!important;
}
/*おさとう缶。タイトル*/
#header{
	width:auto!important; 
	margin:10px 0px 10px 0px!important;
	white-space:nowrap!important;
}
#header h1{
	color:#9F9F9F!important;
	font-style:normal!important;
	font-size:14px !important;
	margin:0% 0% 0% 10%!important;	
}
#header h1 a{
	background-color:transparent!important;
	color:#FFAFD0!important;
	color:#FF9FC7!important;

	font-style:normal!important;
	font-size:30px !important;

	position:relative!important;
	left:-22px!important;
}
#header h1 a:hover{
	color:#FF8FBB!important;
	color:#FFBFDB!important;
}
#main{
	height:100%!important;
	width:70%!important;
}
.write{
	height:100%!important;
	width:99%!important;
	border:solid 1px #DFDFDF!important;

	/*border-radius:15px 15px 15px 15px !important;*/
	-moz-border-radius:15px 15px 15px 15px !important;
	-webkit-border-radius:15px 15px 15px 15px !important;
	-o-border-radius:15px 15px 15px 15px!important;

	/*-moz-box-shadow:1px 3px 4px rgba(82, 82, 82,0.4)inset !important;
	-webkit-box-shadow:1px 3px 4px rgba(82, 82, 82,0.4)inset !important;
	-o-box-shadow:1px 3px 4px rgba(82, 82, 82,0.4)inset !important;*/
}
.entry{
	overflow:hidden!important;
	margin:1em 0px 0px 3em!important;
}
.mobile{
	width:auto!important;

	margin-left:auto!important;
	margin-right:auto!important;
	text-align:left!important;
}
/*記事タイトル*/
.entry_navi{
	color:#8F8F8F!important;
	font-style:normal!important;
	font-size:12px !important;
	margin:1em 0em 0em 0em!important;
}
/*-------------------------------リンク--------------------------------------*/
.entry_navi a:link{
	color:#FF5F9A!important;
	background-color:transparent!important;
}
.entry_navi a:visited{
	color:#AFAFAF!important;
	background-color:transparent!important;
}
.entry_navi a[href="./"]{
	color:#9F9F9F!important;
}
.entry_navi a:hover{
	color:#FF9FC2!important;
	background-color:transparent!important;
	text-decoration:underline!important;
	text-decoration:none!important;
}
/*TOPページの続きを読む*/
div.entry_more a:link{
	color:#FF5F9A!important;
	background-color:transparent!important;
}
div.entry_more a:visited{
	color:#AFAFAF!important;
	background-color:transparent!important;
}
div.entry_more a:hover{
	color:#FF9FC2!important;
	background-color:transparent!important;
	text-decoration:none!important;
}

/*記事タイトルまわり*/
.entry_date{
	text-align:left!important;
	margin:10px 0px!important;
	color:#AFAFAF!important;
	font-style:normal!important;
	font-size:10px !important;

	position:relative!important;
	top:1em!important;
	left:35em!important;
}
.entry_date a{
	color:#FF6FAB!important;
	font-style:normal!important;
	font-size:15px !important;
	background-color:transparent!important;

	position:absolute!important;
	top:-6px!important;
	left:-67%!important;
}
.entry_date a:hover{
	color:#FF9FC5!important;
}
.entry_date a:before{
content:"●"!important;
	color:#FFBFD8!important;
	margin-right:3px !important;
}

/*本文文字色*/
.entry_body
,.jgm_entry_desc_mark p.MsoPlainText
,.jgm_entry_desc_mark p.MsoPlainText
,.entry_more
,.jgm_entry_desc_mark font[color="#000000"]
{
	color:#5F5F5F!important;
}
/*font[color="#ff0000"]{
	color:#FF2F4B!important;
}*/

/*本文文字サイズ*/
.entry_body
,.jgm_entry_desc_mark p.MsoNormal font
,.jgm_entry_desc_mark p.MsoPlainText font
,.entry_more
,.jgm_entry_desc_mark font{
	font-style:normal!important;
	font-size:14px !important;
	line-height:19px !important;
	/*white-space:nowrap !important;*/
	margin:2em 0em 0em 0em!important;
}
.entry_body a{
	color:#FF6FAB!important;
	background-color:transparent!important;
}
.entry_body a:hover{
	color:#2FA2FF!important;
	text-decoration:underline!important;
}
/*記事内容*/
.jgm_entry_desc_mark{
	width:auto!important;
	white-space:normal!important;
	line-height:1.4 !important;

	margin-left:auto!important;
	margin-right:1em!important;
	/*text-align:center!important;*/
}

/*記事中の画像*/
.jgm_entry_desc_mark img[alt="1400010001.jpg"]{
	margin:2em auto 2em auto!important;
}
/*記事下部のカテゴリ*/
.entry_state{
	color:#5F5F5F!important;
	margin:0em !important;
	padding:1em 5%!important;
	border-bottom:solid 1px #EFEFEF!important;
}
.entry_state a{
	color:#FF6FAB!important;
	background-color:transparent!important;
}
.entry_state a:hover{
	color:#2FA2FF!important;
	text-decoration:underline!important;
}
.entry+br+br{
	display: none!important;
}
/*記事下部の各メディアリンク*/
.service_button{
	text-align:right!important;
	overflow:hidden!important;
	margin:2em 2em 1em 2em!important;
	padding:0em 0em 0em 0em!important;
}
/*
.service_button iframe.twitter-share-button
,.service_button iframe.twitter-count-none{
	position:relative!important;
	top:0px!important;
	left:0px!important;
}
*/
.mixi-check-button{
	margin:1px 1px 1px 1px!important;
	position:relative!important;
	top:-2px!important;
}
a.mixi-check-button:link{
	background-color:transparent!important;
}
.mixi-check-button:hover{
	opacity:0.7!important;
}

.fb_edge_widget_with_comment{
	position:relative!important;
	top:-5px!important;
	left:0px!important;
}
.connect_widget_button_count_excluding{

}
.connect_widget_button_count_count{

}

/*----------------------------サイド---------------------------------*/
#side{
	width:30%!important;
	font-family:
	"Verdana",
	"ヒラギノ角ゴ Pro W3",
	"メイリオ","Meiryo",
	"ＭＳ ゴシック",
	"Osaka‐等幅",
	sans-serif !important;
}
.linkmenu{
	margin:10px 2em 2em 2em!important;
	/*margin:15em 2em 2em 2em!important;*/
}

.linkmenu a
,.linkmenu a:visited{
	color:#777777!important;
	font-family:
	"Verdana",
	"ヒラギノ角ゴ Pro W3",
	"メイリオ","Meiryo",
	"ＭＳ ゴシック",
	"Osaka‐等幅",
	sans-serif !important;

	font-style:normal!important;
	font-size:20px !important;
	line-height:25px !important;
}
.linkmenu a:hover{
	color:#FF9FC2!important;
	background-color:transparent!important;
	text-decoration:none!important;
}
/*制作者*/
.by{
	color:#888888!important;
	font-family:
	"Verdana",
	"ヒラギノ角ゴ Pro W3",
	"メイリオ","Meiryo",
	"ＭＳ ゴシック",
	"Osaka‐等幅",
	sans-serif !important;

	font-style:normal!important;
	font-size:12px !important;
	line-height:15px !important;

	position:relative!important;
	position:absolute!important;
	bottom:60px!important;
	left:113px!important;

	z-index:1!important;
}
.by a
,.entry+br+br+center
,ul.navi+br+br+center{
	color:#6F6F6F!important;
	background-color:transparent!important;
}
.by a strong:hover{
	color:#FFAFCC!important;
	background-color:transparent!important;
	text-decoration:underline!important;
	text-decoration:none!important;
}
/*LOG IN*/
div#side div.linkmenu a[href="./admin"]{
	font-style:normal!important;
	font-size:11px !important;

	line-height:15px !important;

	position:relative!important;
	position:absolute!important;
	bottom:150px!important;
	left:173px!important;

	z-index:1!important;
}
/*コピーライト部分*/
ul.navi+br+br+center
,.entry+br+br+center
,div#main div.write center{
	color:#9F9F9F!important;
	margin-bottom:1px!important;
}
.entry+br+br+center a
,ul.navi+br+br+center a
,div.write center a{
	color:#2FA2FF!important;
	color:#5F5F5F!important;
	background-color:transparent!important;
}
.entry+br+br+center a:hover
,ul.navi+br+br+center a:hover
,div.write center a:hover{
	color:#FF5F9A!important;
	text-decoration:underline!important;
}
ul.navi{
	margin:1em 0px 0px 0em!important;
}

/*メニュー展開時*/
.MENU{
	width:200px !important;
	z-index:10!important;

	-moz-box-shadow: 3px 3px 3px rgba(47, 47, 47,0.4);
	-webkit-box-shadow: 3px 3px 3px rgba(47, 47, 47,0.4);
	-o-box-shadow: 3px 3px 3px rgba(47, 47, 47,0.4); 
}
.MENU .list_title{
	color:#FF9FC2!important;
}
.MENU .list_text a
,.calendar{
	color:#5F5F5F!important;
	font-style:normal!important;
	font-size:13px !important;
	line-height:15px !important;
}
.MENU .list_text a:hover
,.calendar a:hover{
	color:#FF5F9E!important;
	background-color:transparent!important;
}
.calendar a
,ul.navi a{
	color:#FF4F94!important;
	background-color:transparent!important;
	text-decoration:underline!important;
}
/*個別*/
/*CALENDAR*/
#MENU1{
	position:absolute!important;
	top:25px!important;
	left:100px!important;
}
.calendar a:hover
,ul.navi a:hover{
	color:#2FA2FF!important;
	text-decoration:none!important;
}
.calendar_month a{
	text-decoration:none!important;
}
/*PROFILE*/
#MENU2{
	position:absolute!important;
	top:50px!important;
	left:100px!important;
}
/*CATEGORY*/
#MENU3{
	position:absolute!important;
	top:75px!important;
	left:100px!important;
}
/*ARCHIVE*/
#MENU4{
	position:absolute!important;
	top:100px!important;
	left:100px!important;

	/*height:38em !important;
	overflow:auto!important;*/
}

/*NEW ENTRY*/
#MENU5{
	position:absolute!important;
	top:125px!important;
	left:100px!important;
}
/*LINK*/
#MENU8{
	width:210px !important;

	position:absolute!important;
	top:200px!important;
	left:100px!important;
}
/*RECOMMEND・OTHERS*/
#MENU9{
	position:absolute!important;
	top:225px!important;
	left:100px!important;
}
#MENU10{
	position:absolute!important;
	top:250px!important;
	left:100px!important;
}
#MENU9 .list_title
,#MENU10 .list_title
,#MENU10 .list_text{
	width:200px !important;
}
#MENU10 .list_text{
	width:170px !important;
	margin-left:auto !important;
	margin-right:auto !important;
	margin-left:auto !important;
	padding:0 0 5px 0 !important;
}

/*カーソル*/
body
,#header
,#side
,.entry{
	cursor:auto!important;
}
#header h1 a:hover
,.entry a:hover
,.entry_navi a:hover
,.entry_date a:hover
,.jgm_entry_desc_mark a:hover
,#side a:hover
,.entry+br+br+center a:hover
,ul.navi+br+br+center a
,ul.navi a
,div.write center a{
	cursor:pointer!important;
}
}
/*-------------------------PROFILE-------------------------------------*/
@-moz-document url("http://osatomi.jugem.jp/?pid=1") {
.entry_date{
	color:#6F6F6F!important;
	font-size:16px !important;
	font-weight:normal!important;

	position:relative!important;
	top:0em!important;
	left:0em!important;
}
.entry_body{
	margin-top:1em !important;
}
.entry_body:before{
	color:#5F5F5F !important;
	font-size:12px !important;
	font-weight:normal!important;

	content:")" !important;
	position:relative!important;
	top:-1px!important;
	left:16em!important;
}
}

@-moz-document url-prefix("http://osatomi.jugem.jp/?eid="){
/*記事下部のカテゴリ*/
.entry_state{
	border-bottom:solid 0px #EFEFEF!important;
}
}
