@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wrongplanet.net") {

/*FONT COLORS*/

.gen, .gensmall, .genmed, .cattitle, .name, .postbody, .topictitle, .quote > A, .option, .content, FONT[color="#b8d0f0"][style="font-weight: bold;"], FONT[color="white"]
{color:#9E0000 !important;}
.nav, .postdetails, .gensmall > A, .mainmenu, .maintitle, .gen > A, .footmsg
{color: #A06200 !important;}
.nav > a, H1 > BIG > A, .forumlink, .postbody > A, .name > A, .postdetails > A, .topictitle > A,  .gensmall > B > A, .quote, .topnav, .postdetails > B > A, .name > B > A, .footmsg > A
{color: #1A65BD !important;}

