TD[background="mods/bbcode_box/images/bg.gif"]{background-color: #1A65BD !important;}
/*BLACK BACKGROUND*/
TABLE[width="100%"][cellspacing="0"][cellpadding="5"][border="0"][bgcolor="#f0f0f0"][align="center"]
{
background-color: #080808 !important;
}
TABLE[width="100%"][cellspacing="1"][cellpadding="8"][border="0"][bgcolor="#f2f2f2"], .custommenu
{
background-color: #000000 !important;
}
