A[href="modules.php?name=Private_Messages&file=index&folder=sentbox"],
A[href="modules.php?name=Private_Messages&file=index&folder=outbox"],
A[href="modules.php?name=Private_Messages&file=index&folder=savebox"],
A[href="modules.php?name=Your_Account"]
{color: #9E0000 !important;}
.content > CENTER > A, .content > A,
A[href="modules.php?name=Members_List"],
A[href="modules.php?name=Search&type=users"]
{color: #1A65BD !important;}
