color: #9E0000 !important;
}
.custommenu > TBODY > TR > TD[bgcolor="#f2f2f2"] > A
{
color: #A06200 !important;
}
.custommenu > TBODY > TR > TD > CENTER > FORM > TABLE[border="0"][bgcolor="#f2f2f2"]
	{
	background-color: #080808 !important;
