css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("torrentfreak.com")
{
body{background:#282952 url(data:image/jpeg;base64,) repeat-x 0px 0px!important}
a { color: #c9d5f6!important;}
a:HOVER { color: #f9d5c6!important;}
#author-wrapper{background:#333!important;width:90%!important}
#comment,[class="comment"],blockquote,#featured{background:#222!important;padding: 4px 4px 4px 4px !important}
.comment{margin-bottom:-5px!important}
.dsq-comment-message { color:#a8a8d4!important}
#wrapper,#content-wrapper{background-image:none!important}
#logo{margin: 7px 0px 0px!important}
#introduction{margin: -17px 0px 0px!important}
#searchform{margin: 0px 0px 0px!important}
#masthead{margin-bottom:3px!important}
#main-content,article,#read-comments{width:960px!important}
#main-content{padding: 8px 0px 0px!important}
button{margin: -37px 0px 0px 0px!important}
#searchform{width:386px!important}
p,textarea,tr{color:#fff!important}
td{background:#222!important}
th{background:#444!important}
h4{color:#ccc!important}
#post-info{float: right!important;margin: -7px 0px 15px 10px!important}
#related,h3,#main,#trackbacks,#rss-info,#featured,#supp-content,tfoot,#other-news,.dsq-comment-show-thread,.dsq-global-toolbar-dropdown-container{display:none!important}
#latest-news{padding-left: 15px!important}
#share,#disqus_thread{border:none!important}
#dsq-global-toolbar{height:12px!important}
.dsq-like-activity{margin-top:2px!important}
section.date{height: 22px!important;width: 60px!important}
span.month{font-size: 14px!important;margin: 0!important}
span.day{font-size: 20px!important;margin: 2px 0 0!important}
section#main-content article footer a { color: #c5c5f6!important}
section#post-supp {width: 85%!important}
section#main-content article {background-color: #34346d!important}
section#blog  {color: #ccc!important;background-color: #34346d!important;}
section#blog li li a {color: #c0c0f5!important}
section#main-content article h3 {
    display:block!important;
    color: #FFEED9!important;
    font-size: 18px!important;
    margin: 9px 0 0!important;
  }
section#main-content article p a {
    color: #c9d5f6!important;
    border-bottom: 1px dotted #c9d5f6!important;
  }
section#main-content article p a:HOVER {
    color: #f9d5c6!important;
    border-bottom: 1px dotted #c9d5f6!important;
  }
}
