@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* Background */

#deviantART-halfmode #depths-in span:before		{content:'deviantTIDY HalfMode 2.22 |';}

#deviantART-halfmode,
#deviantART-halfmode #top-middle			{background-color:#6A7872 !important; border-color:#6A7872 !important}


/* Common elements */

#deviantART-halfmode .output-primary			{width:100% !important; position:relative !important; border:none !important; margin-bottom:15px !important; background-color:#eaf2ee !important; color:#000 !important; -moz-border-radius:8px !important;}
#deviantART-halfmode .output-secondary,
#deviantART-halfmode #nav-east,
#deviantART-halfmode td[style*="width: 324"]		{display:none !important;}
#deviantART-halfmode #depths i				{display:block !important; background-image:url("http://sh.deviantart.com/shadow/6A7872-6A7872/0-1/1/1/null.png") !important; border-color:#6A7872 !important;}

#deviantART-halfmode .output-primary div:not(.h),
#deviantART-halfmode #output h2,
#deviantART-halfmode #output h3,
#deviantART-halfmode #output li,
#deviantART-halfmode #output th,
#deviantART-halfmode #output td,
#deviantART-halfmode #output a,
#deviantART-halfmode #output strong,
#deviantART-halfmode #output form,
#deviantART-halfmode #output table			{border:none !important; background-color:transparent !important; letter-spacing:0 !important;}

#deviantART-halfmode #output ul ul			{padding-left:10px !important;}
#deviantART-halfmode #output li				{padding:0 10px !important;}
#deviantART-halfmode #output li.even			{background:#d4dfdd !important;}
#deviantART-halfmode #output li.first			{margin-top:10px !important;}
#deviantART-halfmode #output li.last			{margin-bottom:10px !important;}

#deviantART-halfmode #output div.section-foot,
#deviantART-halfmode #output .signature			{text-align:center !important;}

#deviantART-halfmode .trailing.section-foot.nav-holder.float-holder
							{padding:16px !important;}


/* Tab Bar */

#deviantART-halfmode #output .tabbar			{top:0 !important; margin:0 !important; padding:30px 15px 0 !important; background:#d7e5e2 !important; -moz-border-radius:8px 8px 0 0 !important;}
#deviantART-halfmode #output .tabbar *			{float:right !important; margin:0 5px !important; padding:10px 20px !important; color:#000 !important; font-size:12px !important; -moz-border-radius:8px 8px 0 0 !important;}
#deviantART-halfmode #output .tabbar strong		{top:0 !important; background:#eaf2ee !important; font-weight:normal !important;}
#deviantART-halfmode #output .tabbar a			{top:-3px !important;}

#deviantART-halfmode #output h2.section-head,
#deviantART-halfmode #devlove				{float:left !important; clear:none !important;  padding:15px 0 0 20px !important; font-style:normal !important; font-weight:bold !important; font-family:"Trebuchet MS" !important; font-size:28px !important; letter-spacing:-1px !important; color:#3b5a4a !important; z-index:20 !important;}


/* Sections */

#deviantART-halfmode #output div.subsection		{border:solid 3px #d4dfdd !important; padding:0 !important;}
#deviantART-halfmode #output div.subsection h3		{margin:0 0 10px !important; background:#d4dfdd !important; font-size:18px !important; letter-spacing:1px !important; color:#3b5a4a !important; text-align:center !important;}
#deviantART-halfmode #output div.subsection h3 img	{margin-bottom:3px !important;}


/* Buttons */

#deviantART-halfmode #output input[type="submit"],
#deviantART-halfmode #output input[type="button"],
#deviantART-halfmode #output .button,
#deviantART-halfmode #output a.beacon			{border:none !important; background:#d4dfdd !important; color:#3b5a4a !important; -moz-border-radius:0 !important; padding-left:5px !important; padding-right:5px !important;}

#deviantART-halfmode #output input[type="submit"]:hover,
#deviantART-halfmode #output input[type="button"]:hover,
#deviantART-halfmode #output .button:hover,
#deviantART-halfmode #output a.beacon:hover,
#deviantART-halfmode #output input[type="submit"]:focus,
#deviantART-halfmode #output input[type="button"]:focus,
#deviantART-halfmode #output .button:focus,
#deviantART-halfmode #output a.beacon:focus		{background:#d7e5e2 !important;}

#deviantART-halfmode #output input[type="submit"]:active,
#deviantART-halfmode #output input[type="button"]:active,
#deviantART-halfmode #output .button:active,
#deviantART-halfmode #output a.beacon:active		{background:#3e8eb7 !important; color:#ffffff !important;}

#deviantART-halfmode #output input.button[value*="!"]		{background:#2b6f88 !important; color:#ffffff !important;}
#deviantART-halfmode #output input.button[value*="!"]:hover,
#deviantART-halfmode #output input.button[value*="!"]:focus	{background:#1f5670 !important; color:#ffffff !important;}
#deviantART-halfmode #output input.button[value*="!"]:active	{background:#4f9fbf !important; color:#000 !important;}


/* Comments */

#deviantART-halfmode #output .comment-holder			{padding:0 10px !important;}

#deviantART-halfmode #output div.item,
#deviantART-halfmode #output div.previewcontainer		{border:solid 3px #d4dfdd !important; background:none !important; position:relative !important;}
#deviantART-halfmode #output div.item				{border-left-width:66px !important; min-height:104px !important;}
#deviantART-halfmode #output div.item-tag			{padding:3px 4px !important;}
#deviantART-halfmode #output div.item-tag span.ext		{display:inline !important; line-height:18px !important; padding-left:5px;}

#deviantART-halfmode #output div.item-head			{position:static !important; color:#3b5a4a !important; font-family:Trebuchet MS !important; font-size:14pt !important; letter-spacing:-1px !important; font-weight:bold !important;}
#deviantART-halfmode #output div.item-head a			{color:#3b5a4a !important;}
#deviantART-halfmode #output div.item-head strong,
#deviantART-halfmode #output div.item-head br			{display:none !important;}
#deviantART-halfmode #output div.item-head div[style] strong	{display:inline !important;}
#deviantART-halfmode #output div.item-head span			{margin-left:8px !important; font-family:Verdana, sans-serif !important; font-size:11px !important; font-weight:normal !important; color:#738281 !important; letter-spacing:0 !important;}
#deviantART-halfmode #output div.item-head img.avatar		{position:absolute !important; top:5px !important; left:-58px !important;}
#deviantART-halfmode #output div.item-head span:after		{content:" ("attr(title)")" !important;}
#deviantART-halfmode #output div.item-head img.mood		{right:20px !important;}

#deviantART-halfmode #output div.item-body			{height:auto !important;}
#deviantART-halfmode #output div.item-body img.avatar		{margin:0 !important;}
#deviantART-halfmode #output div.deviant-signature		{border-top:solid 1px #d4dfdd !important;}
#deviantART-halfmode #output div.nest				{margin-right:20px !important; margin-right:0 !important;}

#deviantART-halfmode #output .comment-holder .item:not(.control-box) .item-foot			{font-size:0 !important; position:absolute !important; bottom:4px !important; left:-58px !important; padding:0 0 22px 0 !important; margin:0 !important;}
#deviantART-halfmode #output .comment-holder .item:not(.control-box) .item-foot a		{display:block !important; width:48px !important; height:20px !important; background:url("data:image/gif;base64,") !important;}
#deviantART-halfmode #output .comment-holder .item:not(.control-box) .item-foot a:active	{background-image:url("data:image/gif;base64,") !important;}
#deviantART-halfmode #output .comment-holder .item:not(.control-box) .item-foot a:focus,
#deviantART-halfmode #output .comment-holder .item:not(.control-box) .item-foot a:active	{outline:none !important; }

#deviantART-halfmode #output .comment-holder .item .item-foot a[href*="#reply"]		{background-position:0 0 !important; position:absolute !important; bottom:0 !important;}
#deviantART-halfmode #output .comment-holder .item .item-foot a[href*="#parent"],
#deviantART-halfmode #output .comment-holder .item .item-foot a:not([href*="#"]),
#deviantART-halfmode #output .comment-holder .item .item-foot a[href*="help/case"]	{background-position:-50px 0 !important;}
#deviantART-halfmode #output .comment-holder .item .item-foot a[href="#"]		{background-position:-100px 0 !important; position:absolute !important; bottom:0 !important;}

#deviantART-halfmode #output div.item.control-box			{padding:0px !important; margin-left:16px !important;}
#deviantART-halfmode #output div.item.control-box .item-head		{font-size:0 !important;}
#deviantART-halfmode #output div.item.control-box .item-head a		{font-size:14pt !important;}
#deviantART-halfmode #output div.item.control-box .aside-right		{float:none !important; text-align:center !important;}
#deviantART-halfmode #output div.item.control-box .emoticon-link	{position:absolute !important; bottom:12px !important; right:10px !important;}
#deviantART-halfmode #output div.item.control-box .reply-cancel		{position:absolute !important; top:8px !important; right:8px !important;}
#deviantART-halfmode #output div.control-box-busy			{border-color:#438197 !important; cursor:wait !important;}
#deviantART-halfmode #output div.control-box-busy strong		{text-decoration:blink !important;}


/* Notes */

#deviantART-halfmode #output :not(.comment-holder)>.item .item-head br			{display:block !important;}
#deviantART-halfmode #output :not(.comment-holder)>.item .item-foot input.aside-left	{float:none !important;}
#deviantART-halfmode #output :not(.comment-holder)>.item .item-foot input.aside-right	{margin-left:8px !important;}
#deviantART-halfmode #output .item:not(.control-box) .item-foot input.button		{margin-top:10px !important; padding:0 0 4px !important; width:90px !important; height:20px !important; color:#4D5551 !important; background:transparent url("data:image/gif;base64,") center no-repeat !important;}
#deviantART-halfmode #output .item:not(.control-box) .item-foot input.button:active	{outline:none !important; background-image:url("data:image/gif;base64,") !important;}


/* Forms */

#deviantART-halfmode #output input,
#deviantART-halfmode #output select,
#deviantART-halfmode #output textarea			{margin-top:0 !important; border:solid 1px #d4dfdd !important; background:#fff !important;}
#deviantART-halfmode form[action*="notes"] div.item,
#deviantART-halfmode form[action*="send"],
#deviantART-halfmode form[action*="journal/update"]	{margin:10px !important;}


/* Edit journal */

#deviantART-halfmode [name="jfooter"],
#deviantART-halfmode [name="jheader"],
#deviantART-halfmode [name="jcss"]			{height:300px !important; width:100% !important;}
#deviantART-halfmode textarea[name="body"]		{height:400px !important; width:100% !important;}


/* Watch List */

#deviantART-halfmode #deviantlist			{margin:10px !important; width:auto !important;}
#deviantART-halfmode #deviantlist th			{padding:4px 5px !important; background:#d7e5e2 !important; white-space:nowrap;}
#deviantART-halfmode #deviantlist th.list-remove	{}
#deviantART-halfmode #deviantlist td			{padding:2px 5px 2px 0 !important;}
#deviantART-halfmode #deviantlist tr.odd td		{background:#d7e5e2 !important;}
#deviantART-halfmode #deviantlist td+td			{width:60% !important;}
#deviantART-halfmode #deviantlist td+td+td		{width:auto !important; text-align:center !important;}
#deviantART-halfmode #deviantlist td[colspan],
#deviantART-halfmode #output table tr.beacon		{font-family:Trebuchet MS, sans-serif; font-size:10pt !important; padding:10px 0 5px !important;}
#deviantART-halfmode #deviantlist tr:hover td:not([colspan]),
#deviantART-halfmode #deviantlist tr:hover td:not([colspan]) a.u
							{background:#3e8eb7 !important; color:#ffffff !important;}
#deviantART-halfmode form[action="removegroup"] input	{width:100% !important;}


/* Miscellaneous */

#deviantART-halfmode div.column-holder			{margin-top:10px !important;}
#deviantART-halfmode div.column-holder			{clear:left !important; padding:16px !important;}
#deviantART-halfmode div.column-holder div.column2>div,
#deviantART-halfmode div.column-holder div.column2>form	{margin-left:10px !important;}

#deviantART-halfmode .output-primary .h			{position:relative !important; top:16px !important;}

#deviantART-halfmode #output div.item.control-box .reply-cancel,
#deviantART-halfmode #output img.remove			{width:0 !important; height:0 !important; padding:15px 15px 0 0 !important; background:transparent url("http://s.deviantart.com/styles/minimal/minish/close-dev.gif") center no-repeat !important; cursor:pointer !important;}

#deviantART-halfmode #output fieldset			{border:solid 3px #d4dfdd !important; padding:12px !important; margin:1em !important;}
#deviantART-halfmode #output legend			{font-family:Trebuchet MS, sans-serif !important; font-size:12pt !important;}

#deviantART-halfmode #output textarea#signature		{width:100% !important;}
#deviantART-halfmode #profile-gallery-mobile-view,
#deviantART-halfmode a[href="#profile-gallery"]		{display:none !important;}


/* Stats Pages */

#deviantART-halfmode-trying i.tl,
#deviantART-halfmode-trying i.tr			{display:block !important;}

#deviantART-halfmode-trying .section-block		{background:none !important; border:none !important;}
#deviantART-halfmode-trying h2.section-head,
#deviantART-halfmode-trying #devlove,
#deviantART-halfmode-trying div.hh			{display:none !important;}

#deviantART-halfmode-trying .tabbar			{margin-bottom:15px !important;}
#deviantART-halfmode-trying .tabbar *			{border:none !important; margin:0 5px 0 0 !important; padding:5px 10px !important; background:none !important; color:#000 !important; font-size:12px !important; -moz-border-radius:8px !important;}
#deviantART-halfmode-trying .tabbar strong		{top:0 !important; background:#9FAFA4 !important; font-weight:normal !important;}
#deviantART-halfmode-trying .tabbar a			{top:-3px !important; background:#899B91 !important; text-decoration:none !important;}
#deviantART-halfmode-trying .tabbar a:hover		{background:#9FAFA4 !important;}

#deviantART-halfmode-trying fieldset			{border:solid 3px #d4dfdd !important; padding:12px !important;}
#deviantART-halfmode-trying legend			{font-family:Trebuchet MS, sans-serif !important; font-size:12pt !important;}

#deviantART-halfmode-trying div.Breakdown,
#deviantART-halfmode-trying div.Chart			{width:100% !important; clear:left !important;}

#deviantART-halfmode-trying .ChartBar,
#deviantART-halfmode-trying .full.layout td .poll .bar,
#deviantART-halfmode-trying #InfoStatusBarDiv		{background:#d4dfdd !important;}

#deviantART-halfmode-trying .ChartBar			{height:18px !important; line-height:18px !important; max-width:95% !important; clear:left !important; color:#fff !important; border:none !important;}
#deviantART-halfmode-trying .ChartValue			{line-height:18px !important;}
#deviantART-halfmode-trying .ChartBar+span+br+br	{display:none !important;}

#deviantART-halfmode-trying .full.layout td		{padding:0 5px !important;}
#deviantART-halfmode-trying .full.layout td *		{margin:0 !important; padding:0 !important; background:none !important; border:none !important;}
#deviantART-halfmode-trying .full.layout td .poll .bar	{height:15px !important;}

}


@-moz-document url-prefix("http://comments.deviantart.com/emoticons") {
/* Emoticons Legend */

body.ready			{background:#d4dfdd !important; margin:6px !important;}

body.ready center,
body.ready div[style],
body.ready ul			{padding:5px !important; background:#eaf2ee !important; -moz-border-radius:3px;}

body.ready ul			{margin:-5px 0 !important; padding:4px !important; -moz-border-radius:3px;}
body.ready li			{background:#eaf2ee !important; font-size:90%;}
body.ready li.even		{background:#d7e5e2 !important;}
body.ready ul br		{display:none;}

body.ready li.even-beacon span,
body.ready li.odd-beacon span	{font-family:Trebuchet MS, Verdana, sans-serif !important; font-size:12pt !important;}

}


@-moz-document url("http://comments.deviantart.com/emoticons") {

/* Main emoticons list */

body.ready ul			{margin:-5px 0 !important;}
body.ready li			{float:left; padding:0 !important; min-width:90px; width:25% !important; height:70px; text-align:center; background:#eaf2ee !important; font-size:90%;}
body.ready li .ext,
body.ready li .main		{display:block !important; float:none !important;}
body.ready li .main		{opacity:0.5;}
body.ready li:hover .main	{opacity:1;}
body.ready li .ext		{padding:0 !important;}

}
