css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") {







#contentCol  { background: url('http://i55.tinypic.com/10n7oux.gif') top left #000000 repeat fixed !important; }

.profile .profile_color_bar  { background-color: !important; }





/* navigation */

#blueBar       { background: !important; }

#headNavOut    { background: !important; border-color:#de264c !important; }







/* main nav icons */

.jewelToggler  {!important; }

#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,

#jewelMail:hover, #jewelMail:focus, #jewelMail:active,

#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color: !important; }

#jewelCase .jewel  { border:none !important; }







/* search */

#navSearch button  {!important; }

#navSearch     { border-color: !important; }







/* nav tabs */

#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color: !important; color: !important; }







/* boxes */

.lightblue_box  { background-color: !important; border-color: !important; }

.commentable_item .ufi_section  { background-color: !important; }

textarea, .inputtext, .inputpassword  { border-color: !important; }







/* links */

a  { color: !important; }

.uiLinkButton input  { color: !important; }

.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color: !important; }

button.as_link  { color: !important; }







/* side nav */

.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color: !important; }



.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:# !important; }

.friends_online_sidebar li.buddy_row a:hover  { background-color: !important; }



.unread  { background-color: !important; }

.line  { color: !important; }







/* pop up box  */

#jewelCase .jewelFooter a   { background-color: !important; }

#jewelCase .jewelFooter .uiTextSubtitle  { color: !important;  }



#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#de264c !important; }



#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color: !important; border-color:#f0788c !important; }

#jewelCase .jewelBox  { background-color: !important; }







/* copy */

.uiHeader h2  { color: !important; }







/* graffiti */

.current_stuff td  { background-color: !important; border-color: !important; }







/* profile page */

.UIComposer .UIComposer_Box  { background-color: !important; border-color: !important; }

.profile .box .box_header  { background-color: !important; border-color: !important; }







/* profile tabs */

.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:!important; border-color: !important; }

.profile .top_bar ul.tabs li a.tab_link  { background-color: !important; border-color: !important; }

.profile .top_bar ul.tabs li a.tab_link:hover  { background-color: !important; color: !important; border-color: !important; }









/* chat */

#chat_tab_bar .disabled .chat_window .chat_conv  { background-color: !important;  }

#chat_tab_bar .chat_header  { background-color: !important; border-color: !important; }

#chat_tab_bar .chat_header_name a  { color: !important; }

#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color: !important; color: !important; }

#presence_bar .presence_menu_opts h2 span  { background: !important; }

#presence_bar .presence_menu_opts h2  { border-color: !important; }

.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background: !important; }



#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background: !important; border-color: !important; }

#presence .presence_bar_button .inner_button  { border-top:none !important; }

#presence #buddy_list_tab #buddy_count .buddy_count_num  { color: !important; }



#presence .presence_menu_opts  { border-color: !important; }







/* buttons */

.UIButton_Blue  { background:#de264c !important; border-color: !important; }

.UIComposer_InputArea  { border-color: !important; }

.uiButtonConfirm  { background: !important; border-color: !important; }

#presence #buddy_list_tab #buddy_count strong  { color: !important; }















}
