@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(myspace.com) {

img, table {border: 0px !important; }
span{background-color:transparent !important;}
#UserHomeSwitch, #brightidea, #abipromo, #footer {display: none !important;}
h1,h2,h3,h4,h5, .daySection, body, .blacktextnb10, #betterb th, #bulletin_inbox th, #messages th, #footerCell th, .fieldTitle, .messageTable, .row, th, .blacktext10, #confirm p, .popup_title, li, ul{color:#696969 !important;background-color:#000000 !important; border: 0px !important;}
div , #home_infoBar span,{background-color : #000000 !important; border:none !important; color: #696969 !important;}
#home_profileInfo, div#home_profileInfo {background-color: #000000 !important;color:#000000 !important;}
#home_additionalLinks, #home_userURLInfo, #home_setHomePage, #home_searchAddressBook, #splash_coolNewPeople, #splash_profile, #home_schools, h4.heading, #PageThemeModule, #home_appnavigation, #ctl00_ctl00_Main_cpMain_SplashDisplay_PromoMember_ABIHeader, #splash_coolNewPeople, .ABIPromoBody, #ctl00_ctl00_Main_cpMain_SplashDisplay_PromoMember_ABIBodyText, #ctl00_ctl00_Main_cpMain_SplashDisplay_featuredVideos_CMS_videos, #amberalert, #pymk {display:none !important;}
body {background-color: #000000 !important;color:#000000 !important}
table, tr, td, #punymce, .addapp_popup_box{background-color: #000000 !important;color:#696969 !important; border:0px !important;}
table.cols {background-color: #000000 !important;color:#000000 !important;}
.section, .content, .buttons {background-color:#000000 !important;color:#696969 !important}
div.section, #modalCreateAlbum {color: #696969 !important; background-color: #000000 !important; border:0px !important; border-style: none !important; width:100% !important; margin-bottom:15px !important;}
a{text-decoration:none !important; font-weight:bold !important;color:#ccc !important;}
a:link, a:active, a:visited{color:#ccc !important;}
a:hover{color:#333 !important; text-decoration:none !important; background-color:transparent !important;}
#home_bulletins th, #home_bulletins .cols, #home_bulletins tr, td, table, tr, th, div, span, form, input, textarea, .blogContent, .blogContentInfo, p, #topnav {background: transparent !important; border: 0px !important; color: #696969 !important;}
#home_infoBar{display: block !important;
	position: fixed !important;
	top: 8px !important;
	left: 0 !important;
        height: 250px !important; 
        width: 110px !important; 
	background-color: #000 !important;}
textarea, input, #home_infoBar {border-color: #1a1a1a !important; border: solid !important; border-width: 2px !important;}
}
