css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("adam4adam.com") {
/*----------
* whole
------------*/
* {outline:none!important;}
#areacenter {color:#01aa37;}
td[class="title2"] {background:#000!important;}
span[id=hotspot] {background:transparent!important;}
/*----------
* header
------------*/
div {background-image:none!important;}
div[id="wrapper"]>table {background:#000!important;}

/*----------
* Alert
------------*/
div[class="alert1"]{background:#000!important; color:red!important;}

/*----------
* ads
------------*/
table[width="125"],
img[alt=banner],
img[src="http://images.adam4adam.com/ressources/rimages/join_now.gif"],
img[src="http://images.adam4adam.com//images/toystore-joinfree.jpg"],
img[width="468"],
iframe {display:none;}

/*--------
sidebar
----------*/
#arealeft,
#menuside dt {background: #000!important;}

#menuside dt a {color:#fff!important;}

/*----------
* message
------------*/
table[class="tblhead"] td {background:#000!important;}
.pageuolbod {background: #FFF;}
.pagetitles,.pagetitles td {background: #000!important;}

/*----------
* profile
------------*/
#pic_type {color:#FFF;}

}
